INSERT INTO "Tips" (id, title, seoTitle, description, seoDescription, category, slug, author, img, featured, status, createdAt, updatedAt, is_paying, likes, note, scheduled_publish_date, id_intern, video_link, thumbnailName, video_nature) VALUES
(1, 'أسرار المادلين الفرنسي', '', 'المادلين من أنواع الكيك الفرنسية التي تتميز بهشاشة قوامها وشكلها المنفوخ، نودوك بالنصيحة هذه باش تتحصل على نتيجة مثالية للمادلين : قومي بوضع قالب مع خليط المادلين، مسبقا في الثلاجة، من ثم قومي بطهيه في فرن مسخن مسبقا على حسب التوقيت الزمني للوصفة، هذه الصدمة الحرارية بين برودة الخليط وسخونة الفرن ستمنح الكيك انتفاخا جميلا على سطحه الخارجي. \r\n', 'أسرار المادلين الفرنسي', 'نصائح طبخ', 'أسرار المادلين الفرنسي', 1, '', 0, 2, '2022-02-22 21:40:28', '2021-04-07 16:01:08', 1, 0, '', '2022-02-02 14:51:00', '1', 'shutterstock-406318747.jpg', '', 0);
(2, 'احسن طريقة باش تركض أبيض العظمة', '', 'فما وصفات تعتمد كليا على نجاح خفق بياض البيض كيما المارينغ و إلا الكيك الاسفنجي، باش تتحصل على بياض بيض هش ومتماسك، هذه هي الاسرار إلي لازمك تعرفهم تتأكد بان بياض البيض خالي من أية صفار وقشور لازم البيض يكون في درجة حرارة الغرفة لتسهل عمليه خفقه تقوم بخفق بياض البيض على سرعة منخفضة أولا من ثم الانتقال للخفق على سرعة عالية عندما يبدأ الخليط بالتحول الى قوام رغوي اخفق البيض بواسطة الخفاقة الكهربائية بحركة دائرية منتظمة في نفس الاتجاه و لدمج أكبر ما يمكن من كمية من الهواء استخدام وعاء نظيف وخال من أي ماء أو زيوت.\r\n', NULL, 'نصائح حلويات', '', 1, '', 0, 1, '2022-05-30 09:12:08', '0000-00-00 00:00:00', 1, 5966, '', '2022-02-02 14:51:00', '2', '2.jpeg', '', 0),
(3, ' تتقلق وقت تقشر البصل و تقصو ؟', '', '<h3 style=\"text-align: right;\"> تتقلق وقت تقشر البصل و تقصو ؟</h3><h3 style=\"text-align: right;\">لقينالك الحل، ماعليك كان تحط البصل في الفريرز لمدة 10 دقائق قبل ما تستعملو، وإلا حطوا في صحفة كبيرة بالماء البارد لمدة 30 دقيقة. العملية هذه تعطل اطلاق البصل للانزيم الحمضي المسؤول عن تهيج العيون واحمرارها</h3>', '', 'نصائح طبخ', ' تتقلق وقت تقشر البصل و تقصو ؟', 1, '', 0, 1, '2020-04-23 02:32:39', '2021-09-05 22:37:45', 1, 0, '', '2022-02-02 14:51:59', '3', 'shutterstock-693771241.jpg', '', 0),
(4, 'سر الحصول على بطاطا هشوشة', '', 'باش تتحصل على بطاطا هشوشة (بوريه) قوامها ناعم وكريمي و باش تتخلص من قوامها المتكتل، زيد رشة من بيكاربونات الصوديوم قبل ما تبدأ تخلط جميع المكونات، هذه الحيلة إلي تخليلك خليط البطاطا منفوش و هشوش\r\n', '', 'نصائح طبخ', 'سر الحصول على بطاطا هشوشة', 1, '', 0, 1, '2020-04-23 02:33:20', '2021-09-27 08:32:37', 1, 0, '', '2022-02-02 14:51:59', '4', 'shutterstock-150840737.jpg', '', 0),
(5, 'باش تنسى ريحة العضم في الحلويات ', '', 'انتي زادة تقلقك ريحة العظم إلي تقعد في الحلويات ؟ياحلاوة تعطيك تكنيك صغيرة و مفعولها كبير، زيد شوية سكر فانيليا للعضم و انت تحضر في الحلويات، باش تلاحظ اللي الريحة تنحات بعد الطبخ مباشرة !\r\n', '', 'نصائح حلويات', 'باش تنسى ريحة العظم في الحلويات', 1, '', 0, 1, '2023-12-11 07:48:16', '2021-05-12 14:04:16', 1, 7411, '', '2022-02-02 14:51:00', '5', 'shutterstock-228711715.jpg', '', 0),
(6, ' باش تقول باي للريحة الحوت في ايديك ', ' باش تقول باي للريحة الحوت في ايديك ', '<p style=\"text-align: right;\">.الحوت بنين, أما ريحتو قوية وتلصق في اليدين، وهذا يقلق برشا منا !</p><p style=\"text-align: right;\">الحل، حك يديك بكل لطف بشوية عصير قارص و ملح و قول باي للريحة الحوت.</p>', '', 'نصائح عامة', ' باش تقول باي للريحة الحوت في ايديك', 1, '', 0, 1, '2020-07-28 10:29:09', '2021-05-12 14:03:49', 1, 0, '', '2022-02-02 14:51:59', '6', 'shutterstock-106053830.jpg', '', 0),
(7, ' باش تنسى ريحة الكرمب في الكوجينة !', ' باش تنسى ريحة الكرمب في الكوجينة !', 'أسهل طريقة باش ترتاح من ريحة الكرنب في الطهي : حط قطعة خبز طري في الماء و الكرنب قاعد يطيب.و هكا الخبز يمص الريحة القوية من غير ميبدل مطعم الكرنب.\r\n', '', 'نصائح عامة', ' باش تنسى ريحة الكرمب في الكوجينة !', 1, '', 0, 2, '2023-06-06 16:40:31', '2021-01-11 14:53:33', 0, 0, '', '2022-02-02 14:51:00', '7', 'shutterstock-1160836645.jpg', '', 0),
(8, 'تخلص من ريحة الفم', '', '<p dir=\"rtl\">ريحة الفم حاجة تقلق برشا ناس، ياحلاوة تمدك بالحل : أمغض طريف فقوس لمدة 90 ثانية.</p><p dir=\"rtl\">الألياف والكيمياء النباتية الموجودة فيه قادرة تقتل البكتيريا المتسببة في الريحة الخايبة.</p><p dir=\"rtl\">وهكا بحاجة بسيطة وموش مكلفة و طبيعية، تتخلص من عناء وقلق كبير.</p>', '', 'نصائح عامة', 'تخلص من ريحة الفم', 1, '', 0, 1, '2020-07-28 10:32:48', '2021-10-08 08:06:22', 1, 0, '', '2022-02-02 14:51:59', '8', 'shutterstock-240243799.jpg', '', 0),
(9, 'تخلص من ريحة الارز المحروق', 'تخلص من ريحة الارز المحروق', '<p dir=\"rtl\">الروز تحرق و خرجت منو ريحة خايبة ؟</p><p dir=\"rtl\">ياحلاوة تعطيك تكنيك صغيرة وساهلا، باش تتخلص من الريحة :</p><p dir=\"rtl\">حط قطعة خبز ابيض ( لبابة ) فوق الروز لمدة تتراوح بين 5 و 10 دقايق لين تتنحى الريحة.</p><p dir=\"rtl\">و بالطبيعة، ما تصبش الروز المحروق في قاع الطنجرة.</p><p style=\"text-align: right;\"></span></p>', '', 'نصائح عامة', 'تخلص من ريحة الارز المحروق', 1, '', 0, 1, '2020-07-28 10:34:25', '2021-10-25 08:37:14', 1, 0, '', '2022-02-02 14:51:59', '9', 'shutterstock-1718075989.jpg', '', 0),
(10, 'وداعا للريحة الخايبة في الثلاجة ', 'وداعا للريحة الخايبة في الثلاجة !', '<p dir=\"rtl\">فما ريحة خايبة في الثلاجة ؟</p><p dir=\"rtl\">تنجم ترتاح منها بكعبة فحم، زعمة علاش ؟</p><p dir=\"rtl\">خاطر الفحم عندو خصوصيات تمكنو باش يمتص الرطوبة و معاه الريحة.</p><p style=\"text-align: right;\"></span></p>', '', 'نصائح عامة', 'وداعا للريحة الخايبة في الثلاجة ', 1, '', 0, 1, '2020-07-28 10:35:19', '2021-05-12 14:13:28', 1, 0, '', '2022-02-02 14:51:59', '10', 'shutterstock-1578968317.jpg', '', 0),
(11, '! من إليوم إتخلص من الريحة إلي في الأوعية الزجاجية', 'من إليوم إتخلص من الريحة إلي في الأوعية الزجاجية !', ' ساعات الحكك البلار تبدى فيهم ريحة خايبة و قد ما تغسل ماحبتش تتنحى ؟الحل، ساهل برشا ؛ هو في غسلانها بالخل و الماء.\r\n', '', 'نصائح عامة', '! من إليوم إتخلص من الريحة إلي في الأوعية الزجاجية', 1, '', 0, 1, '2020-07-28 10:36:57', '2021-10-01 08:25:23', 1, 0, '', '2022-02-02 14:51:59', '11', 'shutterstock-1400025293.jpg', '', 0),
(12, '! إنسا ريحة الثوم و البصل من يديك ', 'إنسا ريحة الثوم و البصل من يديك ', '<p><span dir=\"RTL\"></p><p dir=\"rtl\">تنجم ترتاح من ريحة البصل والثوم بحكان يديك باداة مالحديد الإنوكس بالماء البارد، وتواصل في العملية هذي لين تتنحى الريحة.</p><p style=\"text-align: right;\">ثانيا تنجم تستعمل بقية القهوة مرحية وشايحة بالطبيعة ؛ حط القهوة في وعاء ودخل يديك فيها وحكها لين تتنحى الرائحة</p><p style=\"text-align: right;\">او اخيرا حط يديك في وعاء فيه عصير ليمون لمدة 3 دقايق تقريباً، بعدها، أغسلهم بالقدا</p><p style=\"text-align: right;\">بالطرق هاذي ترتاح كليا من ريحة الثوم و البصل القوية</p><p></span></p>', '', 'نصائح عامة', '! إنسا ريحة الثوم و البصل من يديك ', 1, '', 0, 1, '2020-07-28 10:38:47', '2021-05-28 09:14:29', 1, 0, '', '2022-02-02 14:51:59', '12', 'shutterstock-1221021802.jpg', '', 0),
(13, 'الحل باش إتنحي لون البتراف من إديك', 'الحل باش إتنحي لون البتراف من إديك', '<p dir=\"rtl\">بعدما أنقصو البتراف، الكلنا تتصبغ يدينا باللون الاحمر إلي يقلقنا برشا.</p><p dir=\"rtl\">إليوم نعطيكم الحل ؛ حك إيديك<font color=\"#202124\"> </font>بليمونة و تستنتج إلي لون يبدا يفتاح بشوية، بشوية و بعد يتنحى !</p>', '', 'نصائح عامة', 'الحل باش إتنحي لون البتراف من إديك', 1, '', 0, 1, '2020-07-28 10:43:49', '2021-09-07 23:54:51', 1, 0, '', '2022-02-02 14:51:59', '13', 'shutterstock-228990118.jpg', '', 0),
(14, 'كيفاش تنحي الشوينقوم اللاصق في الحوايج', '', 'كلنا عانينا من الشوينقوم إلي لصق في الحاويج.عنا ليكم الحل :أولا منغير ما تغسل الحوايج ديراكت، لازمها شوية وسع بال ؛- نحي العلكة باللي تقدر بإيديك،- حط ساشي ثلج على بقية العلكة اللاصقة لين تجمد- حك البلاصة حتى تتنحى كلها. وفي الأخر، حك البلاصة بشوية خل والا كحول طبية.\r\n', '', 'نصائح عامة', 'الحل في ازالة الشوينقوم مالحوايج', 1, '', 0, 1, '2023-05-29 07:36:03', '2021-05-29 09:57:36', 1, 7412, '', '2022-02-02 14:51:00', '14', 'shutterstock-1498540277.jpg', '', 0),
(15, 'أسرع طريقة لتظيف الميكرووند', 'أسرع طريقة لتظيف الميكرووند', '<p dir=\"rtl\">تنظيف الميكرووند ساعات يكون صعيب على برشا منا !</p><p dir=\"rtl\">إنقدمولكم الحل من غير إستعمال مواد كيمياوية :</p><p dir=\"rtl\">- حط وعاء فيه شويا ماء (20 صل تقريباً) وعصير ليمونة كاملة في الميكرووند.</p><p dir=\"rtl\">- خدم الميكرووند على الأخر حتى لين يسيب الماء و عصير الليمونة بخارهم، لبعض الدقايق.</p><p dir=\"rtl\">- خرج الوعاء و نظف الميكرووند باسفنجة والا بقطعة قماش نظيفة و مبلولة.</p><p dir=\"rtl\">و هكا عملية التنظيف تكون ساهلة بعد ماتفككت الاوساخ ببخار الماء و الليمون.</p><p style=\"text-align: right;\"></span></p>', '', 'نصائح عامة', 'أسرع و أسهل طريقة لتظيف الميكرووند', 1, '', 0, 1, '2020-07-28 10:47:12', '2021-05-20 11:24:24', 1, 0, '', '2022-02-02 14:51:59', '15', 'shutterstock-703651624.jpg', '', 0),
(17, ' بقايع بيض بعد ما تغسل الأصحنة ؟', ' بقايع بيض بعد ما تغسل الأصحنة ؟', '<p dir=\"rtl\">بعد ماتغسل الأصحنة و إشيحوا، تلاحظ إلي فما عليها بقايع بيض ؟</p><p dir=\"rtl\">نقدملك توا الحل ؛</p><p dir=\"rtl\">بل قطعة قماش (100% قطن) بالخل لبيض و أمسح بيها الأصحنة ولا حتى الكيسان و تتلاحظ ديراكت أنو البقايع ما عادوش موجودين.</p>', '', 'نصائح عامة', ' بقايع بيض بعد ما تغسل الأصحنة ؟', 1, '', 0, 1, '2020-07-28 10:49:51', '2021-07-21 00:03:32', 1, 0, '', '2022-02-02 14:51:59', '17', 'shutterstock-1139381582.jpg', '', 0),
(18, 'باش تنجح في تنظيف الغاز كيف ماتحب ', 'باش تنجح في تنظيف الغاز كيف ماتحب !', '<p align=\"right\"><span dir=\"RTL\">عيون الغاز مسخة و محروقة ؟ الحل ساهل</span> :</p><p align=\"right\"><span dir=\"RTL\">في وعاء، حط الخل الأبيض و عيون الغاز ليلة كاملة</span>.</p><p align=\"right\"><span dir=\"RTL\">في الصباح، أغسلهم بالماء تحت السبالة و باش تفرح كتشوفهم انظافوا.</span></p><p align=\"right\"><span dir=\"RTL\">نفس الشيء تنجم تعملوا مع الغاز في حد ذاتو كي يبدا مسخ برشة.</span></p><p align=\"right\"></span></p>', '', 'نصائح عامة', 'باش تنجح في تنظيف الغاز كيف ماتحب ', 1, '', 0, 1, '2020-07-28 10:52:22', '2021-05-12 14:12:49', 1, 0, '', '2022-02-02 14:51:59', '18', 'shutterstock-85556377.jpg', '', 0),
(19, '! الحل باش ما تسبغلكش القهوة في القاعة ', 'الحل باش ما تسبغلكش القهوة في القاعة !', '<p align=\"right\">تبزعتلك قهوة و إلا تاي وخايف ليسبغولك في القاعة ؟</p><p align=\"right\">.خلط فيسع مغرفة صغيرة بيكاربونات وعصير الليمون حتى لين تتحصل على عجينة</p><p align=\"right\">.حك بيها البقعة، باش تلاحظ إختفائها</p><p align=\"right\"> </p>', '', 'نصائح عامة', '! الحل باش ما تسبغلكش القهوة في القاعة ', 1, '', 0, 1, '2020-07-28 10:54:17', '2021-07-15 18:17:33', 1, 0, '', '2022-02-02 14:51:59', '19', 'shutterstock-1387407023.jpg', '', 0),
(21, 'أسهل طريقة لتنظيف طبق الغراتان المحروق', 'تنظيف طبق الغراتان المحروق', '<p style=\"text-align: right;\">من اليوم تنظيف طبق الغراتان المحروق مهوش حاجة صعيبة : حط شوية ماء وجال تاع غسل الصحون في الوسط، بعد اعمل كرة من الاليمنيوم و حك بيها كل البقع. إلي باش تلقاها تنحات منغير تعب</p>', '', 'نصائح عامة', 'أسهل طريقة لتنظيف طبق الغراتان المحروق', 1, '', 0, 1, '2020-07-28 10:56:54', '2021-08-24 22:01:09', 1, 0, '', '2022-02-02 14:51:59', '21', 'shutterstock-1679754664.jpg', '', 0),
(22, 'سر تنظيف الميكسار بسهولة', 'سر تنظيف الميكسار بسهولة', '<p align=\"right\" dir=\"rtl\">حط ​في الخلاط الكهربائي، المعروف زادة بإسم الميكسار وإلا البلندر، نصف كأس خل أبيض معاه كأس ونصف ماء سخون.</p><p align=\"right\" dir=\"rtl\">خدموا لمدة دقيقتين تقريباً.</p><p align=\"right\" dir=\"rtl\"> باش تلاحظ أنو الأوساخ و الروائح الكل تنحات.</p><p align=\"right\" dir=\"rtl\">و الخل الأبيض يتطهر زادا الخلاط الكهربائي من برشا جراثيم.</p><p align=\"right\" dir=\"rtl\"><br /></span></span></p>', '', 'نصائح عامة', 'سر تنظيف الميكسار بسهولة', 1, '', 0, 1, '2020-07-28 10:59:10', '2021-06-16 08:12:55', 1, 0, '', '2022-02-02 14:51:59', '22', 'shutterstock-419869504.jpg', '', 0),
(24, '! تخلص من الجراثيم الضارة في السبونتيكس', 'تخلص من الجراثيم الضارة في السبونتيكس !', '<p align=\"right\" dir=\"rtl\">في تنظيف الكوجينة لكلنا نستعملو الاسفنجة أو كيما انقلوا \"السبونتيكس\"، أما إلي لازم نعرفوه أنو هي أكثر حاجة تشد الجراثيم، على خاطرها ديما مبلولة، و إنستعملوهة في برشا حاجات.</p><p align=\"right\" dir=\"rtl\">إنقدمولك من أفضل طريقة لتنظيفها ؛</p><p align=\"right\" dir=\"rtl\">حط السبونتيكس في الميكرووند وبرمجو على التسخين لمدة دقيقة و هكا تتخلص من 99.9% من الجراثيم الضارة و من غير مواد كيمياوية.</p><p style=\"text-align: right;\"><br /></span></p><p style=\"text-align: right;\"></span></p><p style=\"text-align: right;\"></span></p>', '', 'نصائح عامة', '! تخلص من الجراثيم الضارة في السبونتيكس', 1, '', 0, 1, '2020-07-28 11:04:53', '2021-07-13 23:42:37', 1, 0, '', '2022-02-02 14:51:59', '24', 'shutterstock-1703297242.jpg', '', 0),
(25, 'متخسرش حتى قطرة من كعبة الليمون ', '', '<p align=\"right\" dir=\"rtl\">تحب تتحصل على أكبر كمية من عصير الليمون ؟</p><p align=\"right\" dir=\"rtl\">حطها في الميكرووند لمدة دقيقة ؛ السخانة تعاون باش تخرج العصير الموجود في الليمونة.</p><p align=\"right\" dir=\"rtl\">تنجم زادة تستعمل الطريقة التقليدية ؛ وهي أنك تكركب كعبة الليمون على أي سطح كاسح.</p>', '', 'نصائح طبخ', 'متخسرش حتى قطرة من كعبة الليمون ', 1, '', 0, 1, '2020-07-28 11:13:35', '2021-05-12 14:13:05', 1, 0, '', '2022-02-02 14:51:59', 'نصائح عامة', 'shutterstock-566767726.jpg', '', 0),
(26, 'مقص \"القلاص\" نجمو نستعملوه في أكثر من حاجة ؟', 'مقص \"القلاص\" نجمو نستعملوه في أكثر من حاجة ؟', '<p align=\"right\" dir=\"rtl\">برشا ماعون نجمو نستعملوه في أكثر من حاجة ولأكثر من مرة.</p><p align=\"right\" dir=\"rtl\">كيما مقص المثلجات أو كيما انقلوا مقص \"القلاص\"، إلي ​نجمو نستعملوه في تقوليب الغريبة إلي تبدى في فورمت \"القبة\" وزادة في تقوليب الكوكيز و إلا حتى فالكوب كايك.</p><p align=\"right\" dir=\"rtl\">و هكا، تبدى\"عندنا نفس الفورمة و خاصةً نفس الكمية.</p>', '', 'نصائح عامة', 'مقص \"القلاص\" نجمو نستعملوه في أكثر من حاجة ؟', 1, '', 0, 1, '2020-07-28 11:16:59', '2021-07-06 23:54:25', 1, 0, '', '2022-02-02 14:51:59', 'نصائح حلويات', 'shutterstock-1864069603.jpg', '', 0),
(27, 'سرّ تطييب عجينة التارت من غير ما تتنفخ و إلا إطبوط ', 'سرّ تطييب عجينة التارت من غير ما تتنفخ و إلا إطبوط ', '<p align=\"right\" dir=\"rtl\">مع يا حلاوة تعرف على سر عدم إنتفاخ عجينة التارت كي إطيبها و من غير ما تنقبها زادا !</p><p align=\"right\" dir=\"rtl\">حط بقول جافة كيف الحمص و إلا اللوبيا في قاع العجينة وبعد حطها ديراكت في الكوشة.</p><p align=\"right\" dir=\"rtl\">وهكا إطيب عجينة التارت من غير ما تتنفخ و إلا إطبوط !</p>', '', 'نصائح طبخ', 'سرّ تطييب عجينة التارت من غير ما تتنفخ و إلا إطبوط', 1, '', 0, 1, '2020-07-28 11:18:26', '2021-05-12 14:14:30', 1, 0, '', '2022-02-02 14:51:59', 'نصائح غذائية', 'shutterstock-136107707.jpg', '', 0),
(29, 'زعمة باش إتعوض البصل الأحمر  ؟', 'زعمة باش إتعوض بصل الشالوت ؟', '<p dir=\"rtl\">في بارشة وصفات نلقاو مكون من المكونات إسمو ؛ بصل الشالوت \"البصل الأحمر\" إلي هو موش موجود برشا في السوق و منعرفوش باش إنعوضوه !</p><p dir=\"rtl\">عنا ليكم الحل :</p><p style=\"text-align: right;\">بصل الشالوت يتميز بمذاقو الحلو و بوجود نكهة خفيفة متاع ثوم</p><p dir=\"rtl\">إتنجم إتعوض 4 أو 3 بصلات شالوت إبصلة حمرا متوسطة وسنة ثوم مهروشة.</p><p dir=\"rtl\">وهكة اطيب الوصفة إلي شاهيها !</p>', '', 'نصائح طبخ', 'زعمة باش إتعوض البصل الأحمر  ؟', 1, '', 0, 1, '2020-07-28 11:22:20', '2021-10-25 08:39:08', 1, 0, '', '2022-02-02 14:51:59', '29', 'shutterstock-426323161.jpg', '', 0),
(30, '! ماكش باش إتحير في تعويض كيس الحلواني', 'ماكش باش إتحير في تعويض كيس الحلواني !', 'باش تنجم تعوض كيس الحلواني ؟إستعمل ساشي الثلاجة (كيس الفريزر) ؛,حط فيها الخليط -- قص بالمقص وحدة من التراكن لوطانين و إختار الفتحة إلي توالمك و رد بالك متنزلش عليه بالقوي. ! و إتنجم بالطبيعة تغسل الساشي و تستعمله مرة أخرى\r\n', '', 'نصائح عامة', '! ماكش باش إتحير في تعويض كيس الحلواني', 1, '', 0, 1, '2020-07-28 11:23:54', '2021-08-23 00:56:38', 1, 0, '', '2022-02-02 14:51:59', '30', 'shutterstock-1718278048.jpg', '', 0),
(32, '! ما عندكش سكر محور ؟ عنا ليك الحل', 'ما عندكش سكر محور ؟ عنا ليك الحل !', 'تستحق السكر المحور لوصفة وهو معدنكش بين إديك ؟ما تحيرش ياحلاوة تعطيك الحل :إرحي السكر العادي في الرحاية لين يتحول لبودرة وزيدو شوية نشاء و خلط مليح ؛ على كيلو سكر إنزيدو 30 غرام نشاء، و هكا السكر ما إحجرش !\r\n', '', 'نصائح حلويات', '! ما عندكش سكر محور ؟ عنا ليك الحل ', 1, '', 0, 1, '2020-07-28 11:25:59', '2021-09-10 09:57:07', 1, 0, '', '2022-02-02 14:51:59', '32', 'shutterstock-1151916320.jpg', '', 0),
(34, 'شنية الطريقة الصحيحة لتذويب الشوكلاطة ؟', '', '<p dir=\"rtl\">أحسن طريقة باش تذوب الشوكلاطة : \"حمام مريم\" أو  </span>بالفرنسية \"بان ماري\", معناها انك تحط وعاء صغير فيه الشوكلاطة الي تحب تذوبها،</p><p dir=\"rtl\">وتحطو في وسط وعاء أكبر منو يكون معبي بالماء المغلي و الكل على الغاز.</p><p dir=\"rtl\">و هكا الشوكلاطة تذوب بمفعول سخانة البخار، من غير متلمس الماء.</p><p dir=\"rtl\">هاذي الطريقة الصحيحة لتذويب الشوكلاطة ؛ من غير ما يتبدل مطعمها و طبقا محافضة على اللقة ولمعانها !</p>', '', 'نصائح حلويات', 'شنية الطريقة الصحيحة لتذويب الشوكلاطة ؟', 1, '', 0, 1, '2020-07-28 11:29:19', '2021-05-12 14:13:45', 1, 0, '', '2022-02-02 14:51:59', '34', 'shutterstock-595968092.jpg', '', 0),
(36, 'خميرة الكاتو وفات ؟ شنوا الحل ؟', '', '<p dir=\"rtl\">في أخر دقيقة، فقت إلي ماعندكش خميرة كاتو !</p><p dir=\"rtl\">ياحلاوة تقدملك الحل :</p><p dir=\"rtl\">إتنجم تعوض خميرة الكاتو بالبيكاربونات الغذائي ؛ على كل قرطاس متاع خميرة كاتو إساويه مغرفة ماكلة متاع بيكاربونات !</p><p dir=\"rtl\">و تتلاحظ  إلي النتيجة، هي هي !</p><p dir=\"rtl\"> </p><p style=\"text-align: right;\"></span></p>', '', 'نصائح حلويات', 'خميرة الكاتو وفات ؟ شنوا الحل ؟', 1, '', 0, 1, '2020-07-28 11:32:06', '2021-08-05 23:59:54', 1, 0, '', '2022-02-02 14:51:59', '36', 'shutterstock-640739113.jpg', '', 0),
(37, 'تذويب الشوكولاتة ولمعانها', '', '<p dir=\"rtl\" mce_style=\"text-align: right;\">شنوا السر في الكاتوات إلو فيها ديكور بالشوكولاتة ؟</p><p dir=\"rtl\" mce_style=\"text-align: right;\">السر إلي مايقلوش عليه الشفان أهوى :</p><p dir=\"rtl\" mce_style=\"text-align: right;\">باش تقعد الشوكولاتة تلمع وكي تبرد ترجع صلبة ؛</p><p dir=\"rtl\" mce_style=\"text-align: right;\">السر هو انك، تذوب 3/4 الشوكلاتة على  \"بان ماري\" لين يخلط درجة 50/55 درجة حرارية،</p><p dir=\"rtl\" mce_style=\"text-align: right;\">بعد هز الشوكلاتة الفوق على السخانة والبخار، وزيدها الربع اللاخر مالشوكلاتة المبشورة</p><p dir=\"rtl\" mce_style=\"text-align: right;\">و خلطهم بالقدا.</p><p dir=\"rtl\" mce_style=\"text-align: right;\">رجع الخليط الكل على النارعلى درجة 27/28 وخلط شويا. بالطريقة هاذي تتحصل على شكلاطة مذوبة وتلمع.</p><p style=\"text-align: right;\"></span></p>', '', 'نصائح حلويات', 'تذويب الشوكولاتة ولمعانها', 1, '', 0, 1, '2020-07-28 11:32:24', '2021-08-26 23:47:13', 1, 0, '', '2022-02-02 14:51:59', '37', 'shutterstock-182279573.jpg', '', 0),
(38, 'كول البنان تولي فرحان', '', '<p dir=\"rtl\" mce_style=\"text-align: right;\">البنان مفيد برشا على المستوى المعنوي ؛ يعتبر كمضاد للاكتئاب و يرجع هذا خاصةً لأنه غني بالمغنزيوم !</p><p dir=\"rtl\" mce_style=\"text-align: right;\">و فيه نسبة باهية من السكريات و برشا ألياف ومعادن كيما البوتاسيوم.</p><p dir=\"rtl\" mce_style=\"text-align: right;\">و زيد بعد ما تاكل كعبة بانان يكون عندك 0.5 ملليغرام من الفيتامين ب، اللي تمثل تقريباً ربع الكمية اللازمة في اليوم.</p>', '', 'نصائح غذائية', 'كول البنان تولي فرحان ', 1, '', 0, 1, '2020-07-28 11:38:02', '2021-05-12 14:14:16', 1, 0, '', '2022-02-02 14:51:59', '38', 'shutterstock-449299675.jpg', '', 0),
(39, 'إليوم باش نحكيو على الكنز الأصفر ', '', '<p align=\"right\">.الكنز الأصفر ؟ الليمون وما أدراك ما الليمون</p><p align=\"right\">.كعبة وحدة الليمون، تعطيك تقريب ثلث الاحتياج اليومي للفيتامين س, و الليمون<span data-cke-marker=\"1\">  زادة غني بفيتامين د</p><p align=\"right\">.موش هاذا كهو، الليمون فيه نسبة عالية مالكاليسيوم، الحديد، وبرشا معادن أخرى</p><p align=\"right\">.ياحلاوة تنصحك أنك تستغل كل وصفة باش تزيد فيها الليمون حد الإمكان، على خاطرو منكه و حاجة بنينة برشا في الحلو و المالح</p><p align=\"right\">.من غير ماننساو إلي هو منعش، أيامات السخانة</p>', '', 'نصائح غذائية', 'إليوم باش نحكيو على الكنز الأصفر ', 1, '', 0, 1, '2020-07-28 11:39:21', '2021-05-12 14:14:49', 1, 0, '', '2022-02-02 14:51:59', '39', 'shutterstock-392050561.jpg', '', 0),
(40, 'المادلينا, أنتي أو لا أحد !', '', '<p align=\"right\" dir=\"rtl\"> المادلينا هي حليفك في البرد !</p><p align=\"right\" dir=\"rtl\"> المادلينا غلة متوفرة اواخر لخريف و أوائل الشتاء ؛ وقت نبداو مستحقين لفيتامين \"س\" اكثر من أي وقت.</p><p align=\"right\" dir=\"rtl\">كعبتين مادلينا تقريب 100 غرام، فيهم 40 مغ مالفيتامين \"س\" إلي فوائدو معدودة على جهاز المناعة متاعنا. </p><p align=\"right\" dir=\"rtl\">الغلة هاذي، غنية برشا بالمعادن، كيما الماغنزيوم و الكاليسيوم، و فيها سعرات حرارية ضعيفة، تمثل 50 سعرة حرارية في 100غ.</p><p align=\"right\"><br /></span></span></p>', '', 'نصائح غذائية', 'المادلينا, أنتي أو لا أحد !', 1, '', 0, 2, '2020-07-28 11:40:37', '2020-12-31 12:49:20', 0, 0, '', '2022-02-02 14:51:59', '40', 'shutterstock-1742204378.jpg', '', 0),
(41, 'حل لمشكلة طيحان طروف الشوكلاطة في قاع الكيك !', '', 'تحب تزين الكيك تاعك بطروف الغلة و إلا بطروف شوكلاطة، لكنهم يطيحوا في قاع الكيك، كاينك معملت شيء ؟عنا ليك الحل : حط شويا فارينة في ساشي و زيدو طروف الشوكلاتة أو طروف الغلة و خضهم بالقدا، لين يتغلفو بالفارينة،بعد نحيهم على جنب وحطهم على سطح الكيك.كي يطيب الكيك باش تلاحظ اللي القطع مطاحوش، على خاطر الفارينة ثبتتهم.\r\n', '', 'نصائح حلويات', 'حل لمشكلة طيحان طروف الشوكلاطة في قاع الكيك !', 1, '', 0, 1, '2022-05-31 23:26:56', '2020-12-31 12:59:20', 1, 4361, '', '2022-02-02 14:51:00', '41', '41.jpeg', '', 0),
(42, 'تعرفش منافع التمر ؟', '', '<p align=\"right\">التمرة غلة، رغم صغر حجمها إلا أنها قادرة تعطيك كمية كبيرة من الطاقة، على خاطر فيها نسبة متاع كربوهيدرات عالية برشا تقريبا 96 بالمئة. وفيها نسبة فيتامين \"ب\" مهمة للبدن، وفيها مجموعة كبيرة متاع معادن كيما البوتاسيوم، المغنزيوم و الحديد بنسب متفاوتة.</p><p align=\"right\">50 غرام مالتمر، تعطيك نسبة 10 بالمئة من حاجياتك اليومية مالزنك و النحاس و الكالسيوم.</p><p align=\"right\">نزيدك زيادة، التمرة فيها ألياف فعالة غير قابلة للذوبان، يعني مثال في 50 غرام عندك تقريبا 3.5 غرام من الألياف، الموصى بيها يوميا !</p><p align=\"right\"></span></p>', '', 'نصائح غذائية', 'تعرفش منافع التمر ؟', 1, '', 0, 1, '2020-07-28 11:42:12', '2021-05-12 14:12:08', 1, 0, '', '2022-02-02 14:51:59', '42', 'shutterstock-722088304.jpg', '', 0),
(43, '! جدودنا عندهم الحق كي إطيبوا بالسبانخ', '', '<p align=\"right\"><span dir=\"RTL\"></p><p align=\"right\">السبانخ نلقاوه في برشا وصفات و أكلات تونسية، أبداها من السلايط، للشربة، للطاجين لغيرهم من الأكلات</p><p align=\"right\">لكن شنية تعرفو على هالخضرة، بخصوص منفعتو و فايدتو الغذائية ؟</p><p align=\"right\">السبانخ فيه نسبة كبيرة من الفيتامينات والمعادن</p><p align=\"right\">! يتميز بالسعرات الحرارية المنخفضة، لكن ارتفاع كبير في المواد المغذية الواقية</p><p align=\"right\">.السبانخ فيه فيتامين ا و س و ب 9. 100 غرام، منو تعطيك تقريباً ربع الحاجيات من فيتامين س</p><p align=\"right\">يفيدنا السبانخ زادا، برشا معادن ؛ في 100 غ السبانخ تلقى تقريباً ؛ 2.7 مغرام من الحديد، 20 بالمئة من المطلوب اليومي مالماغنزيوم، وربع الحاجيات اليومية مالبوتاسيوم</p><p align=\"right\"></span></span></p>', '', 'نصائح غذائية', '! جدودنا عندهم الحق كي إطيبوا بالسبانخ', 1, '', 0, 1, '2020-07-28 11:43:13', '2021-08-10 20:41:43', 1, 0, '', '2022-02-02 14:51:59', '43', 'shutterstock-52471888.jpg', '', 0),
(44, 'تحبها بالكشكوشة ؟', '', '<p align=\"right\" dir=\"rtl\">شكون فينا مايحبش مشروب الشكلاطة و بالكشكوشة ؟</p><p dir=\"rtl\">متفوتش على روحك نصيحة من ذهب : بعد ما تحضر مشروب الشكلاطة متاعك، حطو في الخلاط الكهربائي حتى لين يعمل الكشكوشة و صبو ديراكت في كأس و إستمتع !</p>', '', 'نصائح حلويات', 'تحبها بالكشكوشة ؟', 1, '', 0, 1, '2020-07-28 11:43:51', '2021-09-28 14:50:39', 1, 0, '', '2022-02-02 14:51:59', '44', 'shutterstock-777431902.jpg', '', 0),
(45, 'أشكون إلي قال إلي سميد و إلا الفارينة ما يصبروش ؟', '', '<p style=\"text-align: right;\">.حط السميد في وعاء و سكر عليه بالقدا في بلاصة باردة وشايحة</p><p style=\"text-align: right;\">.بالنسبة للفارينة تنجم تخزنها حتى لعام كامل، أي نعم عام كامل، وهذا كي تحطها في الثلاجة و في وعاء مسكر بالقدا</p><p style=\"text-align: right;\"></span></p>', '', 'نصائح عامة', 'أشكون إلي قال إلي سميد و إلا الفارينة ما يصبروش ؟', 1, '', 0, 1, '2020-07-28 11:45:54', '2021-06-24 22:54:02', 1, 0, '', '2022-02-02 14:51:59', '45', 'shutterstock-1132323131.jpg', '', 0),
(46, 'الفرمبواز حليفك الصحي', '', '<p dir=\"rtl\">الفرمبواز يحسسك بالخفة، وباهي برشا للريجيم على خاطروا من الغلة اللي فيها أقل نسبة من السكريات.</p><p align=\"right\">الغلة هاذي فيها نسبة مالمعادن كبيرة برشا ؛ كيف المانزيوم و الحديد، بخلاف الفيتامين \"س\"</p><p align=\"right\">موش هذا أكهو، الفرمبواز فيه عدد كبير من مضادات الاكسدة</p><p dir=\"rtl\">معلومة : 100غ مالفرمبواز فيه تقريب 38 سعرة حرارية فقط !</p><p dir=\"rtl\"><br /></span></p>', '', 'نصائح غذائية', 'الفرمبواز حليفك الصحي', 1, '', 0, 1, '2020-07-28 11:46:57', '2021-05-12 14:15:19', 1, 0, '', '2022-02-02 14:51:59', '46', 'shutterstock-1120213430.jpg', '', 0),
(47, 'زعمة باهي كنبدى إنهاري بعصير البرتقال ؟', '', '<p dir=\"rtl\">أي نعم، عصير برتقال، حاجة باهية في فطور الصباح، خاطرو، عندو برشة فوائد على صحتنا و يعاونا في محاربة نزلات البرد !</p><p dir=\"rtl\">عصير البرتقال الفرشك كل صباح و تناوله قبل ما تبدأ يومك, حاجة ننصحوك بيها برشة ؛ لأنو غني بالفيتامينات \"س\"، و بالمعادن و البوتاسيوم ...</p><p dir=\"rtl\">إلي يعطيونا شارج باهية على الصباح و يعاونا في محاربة نزلات البرد.</p>', '', 'نصائح غذائية', 'زعمة باهي كنبدى إنهاري بعصير البرتقال ؟', 1, '', 0, 2, '2020-07-28 11:48:03', '2020-12-31 13:03:37', 0, 0, '', '2022-02-02 14:51:59', '47', 'shutterstock-1157679286.jpg', '', 0),
(48, 'الحل باش الفاكية و إلا الغلة يبقاو على سطح الكايك ؟', '', 'أكيد برشا منا يحبو على الكايك بالفاكية و إلا بالغلة.لاكن المشكلة الي نتعرضولها الكل هي انو وقت نحطو المكونات متاع الزينة تهبط وطيح لوطة في خليط الكايك، شنوة الحل ؟زيد خميرة للخليط قدر مغرفة صغيرة هكاكا، باش تتنفخ بالقدا وماتخليش الفاكية متاعنا تطيح و إلا تغرق الداخل.\r\n', '', 'نصائح حلويات', 'الحل باش الفاكية و إلا الغلة يبقاو على سطح الكايك ', 1, '', 0, 1, '2020-07-28 11:48:26', '2021-10-04 08:28:06', 1, 0, '', '2022-02-02 14:51:59', '48', 'shutterstock-1851413254.jpg', '', 0),
(49, 'زعمة تعرف كل شيء على الباذنجان ؟', '', '<p style=\"text-align: right;\">في بالك إلي 100 غرام باذنجان تعطينا تقريب 32 سعرة حرارية و 4 غرامات من الألياف الشيء إلي يخليه باهي لرجيم تنقيص الوزن.</p><p style=\"text-align: right;\">و الباذنجان، فيه برشا معادن، كيما : الماغنزيوم و برشا فيتامينات كيما فيتامين \"ب6\"، فيتامين \"ك\"، وفيتامين \"س\" زيد عليهم الفسفور و النحاس !</p>', '', 'نصائح غذائية', 'زعمة تعرف كل شيء على الباذنجان ؟', 1, '', 0, 1, '2020-07-28 11:49:06', '2021-05-12 14:16:21', 1, 0, '', '2022-02-02 14:51:59', '49', 'shutterstock-1826914532.jpg', '', 0),
(51, 'شنوا إلي يخلي الكايك إطيح ؟', '', '<p style=\"text-align: right;\">يا حلاوة تكشفلك السر وراء الكايك و إلا الكاتو الطايح و اللي موش منفوخ بالقدا، رغم كمية الصحيحة متاع الخميرة الموجودة فيه و رغم إلي ما حليناش الكوشة و هو إطيب ؛</p><p style=\"text-align: right;\">السر يتلخص في خلط السكر و العضم و الزيت بالقدا، أما ! متكثرش من التخليط بعد ما تزيد الفارينة، باش ميخرجش الهواء إلي في الخليط.</p><p dir=\"rtl\"> </p><p> </p>', '', 'نصائح حلويات', 'شنوا إلي يخلي الكايك إطيح ؟', 1, '', 0, 1, '2020-07-28 11:51:33', '2021-05-12 14:11:52', 1, 0, '', '2022-02-02 14:51:59', '51', 'shutterstock-1788972290.jpg', '', 0),
(52, '! منافع البروكلو، ما تحقرهمش', '', 'البروكلو موجود في برشا أكلات تونسية ؛ السلاطة، المرقة و إلا حتى المبطن، شنوا منافعو ؟البروكلو، يعتبر من أكثر المصادر للفيتامينات كيما فيتامين \"أ\" وفيتامين \"ب6\" كما أنو مصدر لحمض الفوليك.الفيتامين \"ا\"، مثلاً باهي برشا للنظر و للجلدة زادة !\r\n', '', 'نصائح غذائية', 'منافع البروكلو، ما تحقرهمش !', 1, '', 0, 2, '2020-07-28 11:52:08', '2020-12-31 13:06:46', 1, 0, '', '2022-02-02 14:51:59', '52', 'shutterstock-142531987.jpg', '', 0),
(53, 'سبب تشقق الكايك ؟', '', '<p style=\"text-align: right;\">.السبب الرئيسي لتشقق الكايك : هو أنو كمية الفارينة وإلا الخميرة إلي كانت أكثر من اللازم</p><p style=\"text-align: right;\"> .</span>هوني النصيحة، إلي لازم نراعيو بالقدا المقادير اللازمة والمطلوبة لكل نوع متاع الكايك، لضمان نجاحه<br /></span></span></p>', '', 'نصائح حلويات', 'سبب تشقق الكايك ؟', 1, '', 0, 1, '2020-07-28 11:52:59', '2021-05-16 03:13:58', 1, 0, '', '2022-02-02 14:51:59', '53', 'shutterstock-593138387.jpg', '', 0),
(54, 'البطاطا و منافعها ', '', '<p align=\"right\">.البطاطا مصدر لفيتامين \"أ\" وفيتامين \"ب\" و فيتامين \"س\". و البطاطا المكوشة فيها تقريبا 95 سعرة حرارية</p><p align=\"right\">.البطاطا المكوشة و إلا المصموطة و إلا الطايبة في الماكلة ؛  مفيدة للبدن و عملية الهضم، في حدود المعقول بالطبيعة ! و زيد إتحسك بالشبع فيسع</p>', '', 'نصائح غذائية', 'البطاطا و منافعها ', 1, '', 0, 1, '2020-07-28 11:54:05', '2021-05-12 14:16:56', 1, 0, '', '2022-02-02 14:51:59', '54', 'shutterstock-222020848.jpg', '', 0),
(55, '! السر، باش تعرف صلاحية الخميرة', '', '<p style=\"text-align: right;\">الطريقة إلي  تنجم تعرف بيها مدى صلاحية الخميرة هي أنك، تحط شوية خميرة في وعاء فيه ماء دافئ مع شوية سكر، تخليها 5 دقايق. بعد كان لاحظت ظهور فقاقيع على السطح ؛ إستعمل الخميرة متاعك و إنت مطمان</p>', '', 'نصائح عامة', '! السر، باش تعرف صلاحية الخميرة', 1, '', 0, 1, '2020-07-28 11:54:53', '2021-09-22 08:24:28', 0, 0, '', '2022-02-02 14:51:59', '55', 'shutterstock-1806185203.jpg', '', 0),
(56, ' البصل إبكينا لكنو إحبنا الخير ', '', '<p align=\"right\">تعرفوا إلي 100 غرام من البصل فيه 33 سعرة حرارية يعني مفيد برشا للريجيم</p><p align=\"right\">و فيه زادة، برشا معادن كيما الفسفور، المنغيزيوم، الحديد، النحاس و الكاليسيوم... إلي هوما من الحاجات الأساسية لجسمنا</p>', '', 'نصائح غذائية', ' البصل إبكينا لكنو إحبنا الخير', 1, '', 0, 1, '2020-07-28 11:55:18', '2021-05-18 08:34:52', 1, 0, '', '2022-02-02 14:51:59', '56', 'shutterstock-1727380246.jpg', '', 0),
(57, ' ! السر في الخميرة زادا', '', '.الخميرة مهما كانت نوعيتها طازجة و الا فورية جافة، عندها دور كبير في نجاح وصفة العجينة متاعناهذاكا علاش، ليوم  إنودوكم إبنصايح إلي تخص الخميرة ؛،اولا، ماتحطش عليها الملح ديراكت،ثانيا، ذوبها في شويا ماء دافئ وحدها قبل متخلطها.ثالثا، زيدها شويا سكر باش فيسع، فيسع يصير التفاعل  ! و هكا تضمن مفعول الخميرةو نجاح العجينة و الوصفة\r\n', '', 'نصائح طبخ', ' ! السر في الخميرة زادا', 1, '', 0, 1, '2024-02-22 15:45:39', '2021-09-12 22:54:56', 1, 0, '', '2022-02-02 14:51:00', '57', 'shutterstock-1806185203.jpg', '', 0),
(58, 'الجلبانة عندها ما إتقلق !', '', '<p align=\"right\">أول ما تبرد الدنيا تبدا الأم التونسية تعول في الجلبانة و تستغل اي ماكلة باش تحط فيها الجلبانة، مرقة، كسكسي، شربة...</p><p align=\"right\">لكن شنيا المنافع متاعها ؟</p><p align=\"right\">على 100 غرام جلبانة طايبة فما تقريباً 71 سعرة حرارية و فيه زادا 5.2 غرام بروتين.</p><p align=\"right\">الجلبانة زادا فيها برشا فيتامينات، كيف الفيتامين \"س\" و الفيتامين \"أ\"</p><p align=\"right\">الجلبانة زادا غنية بالكربوهيدرات و الحديد و الالياف و الماغنزيوم و برشا أحماض كيما حامض الفوليك ...</p><p align=\"right\">بعد القيمة الغذائية هاذي الكل للجلبانة، أكيد برشا باش يبدلو رايهم و تتحول الجلبانة من شبح لصديق للصحة والبدن !</p>', '', 'نصائح غذائية', 'الجلبانة عندها ما إتقلق !', 1, '', 0, 2, '2020-07-28 11:56:38', '2020-12-31 13:17:19', 0, 0, '', '2022-02-02 14:51:59', '58', 'shutterstock-294875855.jpg', '', 0),
(60, 'يا بابا على فوائد الڨنارية !', '', '<p align=\"right\">الڨنارية أو الخرشف من ناحية القيمة الغذائية حاجة اقرب للخيال !</p><p align=\"right\">الخرشف فيه برشا فيتامينات كيما فيتامين \"ك\" و \"د\" وبرشا معادن كيما الحديد، الزنك، الصوديوم ، البوتاسيوم، الكاليسيوم، المنغيز، الفوسفور والزنك...</p><p align=\"right\">يعني مدخول غذائي كبير و مهم لحاجياتنا اليومية.</p><p align=\"right\">كي تعرفت على قيمة  الخرشف، ما تنساش باش تود روحك بربع  الڨنارية.</p><p align=\"right\"></span></p>', '', 'نصائح غذائية', 'يا بابا على فوائد الڨنارية !', 1, '', 0, 2, '2020-07-28 11:58:33', '2020-12-31 12:59:50', 0, 0, '', '2022-02-02 14:51:59', '60', 'shutterstock-620370836.jpg', '', 0),
(61, '! تخريج الكاتو من القالب بلاش مشاكل', '', '<p align=\"right\">.حد مينجم ينكر إلي أسهل و أضمن طريقة تخرج بيها الكايك و إلا الكاتو من القالب هي أنك تدهن القالب بالزبدة و الفارينة</p><p align=\"right\">.أما السر، الاهم هو أنك تحط القالب في بيت الثلج لين تجمد الزبدة مع الفارينة، و بعد تحط خليط الكاتو و ديراكت في الكوشة</p><p align=\"right\">.و هكا يخرج الكاتو نظيف و من غير ما يلصق في القالب</p>', '', 'نصائح حلويات', '! تخريج الكاتو من القالب بلاش مشاكل', 1, '', 0, 1, '2020-07-28 11:58:42', '2021-06-28 00:09:12', 1, 0, '', '2022-02-02 14:51:59', '61', 'shutterstock-1505805458.jpg', '', 0),
(62, 'الخس، باش الوزن إخس ', '', '<p align=\"right\" dir=\"rtl\">الخس فيه نسبة كبيرة متاع ماء تقريبا 95 بالمئة مالحجم متاعو.</p><p align=\"right\" dir=\"rtl\">و السعرات الحرارية إلي فيه شويا عاللخر يعني باهي برشا للناس الي تريجم ؛ تقريب 100 جرام فيها 14 سعرة حرارية. بخلاف إنو مصدر كبير للبروتين و الدهون و الكربوهيدرات.</p>', '', 'نصائح غذائية', 'الخس، باش الوزن إخس ', 1, '', 0, 1, '2020-07-28 11:59:41', '2021-05-14 04:30:18', 1, 0, '', '2022-02-02 14:51:59', '62', 'shutterstock-1131871259.jpg', '', 0),
(63, '! أسرار الشيفان لتطرية الزبدة ', '', 'المعروف انو كيف نستعملو الزبدة في تحضير وصفة لازمها تكون لينة و في درجة حرارة الكوجينة.لكن ساعات ننساو باش نخرجوها من الثلاجة و ما إنتفكروها كان وقت إستعمالها !اليوم عنا ليكم حل ساهل : حط الزبدة بين ورقتين طهي و بالقلقال حلها لين تولي طرية و حاضرة باش إتزيدها في الوصفة !\r\n', '', 'نصائح حلويات', 'أسرار الشيفان لتطرية الزبدة !', 1, '', 0, 1, '2020-07-28 12:00:00', '2021-05-14 13:51:12', 1, 0, '', '2022-02-02 14:51:59', '63', 'shutterstock-1255921693.jpg', '', 0),
(65, ' نية، طايبة و إلا مفورة ؛  السفنارية ديما باهية ', '', '<p style=\"text-align: right;\">.معروف عليها مفيدة للنظر والعينين، مش هذا أكهو </p><p style=\"text-align: right;\">.أولا، لازم تعرف إلي السفنارية كي تاكل منها تحسك بالشبع لمدة باهية</p><p style=\"text-align: right;\">.ثاني حاجة، السفنارية فيها برشا مضادات للاكسدة، وبرشا بيتاكاروتين المفيدة برشا لصحة الصغار و الكبار</p><p style=\"text-align: right;\">.و السفنارية فيها زادا نسبة كبيرة مالالياف والفيتامينات كيما فيتامين \"أ\" و \"س\" و \"ك\" و \"ب6\" وبرشة معادن كيف البوتاسيوم و النحاس و المنغيز و الحديد</p><p dir=\"rtl\">وتنجم تاكلها على كيفك ؛ نية، طايبة، مفورة و إلا حتى في الكوشة !</p>', '', 'نصائح غذائية', ' نية، طايبة و إلا مفورة ؛  السفنارية ديما باهية ', 1, '', 0, 1, '2020-07-28 12:02:06', '2021-05-12 14:15:41', 1, 0, '', '2022-02-02 14:51:59', '65', 'shutterstock-1498759292.jpg', '', 0),
(66, 'نصائح باش تنجح التيراميسو ', '', 'التيراميسو من الحلويات إلي ما إطيبش و تحضيرها موش صعيب برشا، كيما برشا يتصوروا !إنقدمولك بعض العوارض إلي تنجم إتصير و أسببها :- الخليط يكون سائل برشا و الموس ما تجمدش حتى كي تحطوا في الثلاجة ؛ السبب هو أنو أصفر العظم ما تخلطش مع السكر لمدة كافية حتى لين يولي الخليط لونو فاتح.- الخليط يكون موش متجانس ؛ إنجم يكون السبب في إضافة بياض البيض بسرعة. لازم تكون عملية إضافة بياض البيض على مراحل و بكل رفق. - عدم تماسك قوام التيراميسو ؛ إنجم يكون السبب أنو البسكويت تنفخ أكثر من اللازم. البسكويت وقت إلي يتغطس في القهوة و على الجهتين ما يلزموش يطرا.و في الأخر ؛ القاعدة المعروفة هي أنو التيراميسو، ما يتقدم كان بارد. بعد ماتكمل التركيب خليه يرتاح على الأقل 4 ساعات في الثلاجة.\r\n', '', 'نصائح حلويات', 'نصائح باش تنجح التيراميسو ', 1, '', 0, 1, '2023-05-14 22:47:31', '2021-05-12 14:12:32', 1, 7662, '', '2022-02-02 14:51:00', '66', 'shutterstock-1425307373.jpg', '', 0),
(68, ' ! البيطراف و منافعو', '', 'البيطراف بالعربية الفصحى إسمو الشمندر.البيطراف معروف خاصة في العصير أو السلاطة الطايبة و إلا النية. لكن شنيا منافعو ؟​البيطراف فيه عدة مضادات للاكسدة و برشا معادن كيما البوتاسيوم.من غير ما ننساو الالياف إلي يعاونوا ياسر في عملية الهضم\r\n', '', 'نصائح غذائية', ' ! البيطراف و منافعو', 1, '', 0, 1, '2020-07-28 12:04:13', '2021-09-17 08:22:48', 1, 0, '', '2022-02-02 14:51:59', '68', 'shutterstock-1065585824.jpg', '', 0),
(69, ' كيفاش تقوي مطعم الشوكلاطة في البراونيز ؟', '', '<p style=\"text-align: right;\"> تحب تقوي مطعم الشوكلاطة في للبراونيز ؟</p><p style=\"text-align: right;\"> في الوصفة زيد مغرفة صغيرة من القهوة الفورية في خليط البراونيز</p><p style=\"text-align: right;\">و هكاكة تظهر وتزيد تقوى نكهة الشوكلاطة</p><p> </p>', '', 'نصائح حلويات', ' كيفاش تقوي مطعم الشوكلاطة في البراونيز ؟', 1, '', 0, 1, '2020-07-28 12:04:57', '2021-05-17 15:32:57', 1, 0, '', '2022-02-02 14:51:59', '69', 'shutterstock-1406785433.jpg', '', 0),
(70, 'الطمطومة و الرجيم ؟', '', '<p align=\"right\">الطماطم مافيهش سعرات حرارية كبيرة، يعني باهية برشا للناس لليحبوا ينقصوا في الوزن</p><p align=\"right\">كعبة متوسطة متاع طماطم فيها 22 سعرة حرارية و فيها كمية عالية من الماء و الألياف و البروتين، الشيء إلي يخلينا نحسو بالشبع لمدة طويلة</p><p align=\"right\">الطماطم تفيدنا زادة، ببرشا فيتامينات و معادن كيما فيتامين \"ك\"، \"س\"، \"أ\"، و \"ب9\"</p><p align=\"right\">و بالنسبة للمعادن نلقاو : الماغنزيوم، البوتاسيوم، الفسفور و النحاس</p><p dir=\"rtl\">و هكا الطماطم بالقيمة الكبيرة متاعها رغم صغر حجمها، تعتبر من الماكلة البنينة و المفيدة للصحة.</p>', '', 'نصائح غذائية', 'الطمطومة و الرجيم ؟', 1, '', 0, 1, '2020-07-28 12:06:05', '2021-05-12 14:16:01', 1, 0, '', '2022-02-02 14:51:59', '70', 'shutterstock-83013037.jpg', '', 0),
(71, 'الفقوس المساند الرسمي للريجيم ؟', '', '<p align=\"right\">الفقوس، فيه شوية سعرات حرارية و غني بالألياف إلي تخلي الإنسان فيسع مايشبع و يفيد العباد إلي يحبو يريجمو.</p><p align=\"right\">الفقوس فيه عدة فيتامينات كيما فيتامين \"س\" اللي يعتبر منشط، وفيتامين \"أ\" و برشا معادن كيما الماغنزيوم و البوتاسيوم  ...</p><p align=\"right\" dir=\"rtl\">البوتاسيوم مثلاً، يلعب دورًا هام في الأداء السليم للجسم و في قوة عضلات الهيكل العظمي أو في عضلات القلب.</p>', '', 'نصائح غذائية', 'الفقوس المساند الرسمي للريجيم ؟', 1, '', 0, 1, '2020-07-28 12:06:57', '2021-05-12 14:17:13', 1, 0, '', '2022-02-02 14:51:59', '71', 'shutterstock-1724739523.jpg', '', 0),
(72, '! باش ترتاح من فقاعات الهواء إلي في الكايك ', '', '<p align=\"right\" dir=\"rtl\">برشا منا، يتقلقو مالفقاعات إلي في الكايك، على خاطرها تفسد الفورمة متاعوا.</p><p align=\"right\" dir=\"rtl\">الحل أنو بعد متحط الخليط في القالب، إضرب القالب بشوية و برشة مرات، على الطاولة و هكا تتخلص من الفقاعات إلي الداخل.</p>', '', 'نصائح حلويات', '! باش ترتاح من فقاعات الهواء إلي في الكايك ', 1, '', 0, 1, '2020-07-28 12:07:20', '2021-07-23 00:48:19', 1, 0, '', '2022-02-02 14:51:59', 'نصائح حفظ', 'shutterstock-1786449620.jpg', '', 0),
(73, 'الفلفل إينافس البرتقال في فيتامين \"س\" ؟', '', '<p dir=\"rtl\">الفلفل هو رمز الصيف إلي نلقاوه في برشا أنواع إمتاع سلايط، الشكشوكة و الهريسة... </p><p dir=\"rtl\">ميزتو، إنو خفيف في السعرات الحرارية و غني بالفيتامينات و إفوح بارشة وصفات.</p><p dir=\"rtl\">الفلفل زادة يعتبر من أفضل مصادر فيتامين \"س\" !</p><p dir=\"rtl\">إملا كي يغيب البرتقال عوضو بالفلفل، حار و إلا حلو، في عادتك الغذائية.</p>', '', 'نصائح غذائية', 'الفلفل إينافس البرتقال في فيتامين \"س\" ؟', 1, '', 0, 1, '2020-07-28 12:07:53', '2021-05-12 14:16:38', 1, 0, '', '2022-02-02 14:51:59', 'نصائح تنظيف', 'shutterstock-375808741.jpg', '', 0),
(75, 'تعرف إتفوح القرع بوطزينة ؟ ', '', '.القرع بوطزينة، بنين برشة وقت يكون مفوح مليح\r\n! القرع بوطزينة، يستحق شوية توابل و أعشاب للتفويح إلي هوما : الكمون، البابريكا، الثوم و الكركم بالطبيعة.كيما إتنجم زادة تعملو رشة حبق فرشكة و إلا  رشة نعناع شايح و كول بالصحة   \r\n', '', 'نصائح طبخ', 'تعرف إتفوح القرع بوطزينة ؟ ', 1, '', 0, 1, '2020-07-28 12:10:28', '2020-12-31 13:24:35', 1, 0, '', '2022-02-02 14:51:59', '75', 'shutterstock-1193774500.jpg', '', 0),
(76, '! الحل بش إتلف الجنواز على كيفك و منغير ما تتكسر', '', '<p style=\"text-align: right;\">.ساعات كيف تلف الجنواز، تتشقق و يفسد منظرها ! و التشققات هاذي ناتجة عن جفاف الجنواز</p><p style=\"text-align: right;\">.باش ترتاح من المشكلة هذه ؛ ساعة لازم الجنواز تبرد كلياً، بعد غطيها بقطعة قماش نظيفة و مبلولة شوية (منديلة) من 5  إلى 10 دقائق</p><p style=\"text-align: right;\">.لف توا، الجنواز على كيفك و من غير قلق</p>', '', 'نصائح حلويات', '! الحل بش إتلف الجنواز على كيفك و منغير ما تتكسر ', 1, '', 0, 1, '2020-07-28 12:10:35', '2021-05-24 08:19:01', 1, 0, '', '2022-02-02 14:51:59', '76', 'shutterstock-1072903838.jpg', '', 0),
(78, 'كيفاش تعرف الڨنارية فرشكة ؟ ', '', '<p dir=\"rtl\">كيفاش تعرف الڨنارية فرشكة ؟ </p><p dir=\"rtl\">وقت يكون رأس الڨنارية معبي مليح و أوراقو ملمومة لكل على بعضها و كي تمسهم تحس بتكتيكة صغيرة : هذا يعني إلي هي فرشكة و طيبها و إنت مطمان !</p>', '', 'نصائح عامة', 'كيفاش تعرف الڨنارية فرشكة ؟ ', 1, '', 0, 2, '2020-07-28 12:12:30', '2020-12-31 13:25:37', 0, 0, '', '2022-02-02 14:51:59', '78', 'shutterstock-1668998782.jpg', '', 0),
(79, ' أسرار الكريم شانتيي الناجحة ', '', '<p style=\"text-align: right;\">القاتو وتقريبا كل الحلويات أساس زينتها في الكريم شانتيي ! هذاكا علاش مهم، أنك تتحصل على كريمة هوائية رائعة</p><p style=\"text-align: right;\">أولا كل الأواني والمكونات المستعملة لازم تكون باردة بلقدا، مذابيك طحتهم في بيت الثلج ما بين 10 و 15 دقيقة</p><p style=\"text-align: right;\">ثانيا الكريمة السائلة لازمها تكون باردة زادا، و تكون نسبة الدهون إلي فيها 30٪ كحد أدنى. كان ما لقيتش في السوق النوعية هذه، إتنجم تستعمل الكريمة السائلة النباتية.</p><p style=\"text-align: right;\">خلطها بلقدا بالخلاط الكهربائي، لين تحس إلي حجمها زاد و ولات هشة وخفيفة</p><p style=\"text-align: right;\">أخيرا خليها في الثلاجة لين يجي وقت إستعملها</p>', '', 'نصائح حلويات', ' أسرار الكريم شانتيي الناجحة ', 1, '', 0, 1, '2020-07-28 12:12:56', '2021-05-12 14:17:50', 1, 0, '', '2022-02-02 14:51:59', '79', 'shutterstock-294485378.jpg', '', 0),
(86, 'كيفاش إتقص الكايك قصة متاع معلمية ؟', '', '<p align=\"right\" dir=\"rtl\">باش تقص الكايك بطريقة ساهلة و في نفس الوقت تخليك تتحصل على قطع مزيانة ! ياحلاوة تقدملك طرق لقصان الكايك :</p><p align=\"right\">الطريقة الكلاسيكية ؛ حط الكايك على قاعدة ثابتة و قص بالسكين الحادة المسننة</p><p align=\"right\">حط يديك في الوسط و علي سطح الكايك و مشي و جيب السكين لين تتحصل على زوز طروف متساويين</p><p align=\"right\"> فم زادة طريقة الخيط، لازم  الكايك يكون برد بالقدا، باش ما يفسدش كيف تقصو ؛</p><p align=\"right\">.توا بسكين ماضية، حدد شق صغير تقريب 1 صم، بالطريقة اللي تحب تقسم بيها الكايك</p><p align=\"right\">.و بإستعمال خيط  لتنظيف السنين، نظيف و جديد، دورو بالكيك، و إجبد الخيط لين تتقص و تتحصل على طبقتين كيف ماتحب</p>', '', 'نصائح حلويات', 'كيفاش إتقص الكايك قصة متاع معلمية ؟', 1, '', 0, 1, '2020-07-28 12:19:01', '2021-05-19 08:21:25', 1, 0, '', '2022-02-02 14:51:59', '86', 'shutterstock-771038506.jpg', '', 0),
(87, 'الصغار والخضرة ! شنوا الحل ؟', '', '<p align=\"right\">! نعرفوا إلي مصلحة صغاركم من أكثر إهتماماتكم</p><p align=\"right\">: إنقدمولكم  اليوم حلول لصغيرك باش تعاونوا على ماكلت الخضرة</p><p align=\"right\">.هز صغيرك معاك للسوق، وريه و علمو كيفاش نختارو الخضرة بطريقة فيها شويا ضحك و طرافة</p><p align=\"right\">.إذا ممكن، هز معاك صغيرك للبستان و وريه كيفاش تنضج الخضرة</p><p align=\"right\">.طيب مع صغيرك، أو على الاقل دخلو معاك الكوجينة، و وريه و علمو كفاش نطيبو الخضرة بطريقة ذكية و مميزة</p><p align=\"right\">.حضر طبق صغيرك بتشكيل حلو و مزيان يحببو في الماكلة</p><p align=\"right\" dir=\"rtl\">و أهم حاجة ما تجبرش الصغير على الماكلة.</p>', '', 'نصائح عامة', 'الصغار والخضرة ! شنوا الحل ؟', 1, '', 0, 1, '2020-07-28 12:20:09', '2021-06-29 22:10:29', 1, 0, '', '2022-02-02 14:51:59', '87', 'shutterstock-491645689.jpg', '', 0),
(88, 'كيفاش إتقلي بالزبدة من غير ما تتحرق ؟', '', 'الزبدة تعطي نكهة خاصة، كيف إنحمرو بيها اللحم و إلا الخضرة. لكن الزبدة من الدهون إلي متتحملش برشا سخانة، الشيء إلي يعرضها للاحتراق و تكحل الماكلة! الحل ساهل ؛ زيد شويا زيت للزبدة، باش تولي عندها قدرة لتحمل السخانة و تتحصل على نكهة بنينة، من غير لا مطعم و لا لون الحاجة المحروقة\r\n', '', 'نصائح طبخ', 'كيفاش إتقلي بالزبدة من غير ما تتحرق ؟', 1, '', 0, 1, '2020-07-28 12:21:37', '2021-08-18 08:37:46', 1, 0, '', '2022-02-02 14:51:59', '88', 'shutterstock-550602742.jpg', '', 0),
(89, '! التفاح ومن سمنة مرتاح  ', '', '<p align=\"right\">! فما برشة أنواع متاع غلة تعاونك باش تساهم في رشاقة بدنك</p><p align=\"right\">التفاح مثلاً، من أهم و أكثر الغلال إلي تحافظلك على بدن مزيان، على خاطر 100 غرام من التفاح فيه 48 سعرة حرارية فقط و برشة فيتامينات كيما فيتامين ك، س، ب و أ</p><p align=\"right\">و ما ننساوش إلي التفاح غني بالألياف و فيه كمية ماء باهية</p><p align=\"right\">.و مكذبش إلي قال تفاحة كل يوم تغنيك عن زيارة الطبيب</p>', '', 'نصائح غذائية', '! التفاح ومن سمنة مرتاح  ', 1, '', 0, 1, '2020-07-28 12:21:50', '2021-05-23 00:42:23', 1, 0, '', '2022-02-02 14:51:59', '89', 'shutterstock-1018507687.jpg', '', 0),
(90, 'باش مايغدركش الكراميل في تطيبو ', '', '<p style=\"text-align: right;\">.بعد ما تكمل تطيب الكراميل، و طفي القاز ؛ السكر إلي في الكراميل يكمل يطيب بالسخانة الي ديجا موجود فيها</p><p style=\"text-align: right;\">.و هذا يخلي الكراميل لونو يتبدل مالذهبي للبني الغامق و طعمو يولي مر </p><p style=\"text-align: right;\">.الحل هو، إتغطس ديراكت القدر في ماء بارد و من المستحسن فيه طروف ثلج ؛ و هكا طيح فيسع الحرارة و يحافظ الكراميل علي لونه والنكهة البنينة</p><p style=\"text-align: right;\"> </p>', '', 'نصائح حلويات', 'باش مايغدركش الكراميل في تطيبو ', 1, '', 0, 1, '2020-07-28 12:23:06', '2021-05-12 14:17:34', 1, 0, '', '2022-02-02 14:51:59', '90', 'shutterstock-238015234.jpg', '', 0),
(91, '! متغليش الماء مرتين', '', 'الماء المغلي نستعملوه في برشا حاجات، في القهوة و إلا التاي...ننصحوك باش متغليش الماء مرتين : كيف تغلي الماء مرتين، يفقد الأكسيجين و النيتروجين، و يصبح فاقد للقيمة متاعو.و إذا إستحقيت باش تعاود تسخنو، حطو في الميكرووند لمدة 30 ثانية، وهكا تضمن تحول وتحرك الجزئيات متاع الماء من غير ما يضيعو.\r\n', '', 'نصائح عامة', '! متغليش الماء مرتين', 1, '', 0, 1, '2020-07-28 12:23:27', '2021-05-21 22:19:48', 1, 0, '', '2022-02-02 14:51:59', '91', 'shutterstock-411675652.jpg', '', 0),
(92, ' ! باش تاخذ لقب أحسن واحد إطيب القوفر ', '', '<p dir=\"rtl\">ياحلاوة تمدك بسر، باش تبنن الڨوفر و إلا كما إقولو بالانڨليزية الوافلز :</p><p dir=\"rtl\">أولا حضر العجينة متاعك من قبل وخليها ترتاح بالقدا في الفريجيدار لمدة ساعة على الاقل،</p><p dir=\"rtl\">بعد ذوب شوية زبدة على نار هادية،</p><p dir=\"rtl\">زيدها للعجينة و خلطهم،</p><p dir=\"rtl\">توا سخن الماكينة بالقدا، وطيبها بالباهي.</p><p dir=\"rtl\">في الأخر باش تتحصل على ڨوفر لونو إمذهب و بنكهة دلولة !</p>', '', 'نصائح حلويات', ' ! باش تاخذ لقب أحسن واحد إطيب القوفر ', 1, '', 0, 1, '2020-07-28 12:24:08', '2021-05-21 11:29:18', 1, 0, '', '2022-02-02 14:51:59', '92', 'shutterstock-378613054.jpg', '', 0),
(93, 'أشكون إلي قال إلي القرع حكايتو فارغة ', '', '<p dir=\"rtl\">القرع فيه مكونات غذائية كبيرة ومهمة برشة للصحة :</p><p dir=\"rtl\">البذور متاع القرع فيها برشا بروتينات !</p><p dir=\"rtl\">و القرع فيه كمية كبيرة من الأحماض المضادة للأكسدة و فيتامين \"أ\" وعدة معادن مهمة أخرى للصغار و إلا للكبار !</p><p dir=\"rtl\">و القرع موجود بكثرة في أكلاتنا التونسية ؛ كسكسي، مرقة و إلا حتى شربة تي في الغرب يعملوه حتى كنفيتور (مربى)  و هذيكا حكاية أخرى...</p>', '', 'نصائح غذائية', 'أشكون إلي قال إلي القرع حكايتو فارغة', 1, '', 0, 1, '2020-07-28 12:24:37', '2021-05-15 04:00:50', 1, 0, '', '2022-02-02 14:51:59', '93', 'shutterstock-269502911.jpg', '', 0),
(94, 'الفستق لصحة العظام زادا ؟  ', '', '<p align=\"right\">الفستق عندو قيمة غذائية كبيرة برشا</p><p align=\"right\">هو من المكسرات القليلة إلي فيها كمية باهية من العناصر الغذائية كيما : البروتين و الكربوهيدرات</p><p align=\"right\">و فيه زادة برشا معادن كيما : الكالسيوم و الحديد و زالنك و النحاس و الصوديوم... إلي مهمين لصحة العظام</p><p align=\"right\" dir=\"rtl\"><font color=\"#202124\">28 غرام من الفستق تحتوي على 6 </font> غرام<font color=\"#202124\"> من البروتين و 2.8 </font> غرام<font color=\"#202124\"> من الألياف ؛ هذه هي الكمية اليومية الموصى بها من قبل السلطات الصحية في إطار تغذية متوازنة.</font></p><p align=\"right\"> </p>', '', 'نصائح غذائية', 'الفستق لصحة العظام زادا ؟  ', 1, '', 0, 1, '2020-07-28 12:25:48', '2021-05-13 01:14:25', 1, 0, '', '2022-02-02 14:51:59', '94', 'shutterstock-102461516.jpg', '', 0),
(95, 'كيفاش إتغلف الكاتو يا فنان ؟', '', '<p style=\"text-align: right;\">! تغليف الكاتو يخلي منظرو من البرا ميزيان ! و طريقة التزيين، عالم فيه برشا فن</p><p style=\"text-align: right;\">لكن باش تزين الكاتو متاعك،  لازم تخليه يبرد في الثلاجة لين تولي درجة الحرارة متاعو 30/35 درجة، باش الزينة ما تذوبش</p><p style=\"text-align: right;\">السر هو أنك تصب الصوص (شكولاطة، كراميل...) إلي باش تغلف بيها الكاتو في الوسط، وبعد تمشي للاجناب إذا لزم ؛ و هكة تبدى التغليفة متوازنة و النتيجة تحفونة</p><p style=\"text-align: right;\"> ديراكت بعد ما إتصب، تتنجم  إذا كان إستحقيت، تستعمل المغرفة المسطحة الخاصة، لتصحيح العيوب و باش تتحصل زادا على شكل رطب ومزيان</p>', '', 'نصائح حلويات', 'كيفاش إتغلف الكاتو يا فنان ؟', 1, '', 0, 1, '2020-07-28 12:26:36', '2021-08-15 20:10:00', 1, 0, '', '2022-02-02 14:51:59', '95', 'shutterstock-1519623947.jpg', '', 0),
(97, 'سر تحضير القالب قبل ما تحطو في الفور', '', '<p dir=\"rtl\">كل الحلويات تقريبا وقت  كي نجيو باش نطيبوها في الفور، يلزمنا ندهنو القالب، قبل حتى منحطو الخليط.</p><p dir=\"rtl\">نستعملو في العادة إيدينا، باش ندهنو القالب بالزيت و إلا بالزبدة، الشيء الي يخلق برشا تبلبيز ... وممكن حتى القالب يزلق منا !</p><p style=\"text-align: right;\">الحل ؟  كان ما عندكش فوشة إمتاع باتيسري ؛ غلف إديك بكيس متاع تثليج، إنضيف بالطبيعة، و حط شوية زبدة و إلا زيت و إدهن القالب الكل و هكا إديك يبقاو إنضاف</p>', '', 'نصائح حلويات', 'سر تحضير القالب قبل ما تحطو في الفور', 1, '', 0, 1, '2020-07-28 12:30:32', '2021-09-17 12:05:52', 1, 0, '', '2022-02-02 14:51:59', '97', 'shutterstock-1038385867.jpg', '', 0),
(98, 'كيفاش تبقا نكهة الايس كريم هي هي ؟', '', '<p style=\"text-align: right;\"><span dir=\"RTL\"></p><p dir=\"rtl\">كلنا نعرفو بعد متحل حكة الايس كريم (الجيلات)، و كي نرجعوها لبيت الثلج و نعاودو نجبدوها ؛ نلقاو فيها غلاف و طروف صغار متاع ثلج، اللي تفسد النكهة و المذاق الكريمي متاعو.</p><p dir=\"rtl\">ياحلاوة تقدملك الحل للمشكلة هاذي :</p><p dir=\"rtl\">قبل مترجع الحكة لبيت الثلج، حط فوق الايس كريم فيلم بلاستيك (بابيه سيلوفان) و إنزل شوية بيدك باش إتنقص أكثر ما يمكن من الهوا بن فيلم البلاستيك و الايس كريم.</p><p dir=\"rtl\">وهكا  الايس كريم يحافظ على المذاق الكريمي، كيما أول نهار حليت فيه الحكة !</p><p style=\"text-align: right;\"> </p>', '', 'نصائح عامة', 'كيفاش تبقا نكهة الايس كريم هي هي ؟', 1, '', 0, 1, '2020-07-28 12:32:56', '2021-06-17 22:36:49', 1, 0, '', '2022-02-02 14:51:59', '98', 'shutterstock-441140449.jpg', '', 0),
(99, 'الحل باش تنحي الكعابر من الكريمة الإنجليزية ؟', '', '<p dir=\"rtl\">ساعات و أحنا نطيبو في الكريمة، نطيحو في نفس المشكل ؛ يتكتل الخليط و يعمل كعابر !</p><p dir=\"rtl\">السر باش تتخلص من هالكعابر و تتحصل على كريمة رطبة و خفيفة<b> </b>:</p><p dir=\"rtl\">هي أنك تحط الكريمة في دبوزة بلاستيك و خلطها بالقدا و بالقوي عدة دقايق، لين تتحصل على كريمة سايلة و سلسة و القوام متاعها مزيان.</p>', '', 'نصائح حلويات', 'الحل باش تنحي الكعابر من الكريمة الإنجليزية ؟', 1, '', 0, 1, '2020-07-28 12:36:23', '2021-07-29 17:28:28', 1, 0, '', '2022-02-02 14:51:59', '99', 'shutterstock-1034475481.jpg', '', 0),
(100, 'تعرفشي، سر لمعان التارت ؟', '', '<p align=\"right\">تقعد بينك و بين روحك تسأل : شبيها التارت متاعي ماعندهاش نفس اللمعة كيما في المحلات ؟ تحب تعرف السر ؟</p><p align=\"right\" dir=\"rtl\">بصفة خاصة ننصحوك بمعجون المشماش إلي عندو لمعة باهية و لونو و مطعمو ما إثروش على نكهة التارت.</p><p align=\"right\">.سخن مغرفتين معجون  المشماش على نار هادية، باش يتحل، خليه يبرد شوية، و إدهن بيه التارت</p>', '', 'نصائح حلويات', 'تعرفشي، سر لمعان التارت ؟', 1, '', 0, 1, '2020-07-28 13:27:44', '2021-09-23 15:19:49', 1, 0, '', '2022-02-02 14:51:59', '100', 'shutterstock-140338711.jpg', '', 0);
INSERT INTO `tips` (`tip_id`, `tip_title`, `tip_seotitle`, `tip_description`, `tip_seodescription`, `tip_category`, `tip_slug`, `tip_author`, `tip_image`, `tip_featured`, `tip_status`, `tip_created`, `tip_updated`, `is_paying`, `likes_booster`, `note`, `scheduled_publish_date`, `id_intern`, `video_link`, `thumbnailName`, `video_nature`) VALUES
(101, '! الحل للكاتو، الناقص طياب ', '', '! كيف نطيبو الكاتو، و نشوفو الفوقاني متاعو لونو بني وكمل طاب، أما وسطو مازال ناقص طياب ! وقتها نخافو عليه لا يتحرق الحل ساهل ؛ غطي سطح الكاتو بورقة أليمنيوم،و زيد الوقت من 5 إلى 15 دقيقة، وهكا تمنع الكاتو و يطيب الكل.\r\n', '', 'نصائح حلويات', '! الحل للكاتو، الناقص طياب ', 1, '', 0, 1, '2020-07-28 13:29:53', '2021-05-30 09:30:16', 1, 0, '', '2022-02-02 14:51:59', '101', 'shutterstock-1139281754.jpg', '', 0),
(102, '! خليك فنان في قصان الكاتو', '', '<p dir=\"rtl\">باش ما تخليش الكاتو تاعك يتبلحط و يسيح و أنت تقص فيه ؛</p><p dir=\"rtl\">ننصحوك باش إتغطس السكينة في الماء السخون قبل ما إتقص و أمسحها ديراكت بعد كل قصة.</p><p dir=\"rtl\">و هكا تتحصل في الأخر على طروف نظيفة و منظرها مزيان !</p>', '', 'نصائح حلويات', '! خليك فنان في قصان الكاتو', 1, '', 0, 1, '2020-07-28 13:31:12', '2021-08-04 00:14:14', 1, 0, '', '2022-02-02 14:51:59', '102', 'shutterstock-587660726.jpg', '', 0),
(103, 'باش الكريمة ما تشيحلكش من الفوق ؟', '', '<p align=\"right\" dir=\"rtl\">كيف إنطيبو كريمة حلويات و إلا موس تعمل طبقة خارجية كاسحة و شايحة تقلق في المنظر و إلا عند الاستعمال.</p><p align=\"right\" dir=\"rtl\">الحل ساهل برشا، قبل ماتحط الكريمة في الثلاجة، غلفها بفيلم بلاستيكي (بابيه سيلوفان)، حط الفيلم البلاستيكي على الكريمة ديراكت وما تخليش فراغ بيناتهم.</p><p dir=\"rtl\">بالطريقة هاذي تنجم تستعمل كريمة الحلويات و إلا الموس من غير طبقة خارجية كاسحة و شايحة، تقلقك كيف تستعملها في الوصفة بعد.</p>', '', 'نصائح حلويات', 'باش الكريمة ما تشيحلكش من الفوق ؟', 1, '', 0, 1, '2020-07-28 13:33:00', '2021-07-26 00:00:22', 1, 0, '', '2022-02-02 14:51:59', '103', 'shutterstock-619546784.jpg', '', 0),
(104, 'شنوا سر الكراب الخفيفة ؟', '', '<p style=\"text-align: right;\">الكراب حاجة محبوبة، حلوة أو مالحة !</p><p style=\"text-align: right;\">ياحلاوة تقدملك سر الكراب إلي عجينتها خفيفة ؛</p><p style=\"text-align: right;\">في عوض ماتستعمل الماء العادي، إستعمل ماء غازي، السر، إلي يخلي لعجينة خفيفة !</p><p dir=\"rtl\"> </p><p></span></p>', '', 'نصائح حلويات', 'شنوا سر الكراب الخفيفة ؟', 1, '', 0, 1, '2020-07-28 13:34:32', '2021-05-12 14:06:03', 1, 0, '', '2022-02-02 14:51:59', '104', 'shutterstock-735776689.jpg', '', 0),
(105, 'مبيناتنا، هذي أسرار عجينة التارت', '', '<p dir=\"rtl\">ياحلاوة تقدملك خمس نصائح بسيطة، باش تتحصل على عجينة تارت ياسر بنينة و خفيفة : </p><p align=\"right\">أول حاجة لازم المكونات متاعك من سكر و عضم و زبدة ... تبدا عندهم نفس درجة حرارة،</p><p align=\"right\">ثاني حاجة، زيد شويا ماء للفارينة و متكثرش، خاطر ممكن يخليها كاسحة و شايحة،</p><p align=\"right\">ثالث سر، متعجنش العجينة تاعك برشا الشيء اللي يخليها مطاطية و يابسة،</p><p align=\"right\">خلي العجينة ترتاح لمدة 45 دقيقة في الثلاجة، بش حلانها يبدا ساهل، هاذي الخطوة الرابعة،</p><p dir=\"rtl\">اما أخيرا، ما تسخنش الفور بارشة، وهكا العجينة تطيب في عقلها و تكون طرية !</p>', '', 'نصائح حلويات', 'مبيناتنا، هذي أسرار عجينة التارت', 1, '', 0, 1, '2020-07-28 13:40:08', '2021-05-12 14:06:32', 1, 0, '', '2022-02-02 14:51:59', '105', 'shutterstock-610933862.jpg', '', 0),
(106, '! سر المادلين الخفيفة و بالقبة', '', '<p style=\"text-align: right;\"><span dir=\"RTL\"><span dir=\"RTL\"></p><p style=\"text-align: right;\">سر المادلين الخفيفة و إلي تبدا عالية و بالقبة ؛</p><p style=\"text-align: right;\">،حط قالب الخليط متاع المادلين يرتاح في بيت الثلج</p><p style=\"text-align: right;\">،بعد حطو ديراكت في الفور، المسخن مسبقا</p><p style=\"text-align: right;\"> </span> !<span data-cke-marker=\"1\"> الصدمة هاذي من البارد للسخون، تخلي الخليط يتنفخ و ياخذ فورمة باهية و يعمل هاك القبة المزيانة</p><p dir=\"rtl\"> </p>', '', 'نصائح حلويات', '! سر المادلين الخفيفة و بالقبة', 1, '', 0, 1, '2020-07-28 13:44:40', '2021-09-03 00:44:53', 1, 0, '', '2022-02-02 14:51:59', '106', 'shutterstock-406318747.jpg', '', 0),
(107, 'كيفاش تجي، عجينة الشو هشوشة ؟', '', '<p style=\"text-align: right;\">من المعروف، أنو عجينة الشو و هي بيدها عجينة لكلار ؛ تنجم تستعملها مالحة و إلا حلوة، لكن تبقى أهم صفة لازمها تكون في العجينة هاذي هي : الهشاشة</p><p style=\"text-align: right;\">باش تتحصل على عجينة هشة ومنفوخة عنا ليك الحل ؛</p><p style=\"text-align: right;\">.و إنت تطيب فيها  ؛ ماتحلش الفور، خليها تاخو راحتها وتتنفخ في عقلها مانغير، ما تعرضها للهواء البارد</p><p style=\"text-align: right;\">.أخيرا، كيف تكمل إتطيب، حل باب الفور وخليها تبرد في وسطو</p><p dir=\"rtl\"> </p>', '', 'نصائح حلويات', 'كيفاش تجي، عجينة الشو هشوشة ؟', 1, '', 0, 1, '2020-07-28 13:46:15', '2021-05-26 08:23:28', 1, 0, '', '2022-02-02 14:51:59', '107', 'shutterstock-1849478308.jpg', '', 0),
(109, '! عجينة البريوش، إلي ما إقولشي الشفان', '', '<p style=\"text-align: right;\">أهم حاجة لنجاح البريوش، هو نوعية المكونات المستعملة كالفارينة و الزبدة و العظم</p><p style=\"text-align: right;\">ياحلاوة تنصحك باستعمال عضم من الجودة العالية، اللي عادة تكون فيه أكثر قيمة غذائية</p><p style=\"text-align: right;\">بالنسبة للزبدة، صحيح تخلي العجينة طرية و خفيفة لكن تخليها ساعات صعيبة في التشكيل، هاذكا علاش، تجنبا لكل الصعاب، حط نسبة الميزان، 25 بالمئة، تقريباً زبدة من نسبة ميزان الفارينة و يمكن حتى تكون أكثر بشويا</p><p style=\"text-align: right;\">هاذي نصائحنا في المكونات</p><p style=\"text-align: right;\">بالنسبة لطريقة التحضير، لازمك نهار، كان ممكن، علما انو كل العجائن المتخمرة، لازمها بارش وقت ترتاح فيه</p><p style=\"text-align: right;\">،هاذكا علاش، حضر عجينتك قبل و خليها تتخمر في عقلها، في درجة حرارة البيت</p><p style=\"text-align: right;\">! بعد دخلها للثلاجة ليلة كاملة و على الاقل 6 سوايع، باش تبرد و تكون النتيجة عجينة هوائية خفيفة</p>', '', 'نصائح حلويات', '! عجينة البريوش، إلي ما إقولشي الشفان', 1, '', 0, 1, '2020-07-28 13:52:04', '2021-05-31 09:27:03', 1, 0, '', '2022-02-02 14:51:59', '109', 'shutterstock-430888570.jpg', '', 0),
(110, '! فازة ساهلة، تعرف أنو الخبز طاب', '', '<p style=\"text-align: right;\">طريقة تتأكد بيها أنو الخبز طاب ؛</p><p style=\"text-align: right;\">،الكل يعرف إلي الخبز كيف يطيب تولي عندو قشرة خارجية تتقرمش و هشوشة من الداخل</p><p style=\"text-align: right;\">،طبطب على الخبز من اللوطة، كان سمعت صوت طبطيبة فارغة، راهو الخبز طاب</p><p style=\"text-align: right;\">.و كان سمعت صوت طبطيبة معبية، خليه يكمل يطيب على راحتو</p><p style=\"text-align: right;\">! و غطيه بالأليمنيوم، باش ما يتحرقش من الفوق و يبقى محافظ على الللون الذهبي المزيان متاعو</p>', '', 'نصائح طبخ', '! فازة ساهلة، تعرف أنو الخبز طاب', 1, '', 0, 1, '2020-07-28 13:55:07', '2021-08-08 14:41:29', 1, 0, '', '2022-02-02 14:51:59', '110', 'shutterstock-211314982.jpg', '', 0),
(111, 'شوف كيفاش العجينة فيسع ما تخمر؟', '', '<p align=\"right\">ما عندكش وقت وتحب عحينتك تخمر فيسع ؟</p><p align=\"right\">.و أنت تعجن في العجينة، سخن الفور على أقل درجة حرارة، ثم طفيه</p><p align=\"right\">.توا حط العجينة تاعك في الفور، و هكا فيسع، فيسع، ما تخمر</p><p align=\"right\">.​بالطريقة هاذي عجينتك تحضر فيسع و منغير تضييع و قت</p>', '', 'نصائح طبخ', 'شوف كيفاش العجينة فيسع ما تخمر؟', 1, '', 0, 1, '2020-07-28 13:57:22', '2021-05-12 14:05:32', 1, 0, '', '2022-02-02 14:51:59', '111', 'shutterstock-1175142106.jpg', '', 0),
(112, 'تحب على خبز إمبشمط ؟', '', '<p style=\"text-align: right;\"><span dir=\"RTL\"><span dir=\"RTL\"></p><p align=\"right\">تحب على خبز إمبشمط، في الدار  و على كيف، كيفك ؟</p><p align=\"right\">.قص الخبز البايت و خلطو بشويا إكليل و إلا نعناع شايح (حسب الرغبة)...  و زيت زيتونة</p><p align=\"right\">.حطهم في طبق و فوق ورق الطهي و دخلهم للفور على 150 درجة لمدة 5 دقائق على الاقل</p><p align=\"right\">! وهكا تتحصل على خبز إمبشمط بنين ومفوح كيما تحب</p><p align=\"right\">.بالطريقة هاذي, تحافظ  زادا على الخبز و مطيشوش</p><p style=\"text-align: right;\"><span dir=\"RTL\"><span dir=\"RTL\"></p>', '', 'نصائح طبخ', 'تحب على خبز إمبشمط ؟', 1, '', 0, 1, '2020-07-28 14:00:10', '2021-08-29 22:57:39', 1, 0, '', '2022-02-02 14:51:59', '112', 'shutterstock-795816337.jpg', '', 0),
(114, '! أنسى العجينة لتلصق على القلقال ', '', '<p align=\"right\">تتقلق وقت تشدلك العجينة في القلقال كيف تجي تحلها ؟</p><p align=\"right\">: يا حلاوة تقدملك الحل</p><p align=\"right\">! حط ورقة متاع طبخ تحت و فوق العجينة و حلها براحتك</p>', '', 'نصائح طبخ', '! أنسى العجينة لتلصق على القلقال ', 1, '', 0, 1, '2020-07-28 14:06:10', '2021-06-23 11:52:56', 1, 0, '', '2022-02-02 14:51:59', '114', 'shutterstock-165757505.jpg', '', 0),
(115, '! ساهل باش تنجح في تحضير الفرنش توست', '', '<p style=\"text-align: right;\">سر الفرانش توست الناجح و البنين</p><p style=\"text-align: right;\">ياش تحضر الفرنش توست تاعك من الخبز البايت، لستة اشخاص ؛</p><p style=\"text-align: right;\">خلط كعبتين عضم</p><p style=\"text-align: right;\">،مع 60 صل من الحليب</p><p style=\"text-align: right;\">،مع ساشي سكيت فانيليا</p><p style=\"text-align: right;\">مع ربع مغرفة صغيرة قرفة مرحية (حسب الرغبة)</p><p style=\"text-align: right;\">،غطس الخبز البايت في الخليط هاذا، لكن من غير ما يشرب برشا منو باش ميتفتفتش</p><p style=\"text-align: right;\">،حمر الخبز شويا على الزبدة و إلا السمن في المقلاة لمدة دقيقة وحدة على كل جهة</p><p style=\"text-align: right;\">.هكا يكون عندك فرانش توست بنين و خفيف و إلي تنجم إتزدو ؛ طرف غلة، عسل أو مربى</p>', '', 'نصائح حلويات', '! ساهل باش تنجح في تحضير الفرنش توست', 1, '', 0, 1, '2020-07-28 14:11:02', '2021-05-27 08:24:30', 1, 0, '', '2022-02-02 14:51:59', '115', 'shutterstock-1314471524.jpg', '', 0),
(117, '! نصيحتنا باش خبز الحلو بالتوابل ما يشيحش', '', '<p style=\"text-align: right;\">! خبز الحلو بالتوابل أو بالفرنسية \"البان ديبيس\" بخلاف بنتو و حلاوتو،لازم يبقى طري زادة</p><p style=\"text-align: right;\">.الخبز الحلو بالتوابل، بعد مايطيب، ساعات يشيح و ييبس، هاذكا علاش، ننصحوك كي تحطو إطيب في الفور، حط معاه، وعاء فيه شويا ماء</p><p style=\"text-align: right;\">،و بعد ما إطيب و يبرد، ديراكت، غلفو بفيلم بلاستيك ( بابيه سيلوفان)</p><p style=\"text-align: right;\">! و هكا يبقى طري و بنين</p>', '', 'نصائح حلويات', '! نصيحتنا باش خبز الحلو بالتوابل ما يشيحش', 1, '', 0, 1, '2020-07-28 14:18:16', '2021-09-01 00:16:42', 1, 0, '', '2022-02-02 14:51:59', '117', 'shutterstock-530873050.jpg', '', 0),
(119, 'أسهل طريقة لتقشير للبطاطا المسموطة', '', '<p style=\"text-align: right;\">! تقشير البطاطا المسموطة و خاصة كي تبدى سخونة ! يا ميمتي</p><p style=\"text-align: right;\">: أسهل طريقة لتقشير للبطاطا المسموطة</p><p style=\"text-align: right;\">،بعد ما إطيب حطها، ديراكت تحت الماء البارد، بش تبريد شوية</p><p style=\"text-align: right;\">! و هكة، القشرة الفوقانية تكساح و زيد تولي كعبة البطاطا أقل هشاشة و من غير ما تتفتفت</p><p style=\"text-align: right;\"></span></span></p>', '', 'نصائح طبخ', 'أسهل طريقة لتقشير للبطاطا المسموطة ', 1, '', 0, 1, '2020-07-28 14:37:58', '2021-08-20 00:22:03', 1, 0, '', '2022-02-02 14:51:59', '119', 'shutterstock-140688031.jpg', '', 0),
(120, '! من اليوم  إنسى تعب تقشير الطماطم', '', '<p style=\"text-align: right;\">: من اليوم  إنسى تعب تقشير الطماطم</p><p style=\"text-align: right;\">،أعمل قاطع و مقطوع (على شكل علامة +) على رأس كل كعبة طماطم،</p><p style=\"text-align: right;\">،مبعد حط الطماطم في ماء يغلي و خليها دقية و نصف تقريباً</p><p style=\"text-align: right;\">،أخيرا خرجها و حطها مباشرة في الماء مع طروف ثلج</p><p dir=\"rtl\">أك القاطع و المقطوع باش يحلو قشرة كعبة الطماطم و هكة إسهل عليك المهمة !</p>', '', 'نصائح طبخ', '! من اليوم  إنسى تعب تقشير الطماطم', 1, '', 0, 1, '2020-07-28 14:41:02', '2021-06-21 08:36:06', 1, 0, '', '2022-02-02 14:51:59', '120', 'shutterstock-597419105.jpg', '', 0),
(121, 'قشور العضم الطايحين في الماكلة، شعملنا فيهم ؟', '', '<p style=\"text-align: right;\">! كي إنفقصوا لعضم و إيطيح طرف قشرة في الوعاء ! آش باش يسلكها ؟ و نقعدوا نحاولوا بكل الطرق كيفاش نحيوها</p><p style=\"text-align: right;\">: اليوم و مع ياحلاوة انقدمولكم حل ناجح</p><p style=\"text-align: right;\">.كيف تفيق بطرف قشور عضم في الوعاء، بل صبعك بشويا ماء و ديراكت نحيه</p><p dir=\"rtl\">باش تلاحظ انو القشور تلصق فيسع و ما تعاودش إطيح !</p>', '', 'نصائح طبخ', 'قشور العضم الطايحين في الماكلة، شعملنا فيهم ؟', 1, '', 0, 1, '2020-07-29 09:57:18', '2021-08-01 23:12:13', 1, 0, '', '2022-02-02 14:51:59', '121', 'shutterstock-28022941.jpg', '', 0),
(123, 'أسهل طريقة لتقشير اللوز ', '', '<p mce_style=\"text-align: right;\" style=\"text-align: right;\">​حط اللوز في ماء يغلي لمدة دقيقتين</p><p mce_style=\"text-align: right;\" style=\"text-align: right;\">و بعد خرجو و حطو لمدة دقيقة واحدة في وعاء فيه ماء و شويا ثلج و ديراكت نحي القشرة.</p><p mce_style=\"text-align: right;\" style=\"text-align: right;\">​بالطريقة هاذي، تنحي القشرة بكل سهولة ويبقى اللوز بنفس البنه ونفس النكهة !</p>', '', 'نصائح عامة', 'أسهل طريقة لتقشير اللوز ', 1, '', 0, 1, '2020-07-29 10:00:59', '2021-05-12 14:05:10', 1, 0, '', '2022-02-02 14:51:59', '123', 'shutterstock-1130045219.jpg', '', 0),
(125, '!  شوف كيفاش ! تفصل لبيض من لصفر لعضم', ' شوف كيفاش ! تفصل لبيض من لصفر لعضم !', '<p style=\"text-align: right;\">! ​الكل عنا المشكل في فصل أبيض العضم على الاصفر تاعها</p><p style=\"text-align: right;\">: ​ياحلاوة تعطيك حل ساهل</p><p style=\"text-align: right;\">! خوذ قمع صغير، وفقس العضمة في وسطو، باش تلاحظ أن لبيض يهبط لوطة والاصفر يقعد في الوسط</p>', '', 'نصائح طبخ', ' ! شوف كيفاش ! تفصل لبيض من لصفر لعضم', 1, '', 0, 1, '2020-07-29 10:03:22', '2021-07-27 14:26:02', 1, 0, '', '2022-02-02 14:51:59', '125', 'shutterstock-363403886.jpg', '', 0),
(127, 'تحب إطيب عظمة مزيانة ؟', 'تحب إطيب عظمة مزيانة ؟', '<p style=\"text-align: right;\">​تحب على عضمة بنينة و من غير لون بني على لجناب ؟</p><p style=\"text-align: right;\">.ما تسخنش المقلى على الأخر و على نار هادية، خليها اطيب في عقلها</p><p style=\"text-align: right;\">! بالطريقة هذي، العضمة متنشفش فيسع و خاصة ما تيبسش و زيد لونها إجي شباب</p>', '', 'نصائح طبخ', 'تحب إطيب عظمة مزيانة ؟', 1, '', 0, 1, '2020-07-29 10:09:33', '2021-05-25 01:37:23', 1, 0, '', '2022-02-02 14:51:59', '127', 'shutterstock-344915381.jpg', '', 0),
(129, 'تقشير الزنجبيل بأسهل طريقة ! ', 'تقشير الزنجبيل بأسهل طريقة ! ', '<p style=\"text-align: right;\">​الزنجبيل مستعمل برشا في عدة وصفات لكن تقشيرو عند برشا منا، صعيب !</p><p style=\"text-align: right;\">هذكا علاش كالعادة، ياحلاوة تعطيك السر و تقلل عليك التعب :</p><p style=\"text-align: right;\">إستعمل مغرفة صغيرة في التقشير عوض السكين !</p>', '', 'نصائح عامة', 'تقشير الزنجبيل بأسهل طريقة ! ', 1, '', 0, 2, '2020-07-29 10:13:50', '2020-12-31 11:53:19', 0, 0, '', '2022-02-02 14:51:59', '129', 'shutterstock-763861315.jpg', '', 0),
(130, 'كيفاش إتخرج الفول من قشرتو ؟', '', 'بسكينة صغيرة أعمل شق على كل كعبة فول من الفوق، الحلة بالسكينة لازم تكون معاكسة الخط لخضر الغامق إمتاع الفولة،و بعد حطو لمدة 20 ثانية في ماء يغلي،نحيه و حطوا ديراكت تحت ماء بارد،أظغط على حبة الفول من الجهة المعاكسة للشق ؛ هكاكة يخرج ديراكت مالقشرة متاعو \r\n', '', 'نصائح عامة', 'كيفاش إتخرج الفول من قشرتو ؟', 1, '', 0, 2, '2020-07-29 10:15:10', '2020-12-31 11:53:50', 1, 0, '', '2022-02-02 14:51:59', '130', 'shutterstock-1763184677.jpg', '', 0),
(133, '! طريقة سهلة لتقشير الثوم', '', '.​حط سنون الثوم في وعاء زجاجي. سنون الثوم لازمهم يعبيو ربع الوعاء الزجاجي.سكرو توة و حركو بقوة! باش تشوف إلي حبات الثوم تخلصوا من قشرتهم\r\n', '', 'نصائح عامة', '! طريقة سهلة لتقشير الثوم', 1, '', 0, 1, '2024-05-27 15:31:28', '2021-07-18 23:37:08', 1, 0, '', '2022-02-02 14:51:00', '133', 'shutterstock-1017560695.jpg', '', 0),
(134, 'أسهل طريقة لتقشير الفلفل المشوي ', '', '<p style=\"text-align: right;\"> : ​يا حلاوة تقدملك السر باش ما تعديش برشا وقت في تقشير الفلفل</p><p style=\"text-align: right;\">.حط الفلفل في طبق في الفرن وإلا على النار حتى يتشوى</p><p style=\"text-align: right;\"> .و بعد حطو ديراكت في كيس بلاستيك، لمدة 15 دقيقة</p><p style=\"text-align: right;\">.خرج الفلفل تاعك من الكيس</p><p style=\"text-align: right;\">.توا، تلقاه ساهل في تقشير </p>', '', 'نصائح طبخ', 'أسهل طريقة لتقشير الفلفل المشوي ', 1, '', 0, 1, '2020-07-29 10:23:33', '2021-05-12 14:21:15', 1, 0, '', '2022-02-02 14:51:59', '134', 'shutterstock-296066627.jpg', '', 0),
(137, 'كيفاش تعرف العضم الفرشك ؟', '', '<p style=\"text-align: right;\">​يتخلط عليك العضم و ما عادش تعرف الجديد من القديم ؟</p><p style=\"text-align: right;\">: من اليوم يا حلاوة تعاونك باش تفرق بيناتهم</p><p style=\"text-align: right;\"> ،حط العضم في صحفة بلار و فيها ماء</p><p style=\"text-align: right;\">،إلي تهبط لوطا، راهي جديدة و صالحة للإستهلاك</p><p style=\"text-align: right;\">.و إلي تطفو الفوق راهي قديمة و مذابيك إطيشها</p><p style=\"text-align: right;\"></span></span></p>', '', 'نصائح طبخ', 'كيفاش تعرف العضم الفرشك ؟', 1, '', 0, 1, '2020-07-29 10:28:39', '2021-06-02 08:25:44', 1, 0, '', '2022-02-02 14:51:59', '137', 'shutterstock-1099495331.jpg', '', 0),
(138, 'تحب الافوكادو، ينضج فيسع ؟', '', '<p style=\"text-align: right;\">،حط كعبة الافوكادو في كيس بلاستيك مع موزة في درجة حرارة الكوجينة ليلة كاملة</p><p style=\"text-align: right;\">! و كانك مزروب، حطها لمدة 30 ثانية في الميكرووند و هكا تكون حاضرة للإستعمال</p><p style=\"text-align: right;\"><br /></span></p>', '', 'نصائح طبخ', 'تحب الافوكادو، ينضج فيسع ؟', 1, '', 0, 1, '2020-07-29 10:33:01', '2021-06-14 08:46:57', 1, 0, '', '2022-02-02 14:51:59', '138', 'shutterstock-116185129.jpg', '', 0),
(139, 'تعرفش، أحسن طريقة لحفظ التوابل ؟', '', '<p style=\"text-align: right;\">أكيد ما سمعتش إلي الضو هو العامل الأساسي إلي ينقص من نكهة التوابل، على خاطرو يزيد من عمرها بطريقة رهيبة وما تستغربش من هذا، خاطر التوابل في الأصل كانت نباتات.</p><p style=\"text-align: right;\"> مع ياحلاوة اكتشف الطريقة المثالية لحفظ التوابل :</p><p style=\"text-align: right;\">تتلخص النصيحة في حفظ توابلك في أوعية ماتعديش الضو،</p><p style=\"text-align: right;\">أو حط الأوعية بعيدة على الضو، كيما ؛ درج أو مارو الكوجينة.</p>', '', 'نصائح عامة', 'تعرفش، أحسن طريقة لحفظ التوابل ؟', 1, '', 0, 1, '2020-07-29 10:35:40', '2021-05-12 14:19:01', 1, 0, '', '2022-02-02 14:51:59', '139', 'shutterstock-1060609166.jpg', '', 0),
(140, 'تعرفها أحسن طريقة باش إتصبر الفرمبواز؟', '', '<p style=\"text-align: right;\">! الكلنا نعرفوا أنو من الصعيب تخزين الفرمبواز، خاطر حباتو طرية و حساسة ؛ لاكن موش من اليوم</p><p style=\"text-align: right;\">،أول حاجة لازمك تغسل الفرمبواز و تخليه يستقطر بالقدا، لين إيشيح</p><p style=\"text-align: right;\">،حل توا، كعبات الفرمبواز على طبق و ما تخليهمش لاسقين بعضهم</p><p style=\"text-align: right;\">،حط الطبق في بيت الثلج</p><p style=\"text-align: right;\">.بعد ما يتجمد الفرمبواز حطو في كيس، ورجعو في لبيت الثلج مرة أخرى ؛ ​الطريقة هاذي تخلي كعبات الفرمبواز ما يلسقوش في بعضهم كي يتجمدوا</p><p style=\"text-align: right;\">! و بالطريقة هذي زادا، إتصبر كمية باهية من الفرمبواز و وقت إلي تستحق ؛ ما تاخذ كان الكمية إلي حاجتك بيها</p>', '', 'نصائح عامة', 'تعرفها أحسن طريقة باش إتصبر الفرمبواز ؟', 1, '', 0, 1, '2020-07-29 10:38:29', '2021-06-09 11:33:26', 1, 0, '', '2022-02-02 14:51:59', '140', 'shutterstock-1854491524.jpg', '', 0),
(143, 'علاش الطماطم ما تتحطش في الثلاجة ؟', '', '<p style=\"text-align: right;\">! ​ياحلاوة تنصحك باش ما تخزنش، الطماطم و إلا البنان في الثلاجة، خاطر هذا إلي يتسبب في إسودادها و يخليها تفقد نكهتها و حتي من قيمتها الغذائية </p><p style=\"text-align: right;\">.باش تحافظ عليهم مدة أطول حطهم في المطبخ و في درجة حرارة معتدلة</p><p style=\"text-align: right;\">.أما بالنسبة للبنان حاول إشريه موش طايب برشا، باش تتمكن من تخزينو لمدة أطول</p><p style=\"text-align: right;\"></span></span></p>', '', 'نصائح عامة', 'علاش الطماطم ما تتحطش في الثلاجة ؟', 1, '', 0, 1, '2020-07-29 10:44:35', '2021-07-05 08:18:37', 1, 0, '', '2022-02-02 14:51:59', '143', 'shutterstock-389943196.jpg', '', 0),
(144, 'ورقة البريك شاحت ؛ هاو الحل ', '', '<p style=\"text-align: right;\">​برشا منا يحبو البريك، و يعرفو إلي عجينة البريك فيسع ما إتشيح و تتكسر.</p><p style=\"text-align: right;\"> باش تبعد على المشكلة هذي لف ورقات البريك على قطعة قماش مبلولة شوية.</p><p style=\"text-align: right;\">و كان ورقة البريك شاحت، إدهنها بشوية زبدة ذايبة باش تطرا.</p>', '', 'نصائح عامة', 'ورقة البريك شاحت ؛ هاو الحل ', 1, '', 0, 1, '2020-07-29 10:46:15', '2021-05-12 14:18:34', 1, 0, '', '2022-02-02 14:51:59', '144', 'shutterstock-1409761574.jpg', '', 0),
(145, 'كيفاش إترجع الروح في الفجل الذابل ؟', '', '<p style=\"text-align: right;\">! تفكرت إلي عندك الفجل و إشتهيت باش تعمل بيه سلاطة ؟ أما لقيتو ذابل</p><p style=\"text-align: right;\">: عنا ليك الحل</p><p style=\"text-align: right;\">.حط الفجل في الماء البارد و الخل الأبيض (على نصف لتر ماء، كأس صغير خل)، ساعتين من الزمان، على الأقل، في الثلاجة</p><p style=\"text-align: right;\">! تتلاحض إلي أنو إتفرشك و تكبس</p><p style=\"text-align: right;\">.و توا ما عليك كان تتبن سلاطة الفجل إلي عملتها</p>', '', 'نصائح طبخ', 'كيفاش إترجع الروح في الفجل الذابل ؟', 1, '', 0, 1, '2020-07-29 10:47:40', '2021-06-04 08:57:04', 1, 0, '', '2022-02-02 14:51:59', '145', 'shutterstock-1059138953.jpg', '', 0),
(146, ' كيفاش إترجع التڨرميشة للبسكوي ؟', ' كيفاش إترجع التڨرميشة للبسكوي ؟', '<p style=\"text-align: right;\">​ساعات بعد ما إنخبيو البسكوي ننساو الحكة محلولة و إلا ما هايش إمسكرة بال<span class=\"ILfuVd\"><span class=\"hgKElc\">ڨ</span></span>دا. نلقاوه بعد، طري و الت<span class=\"ILfuVd\"><span class=\"hgKElc\">ڨ</span></span>رميشة ما فماش، خاطروا خذا الرطوبة إلي في الكوجينة !</p><p style=\"text-align: right;\">عنا ليك الحل :</p><p style=\"text-align: right;\">حط البسكوي في الميكووند 20 ثانية تقريباً (خدمة تسخين).</p><p style=\"text-align: right;\">و توة <span class=\"ILfuVd\"><span class=\"hgKElc\">ڨ</span></span>رمش البسكوي إمتاعك بالصحة و بالشفاء !</p>', '', 'نصائح عامة', 'رطوبة البسكويت عند التخزين  ', 1, '', 0, 2, '2020-07-29 10:49:08', '2020-12-31 12:14:17', 0, 0, '', '2022-02-02 14:51:59', '146', 'shutterstock-1140370187.jpg', '', 0),
(147, 'كيفاش إتصبر الأعشاب العطرية ؟', '', '<p style=\"text-align: right;\">عندك أكليل و إلا زعطر و إلا حتى حبق فاوح ! ماكش باش تستعملو و تخاف لا ما يصبرش ؟</p><p style=\"text-align: right;\">: عنا ليك الحل</p><p dir=\"rtl\">في قالب متاع مكعبات الثلج، حط الأعشاب العطرية إلي فضلولك مع شويا ماء و إلا زيت زيتونة و جمدهم في بيت الثلج.</p><p dir=\"rtl\">وقت وين تستحق، إجبد مكعب !</p>', '', 'نصائح عامة', 'كيفاش إتصبر الأعشاب العطرية ؟', 1, '', 0, 1, '2020-07-29 10:50:04', '2021-07-12 01:29:00', 1, 0, '', '2022-02-02 14:51:59', '147', 'shutterstock-1280778553.jpg', '', 0),
(149, 'بطاطا تتقرمش على قلية وحدة ؟', '', '<p dir=\"rtl\" style=\"text-align: right;\">تحب علي بطاطا مقلية تتڨرمش ومن غير ما تقليها على مرتين ؟</p><p dir=\"rtl\" style=\"text-align: right;\">أولا لازم البطاطا تكون فرشكة و مكبوسة،</p><p dir=\"rtl\" style=\"text-align: right;\"> </span>بعد ما تقص البطاطا، رش عليها شوية ملح و السر في الحكاية أنك تخليها تترتاح شوية باش ينقص ماها، <span data-cke-marker=\"1\"> </p><p dir=\"rtl\" style=\"text-align: right;\">صخن الزيت بالڨدا ثمة إقليها،</p><p dir=\"rtl\" style=\"text-align: right;\">و تتحصل على بطاطا مقلية تتڨرمش و بنينة !</p>', '', 'نصائح طبخ', 'بطاطا تتقرمش على قلية وحدة ؟', 1, '', 0, 1, '2020-07-29 10:53:51', '2021-07-01 23:56:11', 1, 0, '', '2022-02-02 14:51:59', '149', 'shutterstock-464044346.jpg', '', 0),
(152, 'كيفاش إتنقص من ريحة و مطعم البصل ؟', '', 'البصل بنين و فوائده كبيرة، لاكن برشا منا ما يحبوش ريحتو و مطعمو القوي !يا حلاوة تقدملك الحل باش إتنقص من ريحتو و مطعمو :بعد ما إتقص البصل، خليه لمدة ربع ساعة في الماء البارد، قبل الإستعمال.\r\n', '', 'نصائح طبخ', 'كيفاش إتنقص من ريحة و مطعم البصل ؟', 1, '', 0, 2, '2022-02-03 23:17:26', '2020-12-31 12:22:38', 1, 0, '', '2022-02-02 14:51:00', '152', 'shutterstock-1225841440.jpg', '', 0),
(154, '! ما إتقليش اللوز كيما إجي', '', '<p dir=\"rtl\">أول حاجة، سخن الكوشة على حرارة 180 درجة مئوية.</p><p dir=\"rtl\">حط اللوز و حلو بالكامل على فوق طبق.</p><p dir=\"rtl\">خليه في الفور لمدة 10 حتى 15 دقيقة.</p><p dir=\"rtl\">ثاني حاجة، لازمك تقلبوا بانتظام بملعقة خشبية، باش اللوز لكل إطيب كيف كيف.</p><p dir=\"rtl\">أخر حاجة، خرج اللوز من الفور و حطوا فوق طبق بارد باش يبرد.</p><p dir=\"rtl\">و مبعد ما يبرد تماماً، خزنو في حكة بلار إتسكر مليح، هذا إذا كان ماكش باش تڨرمشو في الوقت !</p>', '', 'نصائح طبخ', '! ما إتقليش اللوز كيما إجي', 1, '', 0, 1, '2020-07-29 11:03:15', '2021-08-12 19:24:56', 1, 0, '', '2022-02-02 14:51:59', '154', 'shutterstock-365381897.jpg', '', 0),
(155, 'تعرف أحسن طريقة لتنظيف الشمبنيون ؟', '', '! بارشا منا، يغسلوا الشمبنيون (الفطر) بالماء، باش إينحيو التراب اللي فيه. لاكن توضح انو الحكاية هاذي غالطة.على خاطر بعملية الغسلان، الشمبنيون يمتص الماء و يكساح و يتحول لمطاطي كيف يطيب.باش تتفادى المشكلة هذي ؛ أمسح الشمبنيون بمنديل ورقي أو منشفة نظيفة و مبلولة بالماء و إلا بالخل الأبيض! و هكا تحافض على مميزاتو و نكهتو\r\n', '', 'نصائح طبخ', 'تعرف أحسن طريقة لتنظيف الشمبنيون ؟', 1, '', 0, 1, '2023-05-01 01:58:51', '2021-07-09 00:54:55', 1, 7659, '', '2022-02-02 14:51:00', '155', 'shutterstock-1659773887.jpg', '', 0),
(156, '! باش ما إيجيش القراتان يعوم في الماء', '', '<p dir=\"rtl\">باش ينجح القراتان وما إجيش مشخش بالماء !</p><p dir=\"rtl\">لازمنا نعرفو حاجة ؛</p><p dir=\"rtl\">برشة أنواع خضرة، كما القرع بوطزينة و إلا الطماطم (إلي هي في الاصل غلة) إسيبو مائهم كي إنطيبوهم، على عكس الباذنجان والسفنارية إلي إشيحوا في الكوشة.</p><p dir=\"rtl\">باش ما إيجيش القراتان يعوم في الماء و طايب في وقتو ؛ ننصحوك، تقلي الخضرة مع شويا زيت و إلا زبدة، باش إيسيبوا مائهم، قبل ماتحطهم في الكوشة.</p><p dir=\"rtl\">و هكا  القراتان يجيك بنين و يذوب في الفم ذوبان !</p><p style=\"text-align: right;\"></span></p>', '', 'نصائح طبخ', '! باش ما إيجيش القراتان يعوم في الماء', 1, '', 0, 1, '2020-07-29 11:07:01', '2021-06-11 17:55:51', 1, 0, '', '2022-02-02 14:51:59', '156', 'shutterstock-779260216.jpg', '', 0),
(157, 'طريقة لمضاعفة الحمص في الثلاجة', 'طريقة لمضاعفة الحمص في الثلاجة', 'الحمص مالحاجات إلي نستعملوها في برشا وصفات و نحيرو كي ما يبداش عنا. باش يكون ديما عندك حاضر : ضاعف كمية الحمص مسبقا في ما يقارب 5 أضعاف حجم الماء المستعمل للنقع بعد خليه لمدة 8 ساعات و بعد اغسل الحمص بالماء وحطو في كيس في الثلاجة بطريقة مسطحة\r\n', '', 'نصائح عامة', 'حمص متوفر في الفريزر دائما', 1, '', 0, 1, '2024-05-27 15:32:30', '2021-01-27 14:35:41', 1, 0, '', '2022-02-02 14:51:00', '157', 'id-157-1636381744.jpg', '', 0),
(158, 'طريقة سهلة لتخليص الخضار من المياه الزائدة', '', ' ​باش ترتاح مالماء الزايد في بعض الخضر كيما الطماطم : حط فيها من الداخل شويا ملح وحطها مقلوبة لمدة ساعة قبل متستعملها على خاطر الملح يمص كل الماء الموجود من الداخل\r\n', '', 'نصائح عامة', 'طريقة سهلة لتخليص الخضار من المياه الزائدة', 1, '', 0, 1, '2024-05-27 15:32:37', '2021-10-26 22:24:38', 1, 0, '', '2022-02-02 14:51:00', '158', 'id-158-1280373001.jpg', '', 0),
(159, 'حل مشكلة قساوة العدس', '', '.​ساعات حتى بعد منطيبو العدس، يقعد يابس هاكا علاه ننصحوك باش تزيد شويا بيكاربونات الصوديوم للماء اللي يطيب فيه\r\n', '', 'نصائح عامة', 'حل مشكلة قساوة العدس', 1, '', 0, 1, '2024-05-27 15:32:44', '2021-10-13 08:20:11', 1, 0, '', '2022-02-02 14:51:00', '159', 'id-159-474838984.jpg', '', 0),
(160, 'تحضير فوشار أكثر من مثالي', 'لتحضير قطانية بنينة وخفيف', '​خلي حبات القطانيا في الماء لمدة 10 دقائق. بعد صفيهم، وحضر القطانية تاعك كالعادة. الطريقة هاذي تخلي القطانيا بنينة وتطيب الكل وميقعدوش هاك الكعبات ماللوطة مهومش طايبين\r\n', '', 'نصائح عامة', 'تحضير فوشار أكثر من مثالي', 1, '', 0, 2, '2024-05-27 15:32:50', '2021-01-27 14:32:32', 0, 0, '', '2022-02-02 14:51:00', '160', 'id-160-527653111.jpg', '', 0),
(161, 'أسرار المطاعم للحصول على ثلج شفاف', 'سر مكعبات الثلج الشفافة', 'حاجتك بمكعبات ثلج كيما في المطاعم شفافة ومزيانة، عنا ليك الحل : غلي الماء، خليه يبرد بالقدا، صبو في القوالب الخاصة بيه وحطو في الثلاجة. بالطريقة هاذي تلاحظ إلي مكعبات الثلج شفافة وتلمع كيما إلي تلقاهم في أضخم المطاعم\r\n', '', 'نصائح عامة', 'أسرار المطاعم للحصول على ثلج شفاف', 1, '', 0, 2, '2024-05-27 15:32:54', '2020-12-31 13:35:41', 0, 0, '', '2022-02-02 14:51:00', '161', 'shutterstock-609986504.jpg', '', 0),
(162, 'القلي الناجح و درجة حرارة الزيت', 'سر لمعرفة الحرارة المناسبة لزيت القلي', '​ياحلاوة تنصحك باش تتأكد من درجة حرارة زيت القلي، باش الدجاج و إلا الحوت أو أي حاجة تحب تقليها متشربش الزيت. كيف تحس إلي الزيت حضر، تنجم طيش كعبة قطانيا في وسطو كان تفرقعت راو سخون وحاضر للقلي\r\n', '', 'نصائح عامة', 'القلي الناجح و درجة حرارة الزيت', 1, '', 0, 2, '2024-05-27 15:32:59', '2020-12-31 13:40:53', 0, 0, '', '2022-02-02 14:51:00', '162', 'shutterstock-305660375.jpg', '', 0),
(163, 'انزلاق لوح التقطيع', 'حل لتثبيت لوح التقطيع', '​اللوحة اللي نقطعو عليها ساعات تزلق منا، الشيء إلي يسببلنا شويا قلق. نقترحو عليك، أنك تحط قطعة قماش مبلولة تحت اللوحة باش تثبتها وتنجم تستعملها و أنت مرتاح\r\n', '', 'نصائح عامة', 'انزلاق لوح التقطيع', 1, '', 0, 2, '2024-05-27 15:33:04', '2020-10-08 11:46:18', 0, 0, '', '2022-02-02 14:51:00', '163', 'shutterstock-426323161.jpg', '', 0),
(164, 'سر التطييب بالبخار', 'سر التطييب بالبخار', '​الماكلة الطايبة على البخار عندها نكهة خاصة، خاطر الخضرة تبقا محافظة على قيمتها الغذائية والفيتامينات والمعادن إلي فيها. أهم حاجة يلزمك تعرفها هي لازم توفر البخار بكمية باهية، باش تنجم تطيب ماكلتك على قاعدة. أكثر طريقة مشهورة ومضمونة لاكلة بنينة ومميزة، هي إستعمال الكسكاس. الكسكاس تحتو قدر فيه ماء يغلي، وهكا إنجمو انطيبو أي ماكلة عليه \r\n', '', 'نصائح عامة', 'الطهي بالبخار وأنواعه', 1, '', 0, 1, '2024-05-27 15:33:15', '2021-01-27 15:17:44', 1, 0, '', '2022-02-02 14:51:00', '164', 'id-164-1481146889.jpg', '', 0),
(165, 'سر تحضيرالشابلير دياري', '', ' ​كان عندك برشا خبز يابس وقديم، معادش تلوحو وخليه عندك استنفع بيه. حط الخبز اليابس في الخلاط الكهربائي وارحيه لين يولي أرطب هكا تتحصل على شابلير دياري. حطو في وعاء بلار، سكرو بالقدا واحفظو في الثلاجة و تنجم تستعملو في أي وقت\r\n', '', 'نصائح عامة', 'سر تحضيرالشابلير دياري', 1, '', 0, 1, '2024-05-27 15:33:18', '2021-10-25 08:37:49', 1, 0, '', '2022-02-02 14:51:00', '165', 'id-165-606918428.jpg', '', 0),
(166, 'خبز جديد من خبز يابس', 'خبز جديد من خبز يابس', '​كان تتصور إلي الخبز كيف ييبس مستحيل يرجع جديد وصالح للاكل، يكون اعتقادك غالط. غطس الخبز في الماء فيسع وخرجوا، بعد دخلو للميكرووند لمدة 5 دقايق. باش تلاحظ إلي الخبز رجع جديد\r\n', '', 'نصائح عامة', 'خبز جديد من خبز يابس', 1, '', 0, 1, '2024-05-27 15:33:27', '2021-10-25 08:38:08', 1, 0, '', '2022-02-02 14:51:00', '166', 'id-166-175128500.jpg', '', 0),
(167, 'سر تحضير رغوة الحليب', 'سر تحضير رغوة الحليب', 'شكون فينا ميحبش رغوة الحليب كيما في القهاوي البرا، خاصة في الكابوتشينو. ياحلاوة تعطيك السر : سخن الحليب شويا، بعد حطو في وعاء بلوري وسكرو بالقدا، ثم خضو بقوة في الإتجاهات الكل لين تتحصل على الموس صبها فوق القهوة وهكا تتحصل على كابوتشينو بنينة\r\n', '', 'نصائح عامة', 'سر تحضير رغوة الحليب', 1, '', 0, 1, '2024-05-27 15:34:33', '2021-10-20 08:51:33', 1, 0, '', '2022-02-02 14:51:00', '167', 'id-167-349842839.jpg', '', 0),
(168, 'اسهل طريقة باش ترابي الجبن', '', '.ادهن المبشرة بشوية زيت قبل متستعملها باش ميلصقش الجبن فيها وتسهال عليك العملية\r\n', '', 'نصائح عامة', 'اسهل طريقة باش ترابي الجبن', 1, '', 0, 1, '2024-05-27 15:34:41', '2021-10-25 08:38:19', 1, 0, '', '2022-02-02 14:51:00', '168', 'shutterstock-1077447071.jpg', '', 0),
(169, 'الحل لتجنب التصاق الحليب في القدر', '', 'كيف نسخنو الحليب نلاحضو طبقة بيضة ماللوطة لاصقة : عدي القدر في الماء البارد قبل متستعملو، وخلي فيه شوية ماء الداخل وصب الحليب. وهكا تلاحظو غياب الطبقة البيضاء في قاع القدر\r\n', '', 'نصائح طبخ', 'الحل لتجنب التصاق الحليب في القدر', 1, '', 0, 2, '2024-05-27 15:34:46', '2021-01-27 14:59:12', 0, 0, '', '2022-02-02 14:51:00', '169', 'id-169-1267507804.jpg', '', 0),
(171, 'كيفية تحضير الشوفان؟', 'طرق باش تحضر بيها الشوفان؟', ' :​برشا طرق لتحضير الشوفان لفطور الصباح، ياحلاوة تقدملك طريقتين باش تجرب بيهم الشوفانخلط تقريب ال100صل مالماء و إلا الحليب كيف ما تحب مع 4 مغارف مالشوفان، وطيبهم بالشويا على نار هادية وتبع النصايح الموجودة في باكو الشوفانكان أنت متحبش عصيدة الشوفان سخونة ومطبوخة، عنا ليك الحل، خلط الشوفان مع السائل الي تحبو وزيدها أي مكون أخر كيما الفاكية مثلا، غطي الصحن توا بالقدا بورق شفاف وخليه في الثلاجة لمدة ليلة كاملة وفي الصباح باش تتحصل على عصيدة باردة بنينة مغذية كيما تحب\r\n', '', 'نصائح عامة', 'كيفية تحضير الشوفان؟', 1, '', 0, 2, '2024-05-27 15:34:53', '2021-01-27 14:47:44', 0, 0, '', '2022-02-02 14:51:00', '171', 'id-171-653741836.jpg', '', 0),
(172, 'لا لتسخين البيتزا في الميكريف بعد اليوم', 'رجع بنة البيتزا حتى بعد متبرد', 'البيتزا بنتها وقت تكون سخونة ومقرمشة. ساعات كيف نشريو البيتزا مناكلوهاش بالوقت، تبرد وتفقد البنة متاعها ​برشا يغلطو ويسخنوها في الميكرووند. لكن موش هذا الحل باش تبقا محافظة على نفس البنة والخفة، سخنها في المقلاة وكولها كاينك شريتها توا \r\n', '', 'نصائح عامة', 'لا لتسخين البيتزا في الميكريف بعد اليوم', 1, '', 0, 2, '2024-05-27 15:35:00', '2021-01-27 14:49:53', 0, 0, '', '2022-02-02 14:51:00', '172', 'id-171-423080818.jpg', '', 0),
(175, 'الحل لكثرة الزيت في الماكلة', '', '​وقت تبدا تطيب، ساعات تلاحظ تجمع الزيت على سطح ماكلتك، الشيء إلي يخليك تخاف تطلع الماكلة مزيتة عنا ليك الحل : لف قطعة مالثلج في شوية قماش نظيف وعديها على السطح تاع الماكلة، باش تشوف إلي الزيت يلصق في القماش ويتنحى بطريقة سحرية\r\n', '', 'نصائح طبخ', 'الحل لكثرة الزيت في الماكلة', 1, '', 0, 1, '2024-05-27 15:35:06', '2021-10-25 08:37:36', 1, 3518, '', '2022-02-02 14:51:00', '175', '175.jpeg', '', 0),
(176, 'هكذا تتأكدين من حرارة المقلاة قبل استخدامها', 'تاكد من سخانة الزيت قبل متقلي', '​برشا حاجات كيف نطيبوها يفسد منظرها وتلصق في المقلاة على خاطرها مش سخونة بالقدا. ياحلاوة تنصحك ترش شويا ماء على المقلاة ، كان عمل حس وطشطش شويا أعرف راهو المقلاة حاضرة للإستعمال\r\n', '', 'نصائح طبخ', 'هكذا تتأكدين من حرارة المقلاة قبل استخدامها', 1, '', 0, 2, '2024-05-27 15:35:19', '2021-01-27 15:15:50', 0, 5254, '', '2022-02-02 14:51:00', '176', 'id-176-279328526.jpg', '', 0),
(177, 'الخطوات الصحيحة للحصول مايونيز لذيذ وناجح', 'مايونيز بنين خفيف', 'باش يكون المايونيز متاعك بنين وخفيف، إستعمل الزيت العادي، زيت الحاكم. حط الزيت فوق أصفر العضم المخلط من قبل بالقدا، وخلط بشويا بشويا. أهم نصيحة تقدمهالك ياحلاوة هي السر في التحريك بالقوي وبزربة وقت تزيد الزيت، الشيء إلي يخلي الصوص معقدة وكثيفة\r\n', '', 'نصائح طبخ', 'الخطوات الصحيحة للحصول مايونيز لذيذ وناجح', 1, '', 0, 2, '2024-05-27 15:35:29', '2020-12-31 13:38:35', 0, 5244, '', '2022-02-02 14:51:00', '177', 'shutterstock-536451577.jpg', '', 0),
(178, 'صوص الكراميل المثالي في الطعم و القوام و اللون', 'كراميل مميز', ' ​باش تتحصل على كراميل مميز في اللون والطعم والقوام، يا حلاوة تعطيك السر : خلط قطرات من عصير القارص وقت التطييب، الحموضية متاع الليم متخليش السكر يتجمد ويتعقد، وباش الكراميل يكون سايل شوية\r\n', '', 'نصائح حلويات', 'صوص الكراميل المثالي في الطعم و القوام و اللون', 1, '', 0, 2, '2024-05-27 15:35:40', '2020-12-31 13:36:25', 0, 4525, '', '2022-02-02 14:51:00', '178', 'shutterstock-737788315.jpg', '', 0),
(179, 'أسرار صلصة البشاميل الناجحة', 'سر الباشاميل البنينة', 'أحسن حاجة تخلي الصوص باشاميل بنينة وخفيفة هي القوام الكريمي متاعها والكثافة متاعها إلي تجي مالفارينة والحليب. ياحلاوة  تعطيك سر الباشاميل الناجح والبنين : أول حاجة خرج الحليب قبل بنص ساعة من تسخينه أو إستعماله، الشيء إلي يمنع تكتل الصوص. باش تخفف الباشاميل، زيد شويا حليب و إلا كريمة وباش تجيك الصوص باشاميل أخف و أبن، بدل الفارينة العادية بفارينة الروز \r\n', '', 'نصائح طبخ', 'أسرار صلصة البشاميل الناجحة', 1, '', 0, 2, '2024-05-27 15:36:12', '2021-01-27 14:54:01', 0, 4424, '', '2022-02-02 14:51:00', '179', 'id-179-255028519.jpg', '', 0),
(180, 'كيفية تكثيف الصلصة', 'كيفاه تكثف الصوص', '​تحب على صوص معقدة وكثيفة، زيد شوية نشاء و إلا فارينة للصوص. وباش ميتكتلش، ذوبو في شوية ماء وحدو وبعد صبو مع الصوص، و خلي الصوص متاعك تطيب على نار هادية \r\n', '', 'نصائح طبخ', 'كيفية تكثيف الصلصة', 1, '', 0, 2, '2024-05-27 15:36:24', '2021-01-27 15:09:37', 0, 6545, '', '2022-02-02 14:51:00', '180', 'id-180-1280046664.jpg', '', 0),
(181, 'السر وراء كلامار طري ويذوب في الفم', 'السر وراء كلامار طري ويذوب في الفم', '​مالمعروف انو الكلامار فيسع يطيب أما ينجم يولي يابس بعد بشوية وقت. باش الكلامار متاعك يكون طري حطو في شوية ماء لمدة 30 دقيقة أو أكثر حتى لساعتين، مع شوية كيوي و إلا أفوكادو. الحموضة إلي في الفاكية تخليه طري وبنين يذوب في الفم\r\n', '', 'نصائح طبخ', 'السر وراء كلامار طري ويذوب في الفم', 1, '', 0, 2, '2024-05-27 15:36:38', '2020-12-31 13:37:16', 0, 5454, '', '2022-02-02 14:51:00', '181', 'shutterstock-257845339.jpg', '', 0),
(182, 'سمك مقلي وذهبي اللون', 'حوت مقلي بطريقة مزيانة', '.​حط الحوت في الحليب وبعد غطسو في الفارينة، وهكا تتحصل على قوام مقرمش ولون ذهبي للحوت عند القلي\r\n', '', 'نصائح طبخ', 'سمك مقلي وذهبي اللون', 1, '', 0, 2, '2024-05-27 15:36:47', '2021-01-27 14:51:06', 0, 5752, '', '2022-02-02 14:51:00', '182', 'id-182-437237980.jpg', '', 0),
(183, 'عملية التنقيع تساعد على جعل أطباقك أشهى', 'خليط للدجاج والحوت.', 'تعني عملية ” التنقيع ” تحضير خليط من الزبادي الطبيعي، بهارات التوابل كالتندورين، عصير الليمون، الكزبرة الخضراء، مع اضافة الملح والفلفل. من ثم يتم دهن صدور الدجاج أو السمك بهذا الخليط ويتم وضعه ليرتاح في الثلاجة لعدة ساعات او حتى لليلة كاملة، وتساعد هذه الطريقة كثيرا على تركيز النكهات ليصبح الطبق ألذ وأعمق. ويبقى فقط بأن تقومي بطهي الدجاج أو السمك المنقوع لعدة ساعات لتستمتعي بنكهات جديدة وشهيةحط في وعاء، زبادي طبيعي وتوابل وعصير ليمون ومعدنوس اخضر وملح وشويا فلفل اكحل.غطس الحوت والا صدور الدجاج في الخليط هاذا، وخليه يرتاح في الثلاجةلعدة ساعاتاو حتى ليلة كاملة. وهكا تتحصل على نكهة بنينة ومركزة\r\n', '', 'نصائح طبخ', 'عملية التنقيع تساعد على جعل أطباقك أشهى', 1, '', 0, 2, '2024-05-27 15:36:55', '2021-01-27 15:07:51', 0, 4545, '', '2022-02-02 14:51:00', '183', 'id-183-b-154382999.jpg', '', 0),
(184, 'مشكلة الروز الملتصق', 'مشكلة الروز الملتصق', 'برشا منا تواجهنا مشكلة تعجن و إلا إلتصاق الروز، إلي يفسد النكهة والمنظر تاع الماكلة. عدي الروز على الماء البارد باش توقف عملية الطهي، بعدها سخن الروز مرة أخرى في مقلاة على نار هادية مع تحريكه. بالطريقة هاذي تقعد كل كعبة روز وحدها، و تنقص من الإلتصاق\r\n', '', '1', 'مشكلة الأرز الملتصق', 1, '', 0, 1, '2023-05-19 07:56:09', '2020-10-09 11:15:18', 1, 7665, '', '2022-02-02 14:51:00', '184', 'image3.png', '', 0),
(185, 'لا ترمي مياه سلق المعكرونة بعد اليوم', 'استحفظ بماء الي سمط فيه المقرونه', 'ياحلاوة تعطيك سر باش تخلي صوص المقرونة متاعك بنينة : هو الماء اللي سمط فيه المقرونة، بحكم انو فيه برشا نشاء، إلي من دورو يعطي كثافة للصوص\r\n', '', 'نصائح عامة', 'لا ترمي مياه سلق المعكرونة بعد اليوم', 1, '', 0, 2, '2024-05-27 15:39:17', '2021-01-27 14:52:01', 0, 0, '', '2022-02-02 14:51:00', '185', 'id-185-271851755.jpg', '', 0),
(186, 'للحصول على الريزوتو الايطالي الكريمي', 'السر في ريزوتو ايطالي كريمي', '​موش لازم تزيد كريمة للريزوتو، بحكم انو الروز ديجا فيه نشاء إلي يخليه دسم وعندو قوام كريمي. لكن زيد شوية بارميجان و زبدة على الريزوتو بعد مايطيب، لتركيز النكهة\r\n', '', 'نصائح طبخ', 'للحصول على الريزوتو الايطالي الكريمي', 1, '', 0, 2, '2024-05-27 15:39:29', '2021-01-27 16:39:34', 0, 0, '', '2022-02-02 14:51:00', '186', 'id-186-493083328.jpg', '', 0),
(187, 'وداعا لالتصاق المقرونة بعد صمتها', '', 'أصمط المقرونة في كمية مناسبة متاع ماء، حرك المقرونة وهي تطيب بمغرفة لوح. إذا تحب تستعملها ديراكت، حط فوقها الصوص وقدمها. أما إذا تحب تقدمها بعد بوقت، أغسلها بشويا ماء. و زيد المقرونة شوية زبدة باش تمنع إلتصاقها\r\n', '', 'نصائح طبخ', 'وداعا لالتصاق المقرونة بعد صمتها ', 1, '', 0, 2, '2024-05-27 15:39:40', '2021-01-27 15:37:06', 0, 4411, '', '2022-02-02 14:51:00', '187', 'id-187-460953895.jpg', '', 0),
(188, 'سر اللحم المشوي الطري', 'سر لحم مشوي بنين', '.​بعد ما تشوي اللحم، خليه يرتاح شوي دقايق في طبق مغطي بورق الليمنيوم، باش يولي اللحم أكثر طراوة و بنة\r\n', '', 'نصائح طبخ', 'سر اللحم المشوي الطري', 1, '', 0, 2, '2024-05-27 15:39:48', '2021-01-27 15:13:01', 0, 5877, '', '2022-02-02 14:51:00', '188', 'id-204-1896787129.jpg', '', 0),
(189, 'كيفاش تحمي لون اللحم من التأكسد ؟ ', '', '​بعد نقع اللحم في التوابل والصلصة، فما إحتمال اللون متاعو يتبدل. الحل للمشكلة هاذي، غطس اللحم و إدهنو بزيت الزيتونة برشا مرات لين يكون طبقة عازلة تحمي اللون متاعو\r\n', '', 'نصائح طبخ', 'حماية لون اللحم من التأكسد عند نقعه', 1, '', 0, 2, '2024-05-27 15:39:59', '2020-12-31 13:43:07', 1, 7574, '', '2022-02-02 14:51:00', '189', 'shutterstock-669792586.jpg', '', 0),
(190, 'لا لفوضى الدجاج البانيه بعد اليوم', 'دجاج باني لذيذ ومن غير فوضى', 'شكون فينا مايحبش السكالوب الباني، أما تعبوا الحوسة متاعوا. عنا ليك الحل : حط في ساشي الثلاجة، فارينة مع ملح وشوية فلفل أكحل وتوابل وزيد الدجاج، وسكر الساشي وخض بالقدا لين تتماسك المكونات الكل مع بعضها. و هكا تتحصل على دجاج حاضر للطهي من دون فوضى\r\n', '', 'نصائح طبخ', 'لا لفوضى الدجاج البانيه بعد اليوم', 1, '', 0, 2, '2024-05-27 15:40:10', '2021-01-27 15:02:40', 0, 7857, '', '2022-02-02 14:51:00', '190', 'id-190-653277361.jpg', '', 0),
(192, 'مكعبات مرق منزلية الصنع ', 'مكعبات مرق دجاج مصنوعة في البيت', '​بعد إكتشاف أن مكعبات مرق الدجاج قد تحتوي على مكونات مضرة، إخترنا لك حل باش تصنع هالمكعبات تاع الدجاج في الدار. جمد شوية مرق دجاج في قوالب مكعبات الثلج، وهكا يمكن إستخدامها في أي وقت تحب\r\n', '', 'نصائح طبخ', 'مكعبات مرق منزلية الصنع ', 1, '', 0, 2, '2024-05-27 15:40:19', '2021-01-27 15:34:17', 0, 7457, '', '2022-02-02 14:51:00', '192', 'id-192-1767720626.jpg', '', 0),
(193, 'الحل السحري لتخفيف الملح أثناء الطبخ', 'الماكلة مالحة، عنا ليك الحل', '​برشا ميعرفوش يعدلو الملح متاعهم في الماكلة، ياحلاوة ترتحك مالمشكلة هاذي وتعطيك الحل للملح الزايد. غطس كعبة بطاطا في القدر بعد متغسلها وتقصها طروف كبيرة. البطاطا تمص الملح الزايد من غير ما تأثر على الماكلة وطعمها، و في اللخر نحي طروف البطاطا، وحط ماكلتك عادي\r\n', '', 'نصائح طبخ', 'الحل السحري لتخفيف الملح أثناء الطبخ', 1, '', 0, 2, '2024-05-27 15:40:32', '2021-01-27 15:08:50', 0, 7547, '', '2022-02-02 14:51:00', '193', 'id-199-1358076956.jpg', '', 0),
(194, 'السر وراء الخفق الناجح للزبدة مع السكر', 'سر لخلط الزبدة والسكر', '​كيف باش نصنعو الحلويات، أكيد باش نستحقو الزبدة والسكر و إلي مالمعروف لازمهم يتخلطو بالقدا باش الماكلة متاعنا تطلع بنينة وخفيفة. أهم حاجة لازمنا نعرفوها، هي أننا لازمنا نخلطو هالخليط متاع السكر والزبدة لين يولي اللون متاعو أصفر وسايل شوية وعندو قوام كريمي. وهكا تدخل كمية كبيرة مالهواء للخليط إلي هي مسؤولة على الخفة والطراوة في الفور\r\n', '', 'نصائح حلويات', 'السر وراء الخفق الناجح للزبدة مع السكر', 1, '', 0, 2, '2024-05-27 15:40:53', '2020-10-09 11:36:15', 0, 4714, '', '2022-02-02 14:51:00', '194', '2.jpg', '', 0),
(195, 'السكر المناسب لكل وصفة', '', '  : برشا ما يعرفو كان السكر الأبيض والسكر البني. مع ياحلاوة أعرف كل أنواع السكر و سر كل واحد فيهم في التصنيفالسكر البني : تكون الحلاوة متاعو واضحة أكثر من الأنواع الأخرين متاع السكر، لازمنا نتحكمو في الكمية متاعو في الحلويات على خاطر ممكن تتحول الحلاوة لمرارة.كيما ينجم يأثر على شكل ولون الحلويات مالبرا، هاكا علاه لازمنا نخلطوه بشويا سكر أبيض في العادا باش ميأثرش عالماكلة متاعنا و إلا عالشكل متاعهاقصب السكر : عكس السكر الأبيض، قصب السكر فيه كمية صغيرة مالعسل الأكحل والفانيليا، الشيء إلي يخليه خفيف وعندو طعم خاص. السكر هاذا يخلي الكريب مقرمشة وبنينة، كيف ترش شويا مالبراالسكر المحور : نتحصلو عليه بعد لا نرحيو السكر الأبيض، الشيء إلي يخليه فيسع يذوب هاكا علاه نستعملوه في تغليف أي نوع متاع حلويات، ونحضرو بيه الميرانغ زاداالسكر الأبيض : ملك السكر، هو أكثر نوع مكرر من أنواع السكر الكل. نتحصلو عليه ماللفت السكري، ونستعملوه في كل الحلويات \r\n', '', 'نصائح حلويات', 'السكر المناسب لكل وصفة', 1, '', 0, 2, '2024-05-27 15:41:05', '2021-01-27 16:40:34', 1, 5457, '', '2022-02-02 14:51:00', '195', 'id-195-1170654637.jpg', '', 0),
(196, 'حل ذكي لتطرية السكر البني المتكتل والمزعج', '', '​تحب على كوكيز وإلا أي حلويات بنينة وخفيفة، يكون الخليط متاعها أرطب و مفيهش أي تكتلات. ياحلاوة تعاونك وتعطيك الحل : حط السكر البني المستعمل في الميكرووند مع شوية ماء لمدة 10 ثواني، وهكا تتحصل على سكر سوبل حاضر للإستعمال\r\n', '', 'نصائح حلويات', 'حل ذكي لتطرية السكر البني المتكتل والمزعج', 1, '', 0, 1, '2024-05-27 15:41:14', '2021-01-27 14:40:58', 1, 2310, '', '2022-02-02 14:51:00', '196', 'id-196-1365809549.jpg', '', 0),
(197, 'سكر فانيليا دياري', 'سكر فانيليا دياري', '​سكر فانيليا مالمكونات الأساسية لأي وصفة و إلا أي ماكلة حلوة، عندو نكهة وطعم خاص وخفيف. وعلى خاطر ديما الحاجة الدياري أبن و أضمن، ياحلاوة تعطيك طريقة وسر باش تصنع بيه سكر فانيليا في الدار. حط شويا أعواد متاع فانيليا في حكة فيها السكر المحور وسكرها بالقدا. بعد شويا أشهر، يكون عندك سكر فانيليا بنين وخفيف وريحتو فاوحة. و إذا حسيت الريحة تنحات مالأعواد، حط أعواد جدد وخليك على نفس الطريقة\r\n', '', 'نصائح حلويات', 'سكر فانيليا منزلي', 1, '', 0, 2, '2020-07-29 12:36:19', '2021-01-27 14:37:33', 1, 0, '', '2022-02-02 14:51:59', '197', 'id-197-255325807.jpg', '', 0),
(198, 'سر باش العسل ميتجمدش', 'سر باش العسل ميتجمدش', '​العسل كيف يقعد مدة، ينجم يتثلج و إلا يتجمد وهاذي مالمشاكل إلي متخليناش نستعملو العسل في برشة وصفات لو كان موش الكل. هاكا علاه ياحلاوة خممت فيك، وتعطيك الحل : ​متسخنش العسل في الميكرووند، خاطر يتبدل لونو ونكهتو، لكن حط حكة العسل في قدر فيه ماء سخون مدة دقايق معدودة، وبعد خرجو، تو تشوف العسل كفاه رجع سايل وحاضر للإستعمال\r\n', '', 'نصائح عامة', 'كيفية منع تبلور وتجمد العسل', 1, '', 0, 1, '2020-07-29 12:37:51', '2021-01-27 14:31:56', 1, 0, '', '2022-02-02 14:51:59', '198', 'id-198-764193367.jpg', '', 0),
(199, 'حل لندوة الملح', 'حل لندوة الملح', '​الملح كيف يدخلو الهواء، يندي شوية ويولي ماهوش صالح للإستعمال في بعض الحالات. باش تتجنب الندوة في الملح، حط معاه شوية روز إلي يعمل على إمتصاص الندوة ويخليه جديد. الطريقة هاذي تنجم تستعملها زادة في الملاحة، بحكم النقب الي فيها أصغر من كعبة الروز وهكا كيف نستعملوها يهبط الملح والروز لا\r\n', '', 'نصائح طبخ', 'مشكلة رطوبة ملح الطعام', 1, '', 0, 1, '2022-02-02 15:07:34', '2021-01-27 15:38:25', 1, 0, '', '2022-02-02 14:51:00', '199', 'id-193-1042462132.jpg', '', 0),
(200, 'اقلي الباذنجان باقل زيت', '', 'تحب تقلي الباذنجان و إلا القرع متاعك من غير متستهلك برشا زيت، ومنغير ميشربو برشا زيت ؟ ياحلاوة عندها الحل : غطس الباذنجان و إلا القرع الأخضر الي باش تقليه في العضم المخفوق قبل، و اقليه \r\n', '', 'نصائح طبخ', 'اقلي الباذنجان باقل زيت', 1, '', 0, 2, '2024-05-27 15:41:22', '2021-01-27 14:56:28', 0, 4524, '', '2022-02-02 14:51:00', '200', 'id-200-1133342444.jpg', '', 0),
(201, 'حفظ الخبز', '', 'كيف تحب تحافظ عالخبز متاعك، متحطوش في الثلاجة خاطر يولي كاسح وموش باهي. حط الخبز متاعك في كيس بلاستيك، ودخلو للثلاجة. وكيف تخرجو وتحب تستعملو، سخنو شوية في الميكروونك وكولو كاينو جديد\r\n', '', 'نصائح حفظ', 'حفظ الخبز', 1, '', 0, 2, '2024-05-27 15:41:36', '2021-01-27 15:10:48', 0, 5872, '', '2022-02-02 14:51:00', '201', 'id-201-388382818.jpg', '', 0),
(203, 'حل للماكلة باش متمصش الزيت', '', 'تحب تقلي الفلفل، الطماطم، البطاطا و إلا أي حاجة أخرى، ومغير ما يمص الزيد ويشد زيت القلى، زيد شوية خل للزيت إلي باش تقلي بيه. وهكا إرتاح نهائيا من الماكلة المقلية إلي تشرب برشا زيت\r\n', '', 'نصائح طبخ', 'حل للماكلة باش متمصش الزيت', 1, '', 0, 1, '2022-05-20 10:58:35', '2020-10-09 12:02:22', 1, 1231, '', '2022-02-02 14:51:00', '203', '203.jpeg', '', 0),
(205, 'السلاطة باش متذبلش', '', 'بعد متقطع السلاطة متاعك، تجنب تحط عليها ديراكت الملح والخل والزيت والقارص. خليها ترتاح وكيف تجي باش تحطها عالطاولة وسايي باش تاكلها فوحها، وهكا باش متذبلش وتبقا محافظة عالماء متاعها\r\n', '', 'نصائح حفظ', 'السلاطة باش متذبلش', 1, '', 0, 2, '2024-05-27 15:41:45', '2021-01-27 15:22:08', 0, 7477, '', '2022-02-02 14:51:00', '205', 'id-205-1787812715.jpg', '', 0),
(206, 'سر لبياض الارز', '', 'تحب على روز أبيض برشا  ؟ ​زيدو شوية عصير قارص في الماء قبل ميغلي. وهكا يكون عندك روز أبيض بياض الثلج بعد ما يطيب\r\n', '', 'نصائح طبخ', 'سر لبياض الارز', 1, '', 0, 1, '2020-07-29 12:48:51', '2021-01-27 14:36:39', 1, 0, '', '2022-02-02 14:51:59', '206', 'id-206-1315935692.jpg', '', 0),
(210, 'Abaisser  شنيا معناه', '', 'Abaisser  شنيا معناها العملية هاذي، معروفة في عالم الطبخ. ياحلاوة  تفسرلك المفهوم : حلان العجينة بالقلقال بعد متحط تحتها شويا فارينة\r\n', '', 'نصائح عامة', 'Abaisser  شنيا معناها', 1, '', 0, 2, '2024-05-27 15:42:03', '2021-01-27 14:46:12', 0, 7355, '', '2022-02-02 14:51:00', '210', 'id-210-300828887.jpg', '', 0),
(229, 'ماهي طريقة التقطيع جوليان ؟', '', 'التسمية فرنسية الأصل، تعني قص الخضرة و إلا الغلة لقطع طويلة ورقيقة، الشيء إلي يخليها تطيب فيسع. اغسل الخضرة و إلا الغلة بالقدا، قصها بالعرض إلى شرائح سمكها 1/16 الى 1/8 حسب الرغبة. الخضرة والغلة إلي تنجم تقصها بالطريقة هاذي نلقاو، السفنارية، الفقوس، الفلفل، الاجاص والطماطم\r\n', '', 'نصائح طبخ', 'ماهي طريقة التقطيع جوليان ؟', 1, '', 0, 2, '2024-05-27 15:42:12', '2021-01-27 14:38:41', 0, 7857, '', '2022-02-02 14:51:00', '229', 'id-229-1157207239.jpg', '', 0),
(232, 'فكرة باش إتعدي أقل وقت في الكوجينة', 'فكرة باش إتعدي أقل وقت في الكوجينة', '<p style=\"text-align: right;\">الكوجينة تاخذينا بارش وقت</p><p style=\"text-align: right;\">فما برش فازت إنجمو نعملوهم يربحونا الوقت و إنقصوا علينا التعب</p><p style=\"text-align: right;\">مثلاً كي إنطيبوا صالصة بولونيز و إلا أي صالصة متاع مقرونة ؛ من المستحسن إنطيبوا كمية كبيرة، ناكلو منها وقتها و إنحطوا بقية الصالصة في بيت الثلج، مقسمين على أجزاء</p><p style=\"text-align: right;\">وقت إلي نبداو، مزروبين و ماعناش برشة وقت، ما عليك كان تذوب جزء من الصالصة و صمتان المقرونة آكهو</p><p style=\"text-align: right;\">الحكاية الكل ما تفوتش 15 دقيقة و بالطبيعة، بأقل وقفة في الكوجينة</p>', '', 'نصائح عامة', 'فكرة باش إتعدي أقل وقفة في الكوجينة', 1, '', 0, 1, '2020-12-24 11:54:27', '2021-05-12 14:19:35', 1, 0, '', '2022-02-02 14:51:59', '232', 'shutterstock-1670682688.jpg', '', 0),
(233, 'شنية الحاجات الأخرى، إلي ترويك ؟', '', '<p dir=\"rtl\">الكلنا نعرفوا أنو الماء، حاجة مهمة جداً للحفاظ على صحة جيدة !  و خاصةً في الصيام !</p><p dir=\"rtl\">و الحاجة إلي ساعات ننساوها، أنو كي ناكلوا كمية باهية من الغلة و الخضرة ؛ يعاونونا زادا، باش يبقى بدنا راوي بالماء !</p><p dir=\"rtl\">كيما الطماطم و الفقوس و الفراولة و السلاطة ... إلي فيهم نسبة كبيرة إمتاع ماء، تتراوح بين 80% و 90% !</p><p dir=\"rtl\">بخلاف أنهم غنيين بألياف إلي باهين برشة لعملية الهضم.</p><p dir=\"rtl\">مع هذا الكل ؛ ما ننساوش شربان الماء !</p>', '', 'نصائح عامة', 'شنية الحاجات الأخرى، إلي ترويك ؟', 1, '', 0, 1, '2020-12-24 12:10:57', '2021-05-12 14:20:11', 1, 0, '', '2022-02-02 14:51:59', '233', 'shutterstock-496465060.jpg', '', 0),
(235, ' السر في ماكلة السحور ؟', '', '<p mce_style=\"text-align: right;\" style=\"text-align: right;\">في السحور، ركز على الماكلة إلي تهضم بعد سوايع ؛  كيما السردينة و اللوز و  و الزوز و البيض</p><p dir=\"rtl\" mce_style=\"text-align: right;\" style=\"text-align: right;\">وعلى الماكلة إلي فيها \"السكر البطيء\" مثل : الكسكسي و إلا الخبز بالقمح الكامل، العدس ...</p><p dir=\"rtl\" mce_style=\"text-align: right;\" style=\"text-align: right;\">إلي يبعدوا عليك الاحساس بالجوع لمدة طويلة !</p><p dir=\"rtl\">و هذه عادة، مذابينا، نحافاظو عليها طول شهر رمضان المبارك، باش نضمنو توزيع باهي و متوازن للطاقة.</p><p dir=\"rtl\"> </p>', '', 'نصائح عامة', ' السر في ماكلة السحور ؟', 1, '', 0, 1, '2020-12-24 12:23:29', '2021-05-12 14:20:32', 1, 0, '', '2022-02-02 14:51:59', '235', 'id-235-1861932964.jpg', '', 0),
(237, ' كيفاش أوراق السلاطة يصبروا أكثر ؟', '', '<p style=\"text-align: right;\">برشة ناس يحطو أوراق السلاطة في كيس بلاستيكي و سكروه و ديراكت في الثلاجة، باش السلاطة ما تذبلش و ما تفسدش فيسع ؛ أما الطريقة هذي ما تنجحش </p><p dir=\"rtl\">السر باش أوراق السلاطة ما يفسدوش فيسع ؛ هو أنك تحط منديلة ورقية معاهم في الكيس البلاستيكي.</p><p dir=\"rtl\">و هكا، المنديلة الورقية هي إلي تمتص الرطوبة في بلاصة أوراق السلاطة !</p><p dir=\"rtl\">و سلاطتنا بالطريقة هذي، تنجم تصبر حتى بالجمعة في الثلاجة !</p>', '', 'نصائح عامة', ' كيفاش أوراق السلاطة يصبروا أكثر ؟', 1, '', 0, 1, '2020-12-24 12:58:14', '2021-05-12 14:19:55', 1, 0, '', '2022-02-02 14:51:59', '237', 'shutterstock-156869681.jpg', '', 0);
INSERT INTO `tips` (`tip_id`, `tip_title`, `tip_seotitle`, `tip_description`, `tip_seodescription`, `tip_category`, `tip_slug`, `tip_author`, `tip_image`, `tip_featured`, `tip_status`, `tip_created`, `tip_updated`, `is_paying`, `likes_booster`, `note`, `scheduled_publish_date`, `id_intern`, `video_link`, `thumbnailName`, `video_nature`) VALUES
(238, 'شعملنا في حشو البريكة ؟', '', '<p dir=\"rtl\">كي تجي تفورم في البريكة، ساعات الحشو ما إشدش بلاصتو و هذا بالطبيعة ما إيساعدش ؛ لعظمة ما تركحش و تسيخ و البريكة فورمتها تجي مش و لا بد !</p><p dir=\"rtl\">عنا ليك الحل :</p><p dir=\"rtl\">خلي حشو البريكة يبرد بالڨدا و في عقلو. هككا ياخذ الفورمة و إشد روحو و إنت تفورم بريكتك في عقلك و من غير قلق.</p>', '', 'نصائح عامة', 'شعملنا في حشو البريكة ؟', 1, '', 0, 1, '2020-12-24 13:05:41', '2021-05-12 14:20:58', 1, 0, '', '2022-02-02 14:51:59', '238', 'shutterstock-52659721.jpg', '', 0),
(239, ' ! نسيت الحوت في بيت الثلج ؟ عنا الحل', '', '<p dir=\"rtl\">ساعات إنتذكروا كان في آخر الوقت إلي ما خرجناش كعبات الحوت من بيت الثلج !</p><p style=\"text-align: right;\">: ما إتحيرش عنا ليك الحل</p><p style=\"text-align: right;\">.حط الحوت في وعاء، غطيه بالماء مع رشة باهية من الملح الخشين و عصير ليمونة و إلا إثنتين</p><p style=\"text-align: right;\">.خليه ما بين 10 و 15 دقيقة ؛ تتلاحظ أنو معادش جامد و حاضر للطهي</p><p style=\"text-align: right;\">.ميزة أخرى أنو الطريقة هذه تخلي الحوت يحافظ على النكهة و ما إشيحش بعد ما يطيب (بالطبيعة، مع إحترام وقت الطهى المنصوح بيه في الوصفة)</p>', '', 'نصائح عامة', ' ! نسيت الحوت في بيت الثلج ؟ عنا الحل', 1, '', 0, 1, '2020-12-24 13:12:33', '2021-06-07 08:53:36', 1, 0, '', '2022-02-02 14:51:59', '239', 'shutterstock-1350807311.jpg', '', 0),
(240, ' إذابة تجميد اللحوم في الماء البارد', '', '<p dir=\"rtl\"> الناس لكل كي تخرج اللحم المجمد من الثلاجة تمشي فيسع للماء السخون بش تنحيلو التجميد.</p><p dir=\"rtl\">عنا ليكم حل أحسن برشة من الماء السخون. غمس اللحم في وعاء من الماء البارد و تأكد أنو غمرته مليح. راقب مليح درجة حرارة الماء بش تتأكد من أنه ديما يقعد بارد. وقت يكون درجات الحرارة منخفضة، يعتمد وقت التذويب على حجم قطعة اللحم</p>', '', 'نصائح عامة', ' إذابة تجميد اللحوم في الماء البارد', 1, '', 0, 1, '2020-12-24 13:24:38', '2021-05-12 14:18:47', 1, 0, '', '2022-02-02 14:51:59', '240', 'id-240-689875237.jpg', '', 0),
(250, 'بديل البيض لدهن المعجنات', '', '.خلطة الحليب : تعطي لمعان ولون مزيان للمخبوزات على أنواعها. إذا كانت الوصفة مالحة، خلط الحليب مع شوية ملح أما إذا كانت حلوة، خلط الحليب مع السكر.الماء المحلّى : إيخليك تتحصل على معجنات ذهبية من غير ما تخاف أنها تتحرق. الماء يعاون على رطوبة العجين والسكر يعطي اللون الذهبي باش تحصل على نتيجة ولا أشهى.القهوة : مثالية للمعجنات الحلوة، خلط القهوة المرحية مع الماء الدافئ وادهن بيه وجه المعجنات.زيت الزيتون : تنجم تستعملو في تحضير الفطائر بالجبن والسبانخ مثلاً على خاطروا إيزيد نكهة مميزة للمعجنات المالحة و يعطي اللون الذهبي المزيانالزبدة الذائبة :  كيفها كيف زيت الزيتون أما هي مفعولها مضاعف على هكا ننصحوك تخلطها مع الحليب باش تتحصل على نتيجة ذهبية وهشّة في كلّ مرة.\r\n', '', 'نصائح طبخ', 'بديل البيض لدهن المعجنات', 1, '', 0, 1, '2024-05-27 15:42:56', '2021-02-19 10:01:16', 1, 3654, '', '2022-02-02 14:51:00', '250', '250.jpeg', '', 0),
(253, ' أفضل طريقة لحفظ الجبن وحمايته من التعفن', '', 'أول حاجة لازمك ديما تنظف الثلاجة بشكل دوري باش تتخلص من جراثيم العفن التي تكون في الثلاجة بفعل التخزين الدائم للكثير من الاطعمة. \r\nثانية حاجة لازم ديما تخلي الجبن مغطى بغلاف بلاستيكي وتفادي تركه خارج الثلاجة لاكثر من ساعتين.\r\nتمنع هذه الخطوات العفن من التكوّن على سطح الجبن.\r\n\r\n', '', 'نصائح حفظ', ': افضل طريقة لحفظ الجبن وحمايته من التعفن', 1, '', 0, 1, '2024-05-27 15:44:21', '2021-02-05 10:36:37', 1, 5788, '', '2022-02-02 14:51:00', '253', '253.jpeg', '', 0),
(257, 'فوائد الكينوا', 'فوائد الكينوا', 'الكينوا عندها برشا فوائد ععلى صحتنا فهي مصدر جيد للكالسيوم، المغنيسيوم والفيتامين \"أو\" كيما انها غنية بالحديد والبروتين و برشا من المغذيات الأخرى.\r\nللكينوا دور في الوقاية من أمراض القلب والعظام.\r\nكيما تعاون الكينوا على خسارة الوزن فهي لا تحتوي على برشا من السعرات الحرارية إذ كل نصف كوب من الكينوا فيه تقريباً 110 سعرة حرارية. إضافةً إلى ذلك، يمكن استهلاك الكينوا من قبل الأشخاص إلي ما ياكلوش الغلوتين.\r\n\r\n', '', 'نصائح غذائية', 'فوائد الكينوا', 1, '', 0, 1, '2022-05-20 11:02:52', '2021-02-05 10:49:18', 1, 1471, '', '2022-02-02 14:51:00', '257', '257.jpeg', '', 0),
(260, 'طريقة باش تجمّد البطاطا في الفريزر من غير ما تكحال، اكتشف المكون السري ! ', '', 'اغسل البطاطا و قصها شرائح دائرية عريضة. قص البطاطا إلي باش تقليها لصوابع عريضة. حط البطاطا في وعاء معبي بالماء وخليها شوية. توا على نار هادية غلي 3 كيسان ماء مبعد زيدهم كاس خل أبيض و زوز مغارف ملح. خلي الخليط يغلي مليح مبعد حط فيه البطاطا مدة 5 ل7 دقايق فقط. توا خرج البطاطا و حطها في صفاية تستقطر. مبعد وزع البطاطا متاعك في صينية كبيرة وصب فوقها نص كاس زيت نباتي وحركها حتى لين تشرب الزيت باش ما تلصقش كي تجمد. آخر حاجة توا  قسم البطاطا متاعك في أكياس مخصصة للتجميد و سكرهم مليح.\r\n', '', 'نصائح حفظ', 'طريقة تجميد البطاطس في الفريزر دون تصبح سوداء اكتش', 1, '', 0, 1, '2024-05-27 15:44:28', '2021-02-05 11:00:23', 1, 4695, '', '2022-02-02 14:51:00', '260', '260.jpeg', '', 0),
(262, 'كيفاش تخبي الشمبنيون مدة طويلة من غير ما يكحال ', '', 'معروف على الشمبنيون الي هو فيسع ما يكحال و مايصبرش برشا، على هذاكا جبنالكم طريقة ساهلة باش تخبي بيها الشمبنيون متاعك لمدة طويلة من غير ما يصيرلو شيئ.\r\nأول حاجة نظف الشمبنيون متاعك مليح و نحي منو أي وسخ ولا تراب، مبعد غطسو في صحفة كبيرة فيها ماء و قارص مدة 5 دقائق تقريبا باش لونو مايتبدلش. توا طيب الشمبنيون سريعا على حمام مائي (الطبخ على البخار يعاون الشمبنيون أنو يحافظ على صلابتو باش يصبر اكثر). غطس الشمبنيون متاعك في الماء البارد و مكعبات الثلج. توا وزع الشمبنيون على صينية باش يشيح و يتخلص مالماء. آخر حاجة وزع الشمبنيون في اكياس و سكرهم مليح و خبيهم في الفريزر. و بالطريقة هاذي تنجم تحافظ عليه لمدة طويلة و تستعملو في اي وقت تحب عليه.\r\n\r\n', '', 'نصائح حفظ', ': كيفية حفظ المشروم المطهو على البخار', 1, '', 0, 1, '2024-05-27 15:44:37', '2021-02-05 11:02:52', 1, 8452, '', '2022-02-02 14:51:00', '262', '262.jpeg', '', 0),
(263, 'كيفاش تقضي على السوس من حكك كوجينتك', 'كيفاش تقضي على السوس من حكك كوجينتك', 'إلي ما يعرفوهش برشا ناس إنو أوراق الرند يعمل على التخلص من اي بكتيريا قد تكون موجودة باش تسوس الحبوب.\r\nعلى هاكا باش تقضي على السوس من حكك كوجينتك حط  ورقة رند داخل كل حكة.\r\nخاطر رائحة الرند تؤثر على اي نوع من السوس والعث والنمل وتبعدها عن محيط التخزين.\r\n\r\n', '', 'نصائح عامة', ': كيفية تفادي تسوس الحبوب بمكوّن واحد', 1, '', 0, 1, '2022-05-19 16:52:59', '2021-02-05 11:07:58', 1, 9376, '', '2022-02-02 14:51:00', '263', '263.jpeg', '', 0),
(265, ' حفظ الموز باش تستخدمو في المخبوزات او العصائر والسموثي ', '', 'قص كل كعبة موز ل8 ولا ل10 قطع. قسم القطع في أكياس حسب الكمية المناسبة لكل وصفة باش تعملها. حط الأكياس متاعك في الفريزر مبعد ما تسكر على كل كيس مليح. كان تحب تستعمل الموز الجامد في الوصفات الشايحة، ذوب الموز متاعك ومبعد إستقطرو في صفاية باش تتخلص من جميع السوائل إلي فيه. وكان باش تستعملو في العصائر ولا السموثي تنجم تستعمل الموز المجمد طول من غير ما تستقطرو.      \r\n', '', 'نصائح حفظ', ' تجميد الموز لاستخدامه في المخبوزات او العصائر وال', 1, '', 0, 1, '2024-05-27 15:44:43', '2021-02-05 11:42:17', 1, 4272, '', '2022-02-02 14:51:00', '265', '265.jpeg', '', 0),
(266, 'افضل طريقة لحفظ البطيخ باش يبقى فترة طويلة من غيرما يفسد', '', 'يحتوي البطيخ على نسبة عالية من الماء، على هذاكا فأن عملية حفظه في الفريزر ماتنجحش في أكثر الاحيان الا اذا تخبى بالطريقة الصحيحة. عل هذاكا جبنالك أحسن طريقة لتفريز البطيخ حتى يبقى لفترة طويلة من غير ما يفسد. ارحي البطيخ بواسطة الخلاط الكهربائي و حطو  في وعاء المخصص لمكعبات الثلج. و هكا تنجم تستعملو في أي وقت باش تحضر عصير البطيخ مثلا.\r\n', '', 'نصائح حفظ', 'افضل طريقة لتفريز الشمام حتى يبقى لفترة طويلة من د', 1, '', 0, 1, '2024-05-27 15:44:50', '2021-02-05 11:16:43', 1, 5884, '', '2022-02-02 14:51:00', '266', '266.jpeg', '', 0),
(269, 'طريقة حفظ السفنارية من غير صمتان ', '', 'أول حاجة اختار كعبات سفنارية لباس و مش مضروبين. اغسل السفنارية مليح وقشرها. قص السفنارية لقطع ولا أبرشها حسب الرغبة. حط قطع السفنارية في أكياس متع ثلاجة ولا حكك تتسكر مليح وحطهم فالفريزر. تنجم تخبي السفنارية متاعك بالطريقة هاذي حتى ال6 شهور. \r\n', '', 'نصائح حفظ', ': طريقة تفريز الجزر بدون سلق', 1, '', 0, 1, '2024-05-27 15:44:59', '2021-02-05 11:21:35', 1, 4526, '', '2022-02-02 14:51:00', '269', '269.jpeg', '', 0),
(270, 'افضل طريقة لحفظ السفرجل من غير ما يفقد لونو', '', '1- اغسل حبات السفرجل الي حاجتك بيهم وقص كل كعبة على أربعة، مبعد حطهم في وعاء فيه ماء وشرائح قارص مدة درجين\r\n2- مبعد حط الخليط علي النار وخليه حتى يغلي مدة درجين\r\n3- مبعد برد السفرجل بالماء البارد والثلج\r\n5- شيح السفرجل وحطو في اكياس تثليث وحطو في الفريزر ومدى وقت الحاجة\r\n', '', 'نصائح حفظ', 'افضل واسهل طريقة لحفظ السفرجل بدون ما يفقد لونه', 1, '', 0, 1, '2024-05-27 15:45:07', '2021-02-05 11:24:15', 1, 6398, '', '2022-02-02 14:51:00', '270', '270.jpeg', '', 0),
(271, 'تفريز الفلفل الأخضر', '', 'أحسن طريقة تخزين الفلفل الاخضر مدة طويلة من غير ما يتبدل لونو أو مذاقو هي تجميدو في الفريزر بالطريقة هاذي :\r\n\r\n1. قص الفلفل حلقات و حطو في صينية على ورق زبدة.\r\n\r\n2. فرق الحلقات بالتساوي في الصينية باش ما تتراكمش فوق بعضها و تتجمد بالتساوي.\r\n\r\n3. حط الصينية في الفريزر لمدة ساعة تقريبًا و خلي الصينية مكشوفة.\r\n\r\n4. مبعد ما يجمدو الحلقات بدلهم في حكك او اكياس تخزين و سكرهم مليح بعد ما تخرج منهم الهواء.\r\n\r\nبالطريقة هاذي تنجم تخبي الفلفل لأطول فترة ممكنة وتستعملو في اي وقت تحب عليه من غير ما يفقد نكهتو.\r\n\r\n', '', 'نصائح حفظ', 'تفريز الفلفل الرومي', 1, '', 0, 1, '2024-05-27 15:45:15', '2021-02-05 11:29:19', 1, 7458, '', '2022-02-02 14:51:00', '271', '271.jpeg', '', 0),
(272, 'ما تحطش البطاطس و البصل مع بعضهم جملا! هاو علاش ', '', 'ما تحطش البطاطس و البصل مع بعضهم جملا! زعمة علاش ؟ \r\nخاطر البطاطا و البصل  يطلقان الرطوبة اثناء التخزين مما قد يؤدي الى فساد حبات البصل والبطاطس بسرعة كبيرة. لذلك فمن الافضل تخزين البطاطس والبصل في مكان جاف وبارد ويتعرض لتدفق الهواء بشكل دائم وبشكل منفصل. اضافة الى ذلك فإن عملية حفظ البطاطس والبصل معاً قد تؤدي الى تشكّل البراعم في البصل والبطاطس بسرعة كبيرة عند التخزين.هذا يعني ان عملية تخزين اي نوع من الخضروات معاً خارج الثلاجة لا يعتبر آمناً ابداً ويعرّض كل الكمية الى الفساد بسرعة\r\n\r\n', '', 'نصائح عامة', 'لا تحتفظي بالبطاطس والبصل معاً ابداً.. اليك السبب', 1, '', 0, 1, '2022-05-02 01:28:48', '2021-02-05 11:31:16', 1, 13641, '', '2022-02-02 14:51:00', '272', '272.jpeg', '', 0),
(274, 'كيفاش تخبي الشربة كان حضرت كمية كبيرة منها؟', '', 'كيفاش تخبي الشربة كان حضرت كمية كبيرة منها؟ سؤال يسألوه برشة خاصةً في رمضان :  \r\nحفظ الشربة في الفريزر يعاون في اطالة مدة صلاحيتها مقارنة مع حفظها في الثلاجة. \r\nتدوم الشوربة المحفوظة في الفريزر لمدة تصل الى 3 اشهر شرط ان تكون محفوظة بشكل صحيح، يعني تكون مسكرة بطريقة مضبوطة و الهواء ما يخرجش منها. \r\n\r\n', '', 'نصائح عامة', 'حضرت كمية كبيرة من الشوربة؟ اليك افضل طريقة للاحتف', 1, '', 0, 1, '2022-04-04 00:28:01', '2021-02-05 11:36:31', 1, 1627, '', '2022-02-02 14:51:00', '274', '274.jpeg', '', 0),
(276, 'افضل طريقة باش تخبي الكريم فراش  بعد ما تحلها من غير ما تفسد', '', 'ما تخبيش الكريم فراش في باب الثلاجة، خبيها في العمق خاطر البرودة تكون أكثر الداخل، تنجم تخبي الكريم فراش مدة 5 حتى ال7 أيام كحد اقصى. فرغ الكريم فراش في دبوزة بلاستيك ولا بلار كان الكريم فراش إلي شريتها من داخل معدن. كان تحب الكريم فراش متاعك تقعد مدة أكثر تنجم تخبيها في الفريزر: فرغ الكريم فراش متاعك في قالب مكعبات الثلج، حط في كل مكعب زوز مغارف كبار مالكريمة ودخلهم للفريزر. تنجم مبعد ما يجمدو تفرغهم في أكياس و هكا تنجم تستعملهم وقت ما تحب. \r\n\r\n\r\n', '', 'نصائح حفظ', 'افضل طريقة لحفظ كريمة الطبخ بعد فتحها من دون ان تح', 1, '', 0, 1, '2024-05-27 15:45:27', '2021-02-05 11:40:49', 1, 2656, '', '2022-02-02 14:51:00', '276', '276.jpeg', '', 0),
(279, 'مدة حفظ الدجاج النيء في الثلاجة', 'مدة حفظ الدجاج النيء في الثلاجة', 'تنجم تحفظ الدجاج النيء حوالي يوم واحد أو اثنين في الثلاجة. \r\nأما في الفريزر، تنجم لـ6-12 شهر تقريباً.\r\nبالنسبة للدجاج المطبوخ تخبيه في الفريزر حتى 6 أشهر. و في الثلاجة ليومين على الأكثر.\r\n\r\n', '', 'نصائح عامة', 'مدة حفظ الدجاج النيء في الثلاجة', 1, '', 0, 1, '2022-03-29 23:25:08', '2021-02-05 12:04:14', 1, 8719, '', '2022-02-02 14:51:00', '279', '279.jpeg', '', 0),
(280, 'الطرق الصحيحة لحفظ الدجاج المسلوق', '', 'كان طيبت دجاجة كاملة : \r\nقسّم الدجاج إلى حصص مناسبة بحسب الكمية المطلوبة للوجبة.\r\nو حط  الدجاج المسلوق في كيس مخصص للثلاجة و سكرو بإحكام.\r\nاحتفظ بالدجاج المسلوق لـ12 ساعة عل الأكثر في الثلاجة\r\nكان طيبت صدور دجاج كهو : \r\nصفّي صدور الدجاج من ماء السلق : \r\nضع الكمية المناسبة بحسب كل وجبة في أكياس مخصصة للثلاجة و سكرو بإحكام.\r\nاحتفظ بصدور الدجاج المسلوق في الثلاجة.\r\n\r\n', '', 'نصائح عامة', 'الطرق الصحيحة لحفظ الدجاج المسلوق', 1, '', 0, 1, '2022-04-07 23:11:55', '2021-02-05 12:05:40', 1, 841, '', '2022-02-02 14:51:00', '280', '280.jpeg', '', 0),
(281, 'بديل البيض في الدونات', 'بديل البيض في الدونات', 'البديل الأول : ماء + بذر الكتان، خلط ملعقة كبيرة من بذور الكتان مع 3 ملاعق كبيرة من الماء، تتحصل على بديل بيض فعال يعاون على تماسك العجينة ويحافظ على طراوتها. خلي المزيج جانباً شوية دقائق حتى تمتصّ بذور الكتان كمية الماء واستخدمو بدل بيضة واحدة في وصفتك. \r\nالبديل الثاني : بذور الشيا، تعتبر بذور الشيا مصدراً مهماً للبروتين الأمر إلي يخليها من أفضل بدائل البيض التي يمكنك استخدامها. يمكنك استخدام بذور الشيا بطريقتين إمّا تخلط ملعقة كبيرة من بذور الشيا مع 3 ملاعق من الماء كبديل لكّل بيضة وإمّا ملعقة صغيرة بمفردها على أن تكون مطحونة جيداً كي لا تتشكّل لديك كتل في العجينة.\r\nالبديل الثالث : موز، يعتبر الموز من أكثر المكونات فعالية لاستبدال البيض بفضل قوامه اللزج وقدرته على تحقيق تماسك المكونات. هو من أفضل بدائل البيض في الكيك والكوكيز ويمكنك استخدامه في وصفة الدونات على أن ستتبدلي كلّ بيضة بنصف موزة صغيرة وناضجة.\r\n\r\n', '', 'نصائح حلويات', 'بديل البيض في الدونات', 1, '', 0, 1, '2022-05-20 11:00:00', '2021-02-05 12:11:38', 1, 619, '', '2022-02-02 14:51:00', '281', '281.jpeg', '', 0),
(283, 'خبي التفاح بالطريقة هاذي', '', 'في بالك الي تنجم تخبي التفاح لمدة أسابيع بالخطوات هاذي :\r\n1-افرز التفاح الفاسد على التفاح الباهي باش المجموعة الكل ما تفسدش.\r\n2-استعمل اوراق الجرائد باش تغطي كل كعبة وحدها.\r\n3-حطهم في صندوق وأعمل طبقة عازلة كيما القش أو فرشة بلاستيك منقوبة بحيث تتمكن من ضبط درجة الحرارة وكمية الهواء الي توصل للتفاح.\r\n4-حط التفاح في الصندوق ولا الوعاء المبطن مبعد اختار بلاصة ذات درجة حرارة ما تقلش على درجة التجمد باش تتمكن من حفظ التفاح.\r\n5-تجنب تخزين التفاح بالقرب مالبطاطا خاطر البطاطا تنتج غازا كلّما نضجت ما سبب تعفن التفاح بسرعة.\r\n\r\n', '', 'نصائح حفظ', 'خطوات تساعدك على حفظ التفاح طازجاً مدة أطول', 1, '', 0, 1, '2024-05-27 15:45:34', '2021-02-05 12:16:29', 1, 8411, '', '2022-02-02 14:51:00', '283', '283.jpeg', '', 0),
(285, 'الفوائد السحرية لمشروب الزنجبيل والقرفة', '', 'مشروب الزنجبيل والقرفة السحري كلو فوائد من بينها :\r\n\r\n1.يساعد مشروب القرفة والزنجبيل على تعزيز صحة القلب والحماية من الجلطات والعديد من أمراض القلب.\r\n\r\n2.يساعد على خفض مستوى الكوليسترول الضار في الدم وخفض ضغط الدم المرتفع.\r\n\r\n3.يساعد على تحسين الدورة الدمويةتحسين مستوى السكر في الدم.\r\n\r\n4.يساعد على الشعور بالشبع لفترة طويلة ما يجنبك الإفراط في تناول الطعام وبالتالي خسارة الوزن.\r\n\r\n5.يعزز جهاز المناعة لاحتوائه على نسب عالية من مضادات الأكسدة كما قد يقي من بعض أمراض السرطان.\r\n\r\n6. يساهم في علاج نزلات البرد.\r\n\r\n7. يساعد على تهدئة الأعصاب ومحاربة الأرق والتوتر الليلي وخاصة الناتج من الاضطراب الهضمية والتنفسية.\r\n', '', 'نصائح غذائية', 'فوائد الزنجبيل والقرفة', 1, '', 0, 1, '2024-05-27 15:45:42', '2021-02-05 13:10:17', 1, 8561, '', '2022-02-02 14:51:00', '285', '285.jpeg', '', 0),
(286, 'المسموح في الكيتو دايت', '', ': إن نظام الكيتو ليس صعباً و لكنه محدود من ناحية الأطعمة المسموحة لذلك احرصي على أن تتبعي الرجيم بالتفصيل، أن تدخلي الأطعمة التالية لنظامك الغذائي.البيض، سواء أكان مسلوقاً، مخفوقاً أو مقلياً بالزبدة فهو أحد أهمّ الأطعمة في الميتو دايت.اللحوم الخالية من الكربوهيدرات مثل اللحم الغني بالدهون والدجاج.الدهون الطبيعية مثل الزبدة، زيت الزيتون، زيت جوز الهند، زيت الأفوكادو والسمن الطبيعي أو البلدي.مشتقات الحليب كاملة الدسم مثل الكريمة، الحليب والأجبان.السمك وثنر البحر على أنواعها.البهارات والتوابل على أنواعها.الشوكولاتة الداكنة التي تحتوي على 90% من الكاكاو.المحليات الصناعية مثل الستيفيا والسكرالوز.بعض أنواع الخضار مثل القرنبيط، الكرنب، البروكولي، الكوسا، الفلفل الرومي، الباذنجان، الطماطم، الخيار، البصل، الفطر والخضار الورقية مثل السبانخ، الخس والجرجير.بعض الفواكه مثل التوت والفراولة\r\n', '', 'نصائح غذائية', 'المسموح في الكيتو دايت', 1, '', 0, 0, '2024-05-27 15:45:52', '2021-02-05 13:13:07', 0, 0, '', '2022-02-02 14:51:00', '286', '286.jpeg', '', 0),
(288, 'تونة كم سعرة حرارية', '', 'السعرات الحرارية في التونة بالماء: ان التونة المحفوظة بالماء تحتوي على عدد سعرات حرارية قليلة نسبياً إذ كل 100 غرام من التونة بالماء تحتوي على 120 سعرة حرارية تقريباًالسعرات الحرارية في التونة بالزيت: أما التونة المحفوظة بالزيت فهي تحتوي على عدد سعرات حرارية أكثر من تلك المتوفرة في التونة المحفوظة بالماء إذ كل 100 غرام من التونة المحفوظة بالزيت تحتوي على 282 سعرة حرارية تقريباً\r\n', '', 'نصائح غذائية', 'تونة كم سعرة حرارية', 1, '', 0, 0, '2024-05-27 15:45:59', '2021-02-05 13:17:19', 1, 0, '', '2022-02-02 14:51:00', '288', '288.jpeg', '', 0),
(289, 'تعرفشي منافع التن ؟ ', '', 'تُعتبر التونة المعلبة مصدر مهم للأوميغا 3 إلي يحسّن صحة القلب والدماغ ويعاون على تقليل نسبة الكوليستيرول الضار في الجسم. \r\nالحاجة الثانية تناول التونة بشكل منتظم يؤمن للجسم نسبة مهمة من البروتين إلي يحتاجها للحفاظ على الكتلة العضلية. \r\nتحتوي زادة التونة على العديد من الفيتامينات والمعادن كالفسفور، الإيود، الفيتامين د، الفيتامين ب12 وغيرها من العناصر الأساسية لصحة الجسم.\r\n\r\n', '', 'نصائح غذائية', 'فوائد التونة', 1, '', 0, 1, '2022-04-27 01:27:55', '2021-02-05 13:18:06', 1, 16314, '', '2022-02-02 14:51:00', '289', '289.jpeg', '', 0),
(290, 'الماكلة إلي تعاونك على تعويض نقص الزنك', '', 'برشة ماكلة غنية بالزنك بشكل طبيعي، إلي تعاونك على تعويض نقص الزنك، منهم : \r\nثمار البحر : المحار، السلطعون والكركند.\r\nاللحوم : لحم البقر ولحم الضأن.\r\nالدواجن: الديك الرومي والدجاج.\r\nالسمك: سمك السلمون، السردين.\r\nالبقوليات: الحمص، العدس، الفاصولياء.\r\nالمكسرات والبذور: بذور اليقطين، الكاجو، بذور القنب.\r\nمشتقات الحليب: الحليب والزبادي والجبن والبيض.\r\nالحبوب الكاملة: الشوفان، الكينوا والارز البني.\r\nالخضروات: الفطر، اللفت، البازلاء والهليون.\r\n\r\n', '', 'نصائح غذائية', ': الاطعمة التي تساعد على تعويض نقص الزنك', 1, '', 0, 1, '2022-04-06 00:55:23', '2021-02-05 13:21:50', 1, 8549, '', '2022-02-02 14:51:00', '290', '290.jpeg', '', 0),
(291, 'فوائد شربة العدس', 'فوائد شربة العدس', 'شربة العدس و ما أدراك ما العدس فيها كمية كبيرة من الألياف الغذائية بفضل احتوائها على مكونات كيما العدس، الجزر وغيرها من الخضار.\r\nعلى هاكا منصوح  بتناولها خاطرها تسهل أداء الجهاز الهضمي وتشعر بالشبع لفترة طويلة و تعزز صحة القلب خاطرها غنية بحمض الفوليك، المغنيزيوم والبوتاسيوم. \r\nو زيد إنو  شربة العدس مصدر نباتي للبروتين لدرجة أنه يمكن اسخدامه بدلاً من اللحم في العديد من الوصفات قليلة السعرات الحرارية : تعتبر شربة العدس خياراً مناسباً عند تباع نظام غذائي صحي فهي قليلة السعرات الحرارية وتحتوي على كافة العناصر الغذائية التي تساعد في الحفاظ على وزن متوازن او خسارة وزن زائد.\r\n\r\n', '', 'نصائح غذائية', 'فوائد شوربة العدس', 1, '', 0, 1, '2022-03-27 23:37:40', '2021-02-05 13:24:58', 1, 973, '', '2022-02-02 14:51:00', '291', '291.jpeg', '', 0),
(294, 'فوائد قلوب القرع', '', 'بذور القرع عندها برشا فوائد و غنية بالعناصر الغذائية أهمها : الاياف الغذائية، الكربوهيدرات، البروتين، الدهون، الفيتامينات والمعادن الاساسية لجسمك.بذور القرع غنية بمضاداات الاكسدة : تحتوي بذور القرع على مضادات الأكسدة الي تعاون على محاربة الالتهابات والامراض على انواعها.بذور القرع تحسن صحة البروستات والمثانة : تخفف بذور القرع من حدة أعراض مرض البروستات كيما انها تساعد على تحسين أداء المثانة.بذور القرع مصدر للمغنيزيوم : تحتوي بذور القرع على المغنيزيوم الي يعاون على تنظيم ضغط الدم، ضبط مستويات السكر في الدم، تقليل فرص الإصابة بأمراض القلب وتعزيز صحة العظام. قد يلعب المغنيزيوم دوراً في تعزيز استرخاء الأعصاب باش تتمتع بنوم مرتاح!بذور القرع غنية بالألياف : تحتوي بذور القرع على نسبة مهمة من الألياف تساعد على تحسين عملية الهضم كما تعزز الشعور بالشبع لفترة طويلة.بذور القرع تساعد على النوم بشكل أفضل : إن تناول بذور القرع يساعد على تحسين جودة النوم بفضل احتواء هذه البذور على الأحماض الأمينية التي تساعد على الاسترخاء خاصة لدى تناولها قبل الخلود للنوم. بالإضافة إلى ذلك، تحتوي بذور القرع على الزنك الذي يساعد الجسم على إفراز هرمون الميلاتونين الذي ينظم ساعات النوم.\r\n', '', 'نصائح غذائية', 'فوائد بذور القرع', 1, '', 0, 1, '2024-05-27 15:46:05', '2021-02-05 13:32:06', 1, 6952, '', '2022-02-02 14:51:00', '294', '294.jpeg', '', 0),
(295, 'فوائد زيت الزيتون', '', 'زيت الزيتونة و ما أدراك ما زيت الزيتونة :\r\nيعزز زيت الزيتونة حركة الأمعاء و يحمي من الإمساك.\r\nيعاون زيت الزيتون على حماية القلب من الأمراض و يقلل من نسبة الكوليسترول في الدمّ.\r\nكيما يعاون على ضبط مستويات السكر في الدمّ.\r\n\r\n', '', 'نصائح غذائية', 'فوائد شرب زيت الزيتون على الريق', 1, '', 0, 1, '2022-03-21 09:23:13', '2021-02-05 13:33:07', 1, 4931, '', '2022-02-02 14:51:00', '295', '295.jpeg', '', 0),
(296, 'تعرف على كل فوائد اللوز', '', 'من فوائد اللوز الصحية : \r\nتقليل خطر الاصابة بامراض القلب والاوعية الدموية والحد من خطر النوبات القلبية.\r\nتنظيم مستويات الكوليسترول والدهون في الجسم.\r\nتنظيم نسبة ومستوى السكر الطبيعي في الدم.\r\nتحفيز عمل الجهاز المناعي في الجسم لمحاربة الامراض والعدوى.\r\nالحفاظ على قوة العظام ومنع الاصابة بهشاشة العظام.\r\nالحد من الشهية المفرطة وبالتالي علاج السمنة.\r\nزيادة مستوى الطاقة والنشاط وزيادة معدل التمثيل الغذائي.\r\n\r\n', '', 'نصائح غذائية', ': فوائد اللوز الصحية', 1, '', 0, 1, '2022-04-21 10:45:05', '2021-02-05 13:35:13', 1, 6174, '', '2022-02-02 14:51:00', '296', '296.jpeg', '', 0),
(297, 'قداه من كعبة لوز في النهار ؟ ', 'قداه من كعبة لوز في النهار ؟ ', 'على الرغم من فوائده الكثيرة للجسم، الا انو الاطباء يحذرو من الافراط في تناول اللوز الذي يعد غنيا بالسعرات الحرارية خاصة اذا كان مقلي. \r\n وبالتالي المنصوح بيه أنو يتاكل قبل التمارين الرياضية باش تزيد مستوى النشاط ومعدل الطاقة في الجسم.\r\nبالتالي، وبحسب ما يقول خبراء التغذية فانه يمكن تناول ما يتراوح بين 5 الى 10 حبات من اللوز يوميا لا أكثر تجنبا للسمنة او للاصابة بالغثيان والاسهال التي تعتبر من اعراض الافراط في تناول اللوز.\r\n\r\n', '', 'نصائح غذائية', 'كم حبة لوز في اليوم؟', 1, '', 0, 1, '2022-03-24 21:37:55', '2021-02-05 13:36:02', 1, 7941, '', '2022-02-02 14:51:00', '297', '297.jpeg', '', 0),
(298, 'فوائد صفار البيض', '', 'أصفر البيض عندو برشة منافع مانعرفوهاش، أهمها: \r\n1.تعزيز صحة الجهاز المناعي؛ 2.تعزيز صحة العيون؛ 3.الحفاظ على صحة العظام؛ 4.الحفاظ على توازن مستوى الحديد في الدمّ؛ 5.الحفاظ على صحة الخلايا في الجسم ونموهم؛ 6.يزيد صفار البيض من نسبة امتصاص الجسم للمعادن؛ 7.الحفاظ على ضغط الدم: يحتوي صفار البيض على مكونات تُساعد على تخفيض ضغط الدم المرتفع؛ 8.تعزيز صحة البشرة والشعر: وذلك بسبب احتوائه على فيتامينات عديدة؛ 9.تعزيز صحة الأمعاء والمثانة؛ 10.الحفاظ على صحة الدماغ: يُساعد صفار البيض على تطوير القدرات العقلية؛ 11.تقليل خطر الإصابة بالالتهابات: بسبب احتوائه على مضادات الالتهابات ومضادات الأكسدة؛ 12.يقلّل صفار البيض من خطر الإصابة بأمراض الكلى.\r\n\r\n\r\n', '', 'نصائح غذائية', 'فوائد صفار البيض', 1, '', 0, 1, '2024-05-27 17:00:54', '2021-02-05 13:38:32', 1, 3604, '', '2022-02-02 14:51:00', '298', '298.jpeg', '', 0),
(299, 'فوائد البطاطا الحلوة', '', 'البطاطا الحلوة منخفضة السعرات الحرارية : تعتبر البطاطا الحلوة منخفضة السعرات الحرارية إذ تحتوي كلّ وجبة بطاطا على 100 سعرة حرارية إذاً فهي مثالية لنظامك الغذائي الصحي اذا كنت ترغبين بخسارة الوزن.\r\nالبطاطا الحلوة غنية بالألياف : تحتوي البطاطا الحلوة على كمية مهمة من الألياف التي تساعد على تسهيل عملية الهضم والتخلص من السموم مع تعزيز الشعور بالشبع لفترة طويلة.\r\nالبطاطا الحلوة غنية بالماء : تعتبر البطاطا الحلوة من الأطعمة غنية بالماء الأمر الذي يجعلها مهمة لمنع الإفراط في تناول الطعام وتحسين عملية الهضمالبطاطا الحلوة توازن مستوى السكر في الدمّ : تساعد البطاطا الحلوة على توازن مستويات السكر في الدمّ والحفاظ على مستويات السكر في الدم صحية لمنع تراكم الدهون وحتى مقاومة الأنسولين الذي قد يسبب بالسمنة\r\n', '', 'نصائح غذائية', 'فوائد البطاطا الحلوة للرجيم', 1, '', 0, 1, '2022-03-15 22:06:07', '2021-02-05 13:40:08', 1, 1974, '', '2022-02-02 14:51:00', '299', '299.jpeg', '', 0),
(301, 'كيفاه التشيز كيك ايجي مش مشقق مالفوق', 'كيفاه التشيز كيك ايجي مش مشقق مالفوق', 'اليوم نقولولك النصائح التي تعاونك باش اج سطح تشيز كيك أملس ومثالي : \r\nحط قالب التشيز كيك في الدرجة السفلى من أدراج الفرن ومن الأفضل أن تكون حرارة الفرن منخفضة الى متوسطة. اذ تؤدي درجة الحرارة المرتفعة والمباشرة الى ظهور التشققات على سطح التشيز كيك \r\nما تخرجش قالب التشيز كيك من الفرن مباشرةً، بل اطفئي الفرن واتركي بابه مفتوحاً حتى يبرد التشيز كيك بالكامل بما أن التشيز كيك يحتاج الى حرارة منخفضة، من الأفضل أن تقومي بخبزه في الحمّام المائي، اذ عند غليان المياه وتبخّرها يساعد ذلك على خبزه ببطئ من الداخل والخارج دون إحداث تشققات على وجهه أو هبوطه.\r\nمن الأفضل أن تكون المكونات اللينة المستعملة كالزبدة و الجبنة بدرجة حرارة الغرفة قبل إضافتها الى المكونات الأخرى وخلطها. عند اضافتها وهي لا تزال باردة سيؤدي ذلك الى ظهور كتل ودخول كرات هوائية الى الخليط وبالتالي التأثير عليها خلال مرحلة الخبز وعدم تماسكها\r\n\r\n', '', 'نصائح حلويات', 'كيف نتجنب التشققات على وجه التشيز كيك المخبوز', 1, '', 0, 1, '2022-05-30 09:12:01', '2021-02-08 13:51:49', 1, 2654, '', '2022-02-02 14:51:00', '301', '301.jpeg', '', 0),
(302, 'كيفاش تخبي البيتراف لاطول وقت ', '', 'من المستحسن تصمط البيتراف قبل ما تكنجلو باش يقعد لاطول مدة\r\n1-اصمط البيتراف مدة 45-60 دقيقة تقريبا حتى لين يطيب.\r\n2-قص البتراف لقطع.\r\n3-وزع البتراف في صينية وكنجلو باش ما يلصقش في بعضو.\r\n4-حطو في اكياش تفريز وخله في الفريزر.\r\n\r\n', '', 'نصائح حفظ', 'إحتفظي بالشمندر حتى 6 أشهر بـ2 خطوات فقط!', 1, '', 0, 1, '2024-05-27 15:46:15', '2021-02-08 14:06:15', 1, 8441, '', '2022-02-02 14:51:00', '302', '302.jpeg', '', 0),
(303, 'كان الروز تحرق، شنوا الحل', 'كان الروز تحرق، شنوا الحل', 'لاحتراق الروز درجات. إذا كان لاحظت أن لونه لكل ولى أسود، إرميه بالوقت. لكن كان حسيت إلي الطبقة السفلية من الروز كهو لاصقة بالقعر، مرّر قعر القدر من الخارج تحت سبالة الماء البارد حتى تتوقف عملية الطبخ. خرج الروز المش محروق من القدر و حطوا في وعاء التقديم.\r\n', '', 'نصائح طبخ', 'إذا احترق الأرز والتصق بالقدر، ما الحل ؟ ', 1, '', 0, 1, '2022-05-30 09:09:56', '2021-02-08 14:08:00', 1, 4526, '', '2022-02-02 14:51:00', '303', '303.jpeg', '', 0),
(307, 'أفوكادو ناضجة في ‏ 10 دقائق', 'أفوكادو ناضجة في ‏ 10 دقائق', 'باش  تتحصل على أفوكادو ناضج ‏خلال 10 دقائق فقط :\r\nحمّ الفرن :  قبل أن تبدأ، حمّ الفرن على حرارة 100 درجة مئوية. هذه الخطوة أساسية لأن الحرارة التي ‏سينتجها الفرن ستلعب دوراً في إنضاج حبّة الأفوكادو. ‏\r\nجهّز الأفوكادو : غلّف حبة الأفوكادو غير الناضجة بورق الأليمنيوم. سيحمي ذلك قوام وحرارة الأفوكادو من ‏حرارة الفرن المرتفعة. تأكد من تغلف حبة الأفوكادو جيداً حتى لا تتسرب الحرارة إلى الداخل ‏وتتلفها.‏إنتظر 10 دقائق : بعد تغليف حبة الأفوكادو بورق الأليمنيوم، ضعيها في صينية وأدخليها إلى الفرن. بعد مرور 10 ‏دقائق، أخرج الأفوكادو من الفرن. ستلاحظ أنها لا تزال باردة ويمكن التقاطها بسهولة.أزيل ‏ورق الأليمنيوم بلطف ثمّ استخدم الأفوكادو الناضجة في تحضير وصفاتك \r\n\r\n', '', 'نصائح عامة', 'أفوكادو ناضج ‏خلال 10 دقائق فقط', 1, '', 0, 1, '2022-05-20 11:02:12', '2021-02-08 14:32:00', 1, 7914, '', '2022-02-02 14:51:00', '307', '307.jpeg', '', 0),
(308, 'دجاج مقلي دياري، يتقرمش تقرميش', '', 'باش تتحصل على قشرة دجاج مقلي مقرمشة ما عليك كان تزيد للخليط شوية خميرة كيميائية. إلي بدورها تبدل حموضة جلد الدجاج و إيحوّلو باش يولي مقرمش أكثر. و تخلق زادة الخميرة تفاعل من ثاني اوكسيد الكربون بين جلد الدجاج والزيت السخن الشيء إلي يخلق فقاعات مقرمشة في قشرة الدجاج\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2022-05-20 11:06:09', '0000-00-00 00:00:00', 1, 108, '', '2022-02-02 14:51:00', '308', '308.jpeg', '', 0),
(309, 'أسرار نجاح البراونيز', '', 'خليط البيض والسكر مليح : الخطوة هذه من أهم الخطوات باش تحصل على نتيجة مضمونة في الأخر إلي هي براونيز هشوش\r\nدخلوا للفور مرتين : كل شيفان العالمين أكدو إنو أحسن حاجة للبراونيز تدخيلوا للفور مرتين. في المرة الأولى 15 دقيقة و تخرجوا و تخليه يبرد شوية و تعاود دخلوا مرة ثانية لمدة 30 دقيقة\r\nتخليه يرتاح ساعة قبل القصان : لازم البراونيز يرتاح في عقلو و يبرد قبل ما نقصوه. \r\n\r\n', NULL, 'نصائح حلويات', '', 1, '', 0, 1, '2024-05-27 15:46:21', '0000-00-00 00:00:00', 1, 294, '', '2022-02-02 14:51:00', '309', '309.jpeg', '', 0),
(310, 'سرّ البنة متاع الدجاج المشوي', '', ': سرّ البنة متاع الدجاج المشوي التفويح متاعوا. نقدمولكم أهم مكونات تفويح الدجاج المشوي\r\n.زيت الزيتون : إخليي الدجاج مروي مش شايح\r\n.عصير الليمون، الخلّ الأبيض، الياغورت الطبيعي و إلا الثلاثة مع بعضهم: إخليو الدجاج طري خاصةً كي يبدى على الفحم\r\n.صلصة الصويا : تعطي للدجاج نكهة مالحة معندهاش مثيل\r\n.المايونيز: حاجتين يعملهم أنو يعزز طراوة الدجاج و يضمن قشرة خارجية مقرمشة\r\n.السكر أو العسل: بالأخصّ إذا كنت من عشاق الماكلة الحلوة والمالحة في نفس الوقت\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2022-03-01 20:47:49', '0000-00-00 00:00:00', 1, 617, '', '2022-02-11 11:15:00', '310', '310.jpeg', '', 0),
(311, 'طريقة حفظ البطاطا في الفريزر', 'طريقة حفظ البطاطا في الفريزر', 'غسلي البطاطس وقطعي الى شرائح دائرية عريضة. وقطعي البطاطس المخصصة للقلي الى اصابع عريضة لتفادي تعريضها لحروق التجميداا\r\n.اغمري البطاطس في كل وعاء بالماء واتركيها تنتقع قليلاً\r\n.على نار خفيفة، اغلي 3 اكواب من الماء في قدر. اضيفي كوب من الخلّ الابيض مع ملعقتين من الملح. واتركي الخليط حتى يغلي جيداً\r\n.اضيفي البطاطس اتركيها من 5 الى 7 دقائق فقط\r\nرفعي البطاطس وضعيها في مصفاةا\r\n.وزّعي البطاطس في صينية كبيرة. اسكبي مقدار نصف كوب من الزيت النباتي فوق البطاطس\r\n.قلبي البطاطس مع الزيت جيداً حتى تتشرب الزيت جيداً لتفادي التصاقها عند التجميد\r\n.وزّعي البطاطس في اكياس حفظ الاطعمة في الفريزر، واقسمي البطاطس بحسب طريقة تقطيعها، الشرائح الدائرية في اكياس واصابع البطاطس في \r\nاكياس أخرى\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2022-05-20 11:02:33', '0000-00-00 00:00:00', 1, 141, '', '2022-02-11 11:29:00', '311', '311.jpeg', '', 0),
(312, 'باش ترتاح من الزيوت إلي تلصق على مفاتح الغاز', '', 'عيون الغاز مسخة و محروقة ؟ الحل ساهل :في وعاء، حط الخل الأبيض و عيون الغاز ليلة كاملة.في الصباح، أغسلهم بالماء تحت السبالة و باش تفرح كتشوفهم انظافوا.نفس الشيء تنجم تعملوا مع الغاز في حد ذاتو كي يبدا مسخ برشة.\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2022-04-20 04:01:39', '0000-00-00 00:00:00', 1, 9431, '', '2022-02-23 09:04:00', '312', '312.jpeg', '', 0),
(313, 'باش تنحي الروائح مالصحون', '', 'باش ترتاح من روايح الماكلة إلي وحلت في الصحون، حط مع الغسول طروف قارص، و كان مازالت ما تنحاتش الريحة حط الصحون متاعك في ماء و خل أبيض و هكا تكون متأكد إلي الروايح تنحو نهائيا.\r\n\r\n\r\n', NULL, 'نصائح تنظيف', '', 1, '', 0, 1, '2024-05-27 15:47:48', '0000-00-00 00:00:00', 1, 3688, '', '2022-02-23 09:25:00', '313', '313.jpeg', '', 0),
(314, ' الطريقة الصحيحة باش تسخن المأكولات المخبوزة', '', 'البيتزا و المأكولات المخبوزة بنتها وقت تكون سخونة ومقرمشة. ساعات كيف نشريو البيتزا مناكلوهاش بالوقت، تبرد وتفقد البنة متاعها ​برشا يغلطو ويسخنوها في الميكرووند. لكن موش هذا الحل باش تبقا محافظة على نفس البنة والخفة، سخنها في المقلاة وكولها كاينك شريتها توا \r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2023-05-10 08:30:13', '0000-00-00 00:00:00', 1, 1654, '', '2022-02-23 09:25:00', '314', '314.jpeg', '', 0),
(315, 'حفظ الثوم المفروم', '', 'باش تحافظ على نوعية الثوم المفروم لفترة طويلة، ننصحوك : تقشر فصوص الثوم كالعادة و ترحيهم حسب الرغبة متاعك بسكينة و إلا بفرامة كهربائية. \r\nو بعد تحط الثوم المفروم في صينية مكعبات الثلج. \r\nو بعد أغمرو بالزيت مليح. \r\nكيما تنجم تخلط الثوم المفروم و الزيت قبل و بعد تحطهم في صينية مكعبات الثلج. \r\nغلفهم و دخلهم للفريزر. \r\nتنجم تخليه من شهر حتى لعام، طيب فيه و صحة و بالشفاء. \r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2022-04-14 21:43:28', '0000-00-00 00:00:00', 1, 8641, '', '2022-02-23 09:27:00', '315', '315.jpeg', '', 0),
(316, 'سر تنظيف الكبدة ', '', 'حضر كمية الكبدة إلي تحب تنظفها و حطها في صفاية \r\nفي قدر نظيف حط لتر ماء بارد \r\nحط في الماء مغرفة كبيرة ملح و مغرفة كبيرة سكر \r\nزيد للماء زوز مغارف كبار خل أبيض و زوز مغارف كبار قارص\r\nحرك الخليط مليح حتى يذوب السكر والملح \r\nصب توا الخليط فوق الكبدة في الصفاية وحرك بالمغرفة باش الكبدة الكل تنظاف\r\nتوا صفي الكبدة مليح مالماء وخليها تستقطر  \r\n باش تلاحظ مبعد العملية هاذي إلي لون الكبدة ولى أفتح وهذاكا يعني إلي هي جاهزة للطبخ \r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 15:47:55', '0000-00-00 00:00:00', 1, 3629, '', '2022-02-23 09:48:00', '316', '316.jpeg', '', 0),
(317, 'نصائح باش اتطيب الكبدة مبعد ما نظفناها', '', 'ماتحركش الكبدة برشا على النار، خلي الكبدة تطيب عل جنب واحد مبعد اقلبها على الجنب الاخر.\r\nطيب الكبدة على نار هادية باش تتأكد من طيابها من داخل من غير ما تتحرق القشرة الخارجية متاعها. \r\nزيد شرائح البصل والثوم والفلفل مع الكبدة باش تتمتع بأبن طعم. \r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:48:03', '0000-00-00 00:00:00', 1, 4596, '', '2022-02-23 09:49:00', '317', '317.jpeg', '', 0),
(318, 'سر نجاح عجينة المخابز', '', 'يستعملو الطباخة إلي في المخابز خلطة تتسمى محسّن المخبوزات، وهي عبارة على فارينة وماء كهو. تعتبر هاذي الخلطة السر وراء نجاح مخبوزات المخابز من ناحية طعمها و شكلها و ريحتها. باش تحضر محسن المخبوزات: حط في قدر ثلث كاس من الفارينة مع ثلاثة أرباع الكاس ماء و خلطهم مليح حتى لين تتأكد إلي الفارينة ذابت الكل في الماء. توا حط القدر على نار هادية وأقعد حرك في الخليط حتى يولي قوام العجينة كريمي. مبعد ما يحضر الخليط لازم تخليه في حكة مسكرة يرتاح 6 ساعات قبل ما تستعملو. وكان تحب تخبي منو، تنجم تحط العجينة إلي قعدت في الثلاجة مدة 3 أيام فقط. \r\n\r\n\r\n\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:48:08', '0000-00-00 00:00:00', 1, 3694, '', '2022-02-23 09:50:00', '318', '318.jpeg', '', 0),
(319, 'كيفاش إتجيك العجينة مذهبة ؟', '', 'أهم حاجة وقت تحضير العجينة ما تزيدش برشة من الفارينة باش تشكل و إلا تحل بيها العجينة. على خاطر الفرينة الزايدة تؤدي الى جفاف المعجنات وقت تحضيرها.\r\n\r\nو باش إتجيك العجينة مذهبة إدهن وجهها إما بخليط من البيض أو الحليب قبل ما دخلها للفور.\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2022-04-29 02:43:24', '0000-00-00 00:00:00', 1, 6149, '', '2022-02-23 09:52:00', '319', '319.jpeg', '', 0),
(320, 'تأكد إلي البوب كورن باش يجيك كعبة كعبة بمكون واحد برك ماهوش باش يجي على بالك ', '', 'باش تتأكد الي البوب كورن يجيك كعبة كعبة و بنين  لازم أول حاجة تحرص باش تاخو ذرة من النوع الباهي. توا في قدر سخّن كمية من الزيت النباتي على نار هادية. مبعد ما يسخن الزيت حط حبات الذرة و زيدهم كمية من خميرة القاطو و كمية من الملح حسب ذوقك. غطّي القدر و خلي كعبات الذرة يطيبو مليح و تو تلاحظ الي الحبات الكل تحولو لبوب كورن بفضل خميرة القاطو. صب البوب كورن متاعك في صحفة و قدمها لضيوفك و صحة و بالشفاء.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 15:48:21', '0000-00-00 00:00:00', 1, 2068, '', '2022-02-23 09:53:00', '320', '320.jpeg', '', 0),
(321, 'نصائح إضافية باش إتطري اللحم ', '', 'لملح: ما تنساش تملّح اللحم قبل ما تطيبو خاطر الملح يعاون باش يطري اللحم\r\n\r\nخلّي اللحم في حرارة الغرفة: الخطوة هاذي مهمة برشا خاصة لللحم البقري. لازم تخلي اللحم مدة ما تقلّش على 30 دقيقة في حرارة الغرفة قبل ما تطيبو.\r\n\r\nطيب اللحم في عقلو: خلي اللحم يطيب على نار هادية، يتحلل الكولاجين في السوائل الي تخرح ماللحم و هذا الي يخلي الألياف العضلية اليابسة تولي طرية\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:48:33', '0000-00-00 00:00:00', 1, 2806, '', '2022-02-23 09:55:00', '321', '321.jpeg', '', 0),
(322, 'الأسباب إلي تخلي الماكلة تلصق في المقلاة', '', 'برشا اسباب تخلي الماكلة تلصق في القلاة، أهمها :\r\n1.المقلاة مش سخونة برشا : لازم نسخنو المقلاة مليح مع شوية زيت قبل ما نحطو فيها الماكلة باش ما تلصقش.\r\n2.قلب المكولات قبل ما تطيب : كي تطيب الماكلة مليح (مثلا السمك) تتكون طبقة مقرمشة تحميه مالالتصاق، بينما كان نقلبوه قبل ما يطيب باش يقعد جزء منو لاصق في المقلاة.\r\n3.تطييب المأكولات رطبة : خاصة في الشوي، لازم نشيحو الماكلة مليح مالماء باش نمنعو التصاقها.\r\n4.استعمال مقلاة قديمة :لازم نبدلو المقلاة مبعد مدة من استعمالها، خاطر مع مرور الوقت و كثرة الاستعمال تفقد الطبقة العازلة الي فيها و تولي تلصق في الماكلة.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:48:43', '0000-00-00 00:00:00', 1, 7943, '', '2022-02-23 11:20:00', '322', '322.jpeg', '', 0),
(323, 'أسرار نجاح الكوكيز', '', 'حرارة الزبدة : باش ايجيك كوكيز مزيان حط الزبدة الباردة بعد تقطيعها الى مربعات متساوية الحجم.\r\nمساحة كافية بين حبات الكوكيز : خلي ديما ما لا يقل عن 2 سم مساحة بين الحبة والاخرى حتى تتوسع دون أن تلتصق ببعضها.\r\nوقت التطيب في الفور : من المستحسن إخراج الكوكيز قبل 3 دقائق من إنتهاء وقت الطبخ متاعها وإبقائها على ورق الخبز حتى يكتمل نضوجها.\r\nإستخدام السكر الابيض كهو : إحرص ديما باش تستعمل السكر الابيض والسكر الاسمر في وصفة الكوكيز مع إضافة كمية كافية من الزبدة.\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2023-05-26 07:27:49', '0000-00-00 00:00:00', 1, 1374, '', '2022-02-23 11:21:00', '323', '323.jpeg', '', 0),
(324, 'طرق مضمونة باش المقرونة ما تلصقش', '', 'برشا منا يحطو ملح و شوية زيت في ماء صمطان المقرونة باش ما تلصقش. اما الطريقة هاذي مش صحيحة خاطر الزيت يمنع التصاق الصوص على المقرونة و يخليها ماتشربش. \r\nمالا شنوا نعملو باش تجينا المقرونة كعبة كعبة؟ اول حاجة لازم الماء يكون يغلي مليح قبل ما ترمي المقرونة، حط شوية ملح مع الماء و مبعد صب المقرونة، و اقعد حرك فيها مدة دقيقتين ماللول باش ماتلصقش في بعضها، ثاني حاجة مالازمش تغطي الطنجرة، ثالث حاجة طيب المقرونة اقل بشوية مالوقت المذكور في الباكو و ذوقها باش تتاكد من طيابها. صفي المقرونة مليح ورجعها للطنجرة، صب فوقها الصوص و خليها تكمل طيب مدة دقيقتين على نار هادية مع الصوص متاعها باش تشرب مليح.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:48:56', '0000-00-00 00:00:00', 1, 6332, '', '2022-02-23 11:28:00', '324', '324.jpeg', '', 0),
(325, 'أسرار تفويح الدجاج للشوي', '', 'باش تحضر تتبيلة الدجاج المشوي تحتاج لملح، فلفل أكحل، اكليل مرحي، ثوم بودرة و بابريكا حسب الرغبة، خلطهم مليح مع شوية زيت و قارص و فوح بيهم الدجاج متاعك قبل الشوي باش يجي فيه طعم بنين و مميز. تنجم زادة تخبي التتبيلة متاعك في حكة بلار مسكرة و تستعملها وقت ما تحب تفوح الدجاج متاعك، كيما تنجم زادة تفوح بيها الخضرة الي باش تشويها مع الدجاج باش هككا يجي الطعم موحد و بنين.\r\n\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 15:49:06', '0000-00-00 00:00:00', 1, 6588, '', '2022-02-23 11:41:00', '325', '325.jpeg', '', 0),
(326, 'كيفاش تشوي الدجاج ', '', 'حط الدجاج في درجة حرارة الغرفة مدة 15 دقيقة قبل ما إطيبو. باش تتأكد من طياب الدجاج أعمل فيه نقب بالسكينة ولا قصو رهيف. مبعد سخن الشواية مليح قبل بوقت باش الدجاج مايلصقش، وتوا في صحفة صغيرة حضر التتبيلة: حط التوابل متاعك و الزيت و خلطهم مليح حتى لين تتحصل على خليط متجانس. حط الدجاج فوق الشواية و إدهنو بالتتبيلة إلي حضرناها. خلي كل جهة اطيب مدة 5 دقائق مبعد اقلبها و إدهنها بالتتبيلة على الجهة الاخرى. بالطريقة هذي تتأكد إلي الدجاج طاب مليح وقعد طري و رطب من الداخل وفي نفس الوقت تضمن عدم جفاف الجزء الخارجي من الدجاج.\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:49:16', '0000-00-00 00:00:00', 1, 6521, '', '2022-02-23 11:41:00', '326', '326.jpeg', '', 0),
(327, 'نصائح إضافية لتحضير أفضل معجنات', '', 'باش تتحصل على أفضل المعجنات تبع النصائح هاذم :\r\n1.لازم تسخن الفور على الاقل 15 دقيقة قبل ما تستعملو.\r\n2.كي اتطيب المعجنات، لازم تكون سخانة الفور مرتفعة أول 5 دقائق مبعد تنقص فيها.\r\n3.ما تخليش المخبوزات متاعك يطيبو أكثر من اللازم باش ما تشيحش العجينة من السوائل و تولي يابسة.\r\n4.لازم تخلي العجينة تخمر على الاقل ساعة و مبعد ماتحلها و تشكلها لازم تزيد تخليها ترتاح 15 دقيقة اخرى.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:49:24', '0000-00-00 00:00:00', 1, 7457, '', '2022-02-23 11:43:00', '327', '327.jpeg', '', 0),
(328, 'نصائح باش تحافظ على لوحة التقطيع لمدة أطول', '', 'باش تحافظ على لوح التقطيع لأطول مدة ممكنة:  \r\nما تحطش لوح التقطيع الخشبي في غسالة الصحون خاطر بالطريقة هاذي تتشبع اللوحة بكمية كبيرة من الماء و زيد حرارة تجفيف الغسالة تنجم تشيح اللوحة اكثر ماللازم و تتشقق.\r\nنظف و عقم لوح التقطيع مبعد ما تستعملو طول باش ما تتراكمش عليه الجراثيم.\r\nشيج لوح التقطيع مليح بعد ما تغسلو خاطر تتكون داخلو الرطوبة و ينجم يتعفن.\r\n\r\n', NULL, 'نصائح تنظيف', '', 1, '', 0, 1, '2024-05-27 15:49:32', '0000-00-00 00:00:00', 1, 4252, '', '2022-02-23 11:44:00', '328', '328.jpeg', '', 0),
(329, 'كي نحل باب الفور على الكايك زعمة يفسد ؟', '', 'كي نحلو باب الفور من الطبيعي باش تتأثر السخانة الي فيه، و الغلطة هاذي تنجم تفسّد الكايك خاطر الهواء البارد ما يخليش الكايك ينفّج، لازم تخلي الفور مسكر مدة 3/4 الوقت متاع التطييب، و مبعد كان مازلت تحب تتأكد كان طابت ولا، ستنى من 5 الى 10 دقائق قبل ما تحل الفور مرة أخرى.\r\n\r\n\r\n\r\n\r\n\r\n', NULL, 'نصائح حلويات', '', 1, '', 0, 1, '2024-05-27 15:49:39', '0000-00-00 00:00:00', 1, 2697, '', '2022-02-23 11:45:00', '329', '329.jpeg', '', 0),
(330, ' نصائح إضافية لازم تطبقها كي تطيب كايك', '', '1.لازم نصفيو المكونات الجافة كيما: الفارينة، النشاء ولا الخميرة، قبل الاستعمال باش مايصيرش تكتلات.\r\n\r\n2.الزبدة و المارقرين و البيض و الحليب لازم نخرجوهم من الثلاجة قبل الاستخدام. \r\n\r\n3.بعد ما نخلطو الكايك، لازم ما يقعدش برشا البرا، لازم نحطوه يطيب طول في الفور باش ما يطيحش.\r\n\r\n4.كايك الجبن ولا كايك الكريمة لازم يقعدو يبردو في الفور، طفي سخانة الفور وحل الباب و خلي الكايك يبرد في عقلو باش تمنع طياح الكايك متاعك. \r\n\r\n5.مبعد ما تركض بياض البيض لازم تستعملو طول وماتخليهش برشا في الهواء باش مايولش يابس. \r\n\r\n\r\n', NULL, 'نصائح حلويات', '', 1, '', 0, 1, '2024-05-27 15:49:45', '0000-00-00 00:00:00', 1, 2807, '', '2022-02-23 11:46:00', '330', '330.jpeg', '', 0),
(331, 'حيلة للحصول على بيض على حرارة الغرفة', '', 'لإستخدام البيض مباشرةً دون الحاجة الى الانتظار وقت طويل حتى تصبح حرارته على حرارة الغرفة، ضعي كمية البيض التي ترغبين بإستخدامها في وعاء، أغمري البيض بالماء الدافئ وليس الساخن. أتركي البيض مغمور بالماء لمدة 5 الى 10 دقائق حتى تصبح حرارته أكثر دفئاً للتمكن من إستخدامه مباشرةً.\r\nإذا كنت تقومين بتجهيز وصفة تتطلب فصل الصفار عن زلال البيض فحينها من المفضل عدم تطبيق هذه الحيلة قبل فصلهما.\r\n', NULL, 'نصائح حلويات', '', 1, '', 0, 0, '2024-05-27 15:49:51', '0000-00-00 00:00:00', 1, 0, '', '2022-02-23 12:15:00', '331', '331.jpeg', '', 0),
(332, 'نصائح لصمتان الروز ', '', 'باش تصمط الروز ويجيك كعبة كعبة تبع الطريقة هاذي :\r\n\r\n1. أغسل الروز مليح بالماء البارد.\r\n\r\n2. خليه يتنقع في الماء البارد مدة 20 دقيقة تقريبا باش يتنحى النشاء متاعو.\r\n\r\n3. نحطو مقدار 2 لتر ماء مع مغرفة صغيرة ملح يغليو.\r\n\r\n4. نصبو الروز متاعنا في الماء المغلي ونخليوه يطيب مدة 15 دقيقة تقريبا، مع التحريك.\r\n\r\n5. نتفقدو الروز كان طاب نطفيو عليه النار و نصفيوه مليح في الصفاية.\r\n\r\n6. نصبو عليه ماء بارد باش يبرد و ما يلصقش في بعضو و نصفيوه.\r\n\r\n7. و هكا يكون عندك روز طايب كعبة كعبة وبنين، تنجم تزيد عليه اي نوع متع صوص او مكونات. \r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:51:33', '0000-00-00 00:00:00', 1, 8451, '', '2022-02-23 12:19:00', '332', '332.jpeg', '', 0),
(333, 'كيفاش اطيب اللازانيا من غير ما تلصق ؟', '', 'برشا نساء يصمتو كعبات اللازانيا بعض الدقايق قبل ما يحطوها في الطبق مع الصوص. اما المشكلة في العمليّة هاذي إلي كعبات اللازانيا يلصقو في بعضهم أول ما تحطهم في الماء السخون و مبعد يتكسرو كي تجي تفككهم من بعضهم. باش تتفادى المشكلة هاذي حط شوية زيت زيتونة قبل ما تصمت اللازانيا خاطر الزيت يمنعهم من الالتصاق. اما من جهة اخرى تجم تستغنى على عملية الصمتان و تطيّب اللازانيا طول في الفور مع الصوص، فقط كثّر من السوائل في الصلصة متاعك، تنجم حتى تزيد سوائل ذات نكهة كيما مرق اللحم. ما تخافش حتى كي تحط برشا سوائل مش باش يأثر على الصوص خاطر باش تمتصها اللازانيا ولا باش تتبخر.\r\n\r\n\r\n\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:51:38', '0000-00-00 00:00:00', 1, 2739, '', '2022-02-23 12:21:00', '333', '333.jpeg', '', 0),
(334, 'طريقة أخري تضمن بيها الي اللازانيا ما تلصقش في قاع الطبق ', '', 'باش تضمن الي اللازانيا ما تلصقش في قاع الصينية تنجم تحط الصلصة قبل قطع اللازانيا وهكا تضمن الي الازانيا تطيب مليح وزيد ما تشدش في القاع.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:51:43', '0000-00-00 00:00:00', 1, 7127, '', '2022-02-23 12:21:00', '334', '334.jpeg', '', 0),
(335, 'كيفاش تتحصل على زبدة طرية خلال 15 دقيقة من غير ميكرووند ', '', 'تبع الخطوات هاذي باش تتحصل على زبدة طرية:\r\nحط كمية الزبدة إلي تحب اطريها بين زوز ورقات. \r\nإستعمل القلقال باش تنزل على الزبدة مليح. \r\nلف الزبدة على القلقال مع الورقتين باش تعاون الزبدة باش تطرى أكثر.\r\nكي يولي خشن الزبدة 2 صم، نحي الورقتين و إستعمل الزبدة متاعك، الطريقة هاذي تاخو 4 دقائق فقط. \r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:53:02', '0000-00-00 00:00:00', 1, 4638, '', '2022-02-23 12:25:00', '335', '335.jpeg', '', 0),
(336, 'المدة المناسبة باش تصمط العضم', '', 'تختلف مدة صمتان البيض بحسب درجة الاستواء :\r\n\r\nاذا كنت تحب تناول البيض شبه نيء أصمتو مدة 5 دقائق.\r\n\r\nاذا كنت تحب البيض شبه ناضج أصمتو لمدة 8 دقائق.\r\n\r\nأما البيض الناضج تماماً أصمتو لمدة 12 دقيقة.\r\n\r\nاحتسب وقت صمتان البيض من وقت غليان الماء وليس من وقت وضع القدر على النار.\r\n\r\nو الطريقة المثالية لاكل البيض المسموط و الحفاظ على جميع فوائدو هي ترك البيضة لست دقائق على الأكثر في الماء المغلي بحيث يتم طهي البياض بشكل كامل، ويبقى الصفار لزج وطري شوية.\r\n\r\nحيث يحتوي صفار البيض الرخو بعد الطهي على قيمة غذائية أكثر بنحو 50% من الصفار المصموط بالكامل.\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:53:10', '0000-00-00 00:00:00', 1, 9008, '', '2022-02-23 12:57:00', '336', '336.jpeg', '', 0),
(337, ' الطريقة الصحيحة، باش إتذوب اللحم المجمد', '', ' الناس لكل كي تخرج اللحم المجمد من الثلاجة تمشي فيسع للماء السخون بش تنحيلو التجميد.عنا ليكم حل أحسن برشة من الماء السخون. غمس اللحم في وعاء من الماء البارد و تأكد أنو غمرته مليح. راقب مليح درجة حرارة الماء بش تتأكد من أنه ديما يقعد بارد. وقت يكون درجات الحرارة منخفضة، يعتمد وقت التذويب على حجم قطعة اللحم\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2022-04-01 10:17:55', '0000-00-00 00:00:00', 1, 7943, '', '2022-02-23 13:00:00', '337', '337.jpeg', '', 0),
(338, 'ماعادش تلوح ماء صمطان الروز و هاو علاش!', '', 'ماء صمتان الروز عندو برشا منافع أهمها: \r\n1.يهدي البشرة المتهيجة:أكدت الدراسات أن الاستحمام لمدة 15 دقيقة بماء سلق الأرز كل يوم يساعد في تهدئة أعراض التهاب الجلد. ويعود السبب وراء ذلك إلى الخصائص المضادة للالتهابات الموجودة في ماء الأرز.\r\n2.منظف طبيعي للبشرة:إذا كانت بشرتك عرضة للحساسية، فقد تكون ماء الأرز منظفًا طبيعيًا ولطيفًا لك. انقعي بعض القطع من القطن في ماء الأرز ثم قومي باستخدامها على بشرتك عبر التدليك. اتركي الماء حتى تجف بشكل طبيعي لتتمكن بشرتك من إمتصاص الماء والاستفادة من فوائد ماء الأرز. طبقي هذه الطريقة يوميًا للحصول على بشرة نضرة.\r\n3.غسول للشعر:عند وضعها على الشعر، تساعد ماء سلق الأرز على تعزيز مرونة الشعر وتقليل الاحتكاك المرتبط بتلف الشعر. لاستخدام الماء قومي بعد غسل شعرك، بشطفه بماء سلق الأرز مباشرةً. سيعطي هذا شعرك لمعانًا وقوة صحية بسرعة.\r\n4.معالجة حروق الشمس:يمكن لماء الأرز أيضًا أن تساعد على مدواة حروق الشمس لأنها تسرع عملية شفاء الجلد وتمنع المزيد من الضرر. لإستخدام ماء سلق الأرز على مناطق حروق الشمس قومي بتبريدها تماماً قبل الاستخدام.\r\n5.يهدي البشرة المعرضة لحب الشباب:بفضل الخصائص المضادة للالتهابات لماء الأرز، فهي مفيدة للغاية للأشخاص الذين يعانون من حب الشباب. يمكن أن تهدئ ماء سلق الأرز الاحمرار والالتهابات المختلفة المرتبطة بحب الشباب.\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:53:18', '0000-00-00 00:00:00', 1, 4029, '', '2022-02-23 13:21:00', '338', '338.jpeg', '', 0),
(339, 'شنوا المكون إلي تزيدوا للشربة  كان جات مالحة برشة ؟', '', 'باش تتخلص من الملوحة الزائدة، زيد بضع قطع من االبطاطا االنية في القدر و خليها لمدة 10-15 دقيقة أو حتى تقل الملوحة حسب رغبتك. تمتص قطع االبطاطا الملح. من ناحية الطعم ماتخافش ، خاطر االبطاطا ما تخلي حتى أثر على طعم الشوربة.\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2022-04-14 08:45:16', '0000-00-00 00:00:00', 1, 8349, '', '2022-02-23 13:23:00', '339', '339.jpeg', '', 0),
(340, 'كيفاش تتجنب الإفراط في تمليح الشربة', '', 'لازم تزيد الملح تدريجيا للماكلة وترد بالك مالمكونات اللي تجم تغلطك كيما مرق الدجاج ولا الهريسة العربي كان مالحة، ومالمستحسن تعدل الملح في اللخر كي تذوقها.\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:54:36', '0000-00-00 00:00:00', 1, 8542, '', '2022-02-23 13:24:00', '340', '340.jpeg', '', 0),
(341, 'شنوا سر اللحم الطري في الفور ؟', '', 'المكون السحري: الخلّ\r\nيتمتع الخلّ بفوائد متنوعة سواء في الطبخ أو العناية بالمنزل وهو من أهمّ المكونات التي تستخدم بشكل يومي.\r\nفي حالة طبخ اللحم في الفور، يلعب الخلّ دور المطرّي خاطر يتغلغل في اللحم ويضمنلك نتيجة طرية من الداخل مع الحفاظ على القرمشة اللذيذة من الخارج.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2022-04-17 23:44:17', '0000-00-00 00:00:00', 1, 8614, '', '2022-02-23 13:25:00', '341', '341.jpeg', '', 0),
(342, 'كيفاش تحافظ على لون البروكولي الأخضر أثناء السلق ', '', 'حيل سهلة للمحافظة على لون البروكولي الاخضر أثناء السلق:\r\nتقليل وقت السلق: ما يجب أن تعرفيه هو أنه كلما طالت مدة سلق الخضروات ، كلما تغيرت جزيئات الكلوروفيل وكلما فقدت الخضروات لونها الأخضر الجميل. لذلك عليك تقليل وقت الطهي من أجل الحفاظ على اللون، سواء في الماء أو على البخار، فإن طهي الخضار الخضراء لمدة 5 الى7 دقائق سيحمي الكلوروفيل من التلف. إذا كنت تحضرين الخضار المسلوقة، اغمريها بالماء المثلج بعد السلق لإيقاف الطهي ولتفادي التأثيرات الضارة للحرارة.\r\nحماية الخضار من الصلصات الحمضية: اذا كنت ترغبين بإضافة البروكولي لأي وصفة تحتوي على الخلّ أو عصير الليمون الحامض، فلا تقومي بإضافة الصلصة الى البروكولي سوى قبل التقديم بدقائق قليلة لتفادي تغير لونه الأخضر أيضاً.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:54:43', '0000-00-00 00:00:00', 1, 7419, '', '2022-02-23 13:27:00', '342', '342.jpeg', '', 0),
(343, 'نصائح لتناول البروكولي بشكل صحيح', '', 'حتى تستفيدي من كلّ فوائد البروكولي الصحية، لا بدّ أن تتناوليه كاملاً مع الجذع والأوراق إذ أن هذا النوع من الخضار يدخل في مجموعة الأكلات الخارقة الغنية بالفيتامين A, C, وE. إليك في ما يلي طريقة تناول البروكولي بالتفاصيل.\r\n\r\nيتخلص الكثيرون من جذع البركولي لأنه الجزء الذي يبقى قاسٍ عند الطبخ ولكن هذه الخطوة قد تحرمك من العديد من الفوائد مثل الألياف المفيدة لتعزيز الشعور بالشبع وتحسين عملية الهضم. قبل طهي البروكولي، أفصلي الجذوع وقطّعيها إلى مكعبات. ضعيها في\r\n قدر مملوء بالماء المغلي واسلقيها حتى تصبح طرية.\r\nتحتوي أوراق البروكولي على أكبر كمية من البيتا كاروتين المفيد للبشرة وتخفيف خطر الإصابة بسرطان الصدر لدى النساء قبل سن اليأس وهو من أهمّ مضادات الأكسدة التي تحارب الزمراض بأنواعها. تعتبر الأوراق أيضاً مصدر مهم للفيتامين A الذي يلعب دوراً مهماً في تعزيز صحة العينين وجهاز المناعة. تحتوي الزوراق أيضاً على الفيتامين C الذي يحمي من الأمراض كالرشح.\r\nتلعب طريقة الطبخ دوراً مهماً في الاستفادة من فوائد البروكولي بشكل كامل لذلك من الأفضل أن تطهيه على البخار بحسب الدراسات حتى تحافظي على أكبر كمية من الألياف، الفيتامينات والمعادن. أضيفي إليه مجموعة من البهارات والتتبيلات اللذيذة حتى تستمتعي بمذاقه في اي وقت دون أن تسأمي من نكهته!\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 15:54:51', '0000-00-00 00:00:00', 1, 0, '', '2022-02-23 13:32:00', '343', '343.jpeg', '', 0),
(344, 'حيلة ساهلة و بسيطة باش تنظف بلار الميكرووند و الفور ', '', 'باش تنظف بلار المكرووند والفور من بلايص اليدين المزعجة، خلط في فشفاشة كمية متساوية مالماء و الخل الأبيض. زيدهم شوية من غسول الصحون وخلط الدبوزة مليح. رش الخليط على بلار المكرووند والفور بالتساوي و إمسح البلار بقطعة قماش من الالياف الدقيقة. آخر حاجة شيح ولمع البلار بالجرايد إلي عندك و لاحظ النتيجة المثالية. \r\n', NULL, 'نصائح تنظيف', '', 1, '', 0, 1, '2024-05-27 15:54:58', '0000-00-00 00:00:00', 1, 4255, '', '2022-02-23 13:47:00', '344', '344.jpeg', '', 0);
INSERT INTO `tips` (`tip_id`, `tip_title`, `tip_seotitle`, `tip_description`, `tip_seodescription`, `tip_category`, `tip_slug`, `tip_author`, `tip_image`, `tip_featured`, `tip_status`, `tip_created`, `tip_updated`, `is_paying`, `likes_booster`, `note`, `scheduled_publish_date`, `id_intern`, `video_link`, `thumbnailName`, `video_nature`) VALUES
(345, 'نصائح باش يجيك مشوي ناجح', '', 'تأكدي من أن جميع أدوات الشواء المناسبة التي تسهل عليك جميع الخطوات متوفرة لديك.\r\nتأكدي من نوع الفحم لديك، وأنه لم يتعرض للرطوبة أبداً.\r\nوزّعي الفحم جيداً على الشواية عندما ترغبين بالشوي.\r\nلتفادي إلتصاق الاطعمة على الشواية إحرصي على دهن الشواية المصنوعة من المعدن بالقليل من الزيت قبل إضافة اي نوع من الاطعمة مثل الدجاج.\r\nيحتاج الدجاج عند تطبيق هذه النصائح بين 20 الى 25 دقيقة كحد أقصى حتى ينضج تماماً.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2023-05-17 07:50:41', '0000-00-00 00:00:00', 1, 7702, '', '2022-02-23 14:13:00', '345', '345.jpeg', '', 0),
(346, 'زعما الزنجبيل يضعّف؟', '', 'علاش برشا دراسات تقول انو الزنجبيل يضعف ؟ على خاطرو يعزز عملية الهضم في الجسم ويعمل على خفض نسبة الكورتيزول ويعطيك الطاقة اللازمة باش تكمل نهارك نهارك.\r\n\r\nكيما انو الدراسات تقول انو ممارست التمارين الرياضية بشكلٍ رويتيني وتناولك شاي الزنجبيل بعد الإنتهاء من كل وجبة يعاون برشا في التخفيض الوزن.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 15:55:05', '0000-00-00 00:00:00', 1, 7660, '', '2022-02-24 11:01:00', '346', '346.jpeg', '', 0),
(347, 'إنسى زفرة الدجاج', '', 'بعد ما تنظف الدجاج أغسلو مليح،و في صحفة كبيرة عبيها بالماء، زيدها مغرفتين كبار فارينة، شوية ملح، قارص مقصوص، شوية خل أبيض و رشة كمون. خلي الدجاج في الخليط هذا ساعة و إنسى زفرة الدجاج إلي يقضي عليها خاصةً الكمون\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2022-05-20 11:03:29', '0000-00-00 00:00:00', 1, 147, '', '2022-02-24 22:11:00', '347', '347.jpeg', '', 0),
(348, 'أطعمة تقلل العطش في نهار رمضان', '', 'في السحور، ركز على الماكلة إلي تهضم بعد سوايع ؛  كيما السردينة و اللوز و  و الزوز و البيضوعلى الماكلة إلي فيها \"السكر البطيء\" مثل : الكسكسي و إلا الخبز بالقمح الكامل، العدس ...إلي يبعدوا عليك الاحساس بالجوع لمدة طويلة !و هذه عادة، مذابينا، نحافاظو عليها طول شهر رمضان المبارك، باش نضمنو توزيع باهي و متوازن للطاقة. \r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2022-05-19 17:01:26', '0000-00-00 00:00:00', 1, 8416, '', '2022-03-07 14:40:00', '348', '348.jpeg', '', 0),
(353, 'تجنب الأطعمة هاذي في السحور باش ما تعطش في النهار ', '', 'الأطعمة الي لازم تتجنبها في السحور باش ما تعطش في النهار:\r\n\r\n1- المخللات: تحتوي على نسبة عالية من الأملاح مما يؤدي للشعور بالعطش بسبب خروج الماء من الجسم.\r\n\r\n2- الأكل الحار والمتبل: يجب تجنب تناول الأكل الحار والأكلات الغنية بالبهارات لأنه تسبب العطش أيضا خاصة بالنهار، وابتعدي أيضا عن الجبن الرومي والفلافل.\r\n\r\n3- السكريات: قللي من السكريات كلما جاء وقت السحور، إذ أنها تجعل البنكرياس يفرز الأنسولين لحرقها مما يؤدي لانخفاض السكر في الدم والشعور بالجوع وأحيانا بالغثيان في النهار.\r\n\r\n4- اللحوم والدواجن والأسماك: يفضل تجنبها على السحور والاكتفاء بها أثناء وجبة الإفطار.\r\n\r\n5- مشروبات الكافيين: يؤدي الكافيين لتنشيط عمل الكلى مما يؤدي للتخلص من مخزون الماء الموجود بالجسم عن طريق البول ومن ثم الشعور بالعطش، وأبزرها القهوة والنسكافيه.\r\n\r\n6- الأطعمة المعلبة: تحتوي على كمية كبيرة من الملح وبالتالي فهي تجعل الجسم يتخلص من الماء الموجود به.\r\n\r\n7- المشروبات الغازية: تعطي شعورا وهميا بالشبع بالإضافة إلى أنها تتسبب في نفخ البطن، فسرعان ما تجد نفسك جائعا مرةآخرى.\r\n\r\n8- المقليات: تجنبي المقليات على وجبة السحور حفاظا على صحتك.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 15:55:13', '0000-00-00 00:00:00', 1, 7485, '', '2022-03-07 14:56:00', '353', '353.jpeg', '', 0),
(355, 'الحل كي يطيح صفار البيض مع أبيض البيض ', '', 'إذا كان هناك القليل من صفار البيض أو الدهون المتبقية في الوعاء أو في الخلاط، فسيكون من الصعب جدًا خفق بياض البيض. إذا لم يكن هناك الكثير مما يمكنك فعله للأسف، فإن القليل من عصير الليمون أو بعض الملح الخشن يمكن أن ينقذ الموقف.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 0, '2024-05-27 15:55:20', '0000-00-00 00:00:00', 1, 0, '', '2022-05-16 16:21:00', '355', '355.jpeg', '', 0),
(356, 'كيفاش تنظف الغلة  بطريقة صحيحة', '', ' .حيلة باش تنظف الغلة مليح و تخليها تدوم لفترة اطول : حط الغلة متاعك في حوض معبي بالماء و صب كاس واحد خل, خليهم مدة 10 دقايق مبعد اغسلهم و شيحهم مليح و خبيهم \r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 15:55:29', '0000-00-00 00:00:00', 1, 5236, '', '2022-05-16 16:23:00', '356', '356.jpeg', '', 0),
(357, 'القاطو تكسر ؟ ما فما حتى مشكلة  ', '', 'حضرت قاطو بنين اما خسارة تكسر؟ ما تتأسفش بالعكس اعمل منو حاجة مزيانة ومبتكرة، حط طبقات مالقاطو المكسر وحط فوقو طبقات مالكريمة المخفوقة في كاس بلار، و من فوق زينو بالفواكه الجافة ولا الغلال. وهكا تتحصل على تحلية رائعة للسهرة و تكون انقذت القاطو متاعك بأحسن طريقة.\r\n', NULL, 'نصائح حلويات', '', 1, '', 0, 1, '2024-05-27 15:55:37', '0000-00-00 00:00:00', 1, 6637, '', '2022-05-16 16:25:00', '357', '357.jpeg', '', 0),
(358, ' حيلة باش الخبز مايفسدش فيسع ', '', 'حط ساق كرفس مع الكيس الي تخبي فيه الخبز و تو تلاحظ الفرق، الكرفس يحافظ على طراوة الخبز لمدة اطول مالعادة.\r\n\r\n\r\n', NULL, 'نصائح حفظ', '', 1, '', 0, 1, '2024-05-27 15:55:49', '0000-00-00 00:00:00', 1, 7487, '', '2022-05-16 16:26:00', '358', '358.jpeg', '', 0),
(359, '  أحسن طريقة باش تحافاظ على الموز لمدة طويلة ', '', 'افصل حبات الموز للحفاظ عليها لفترة أطول. معظم الناس يشترون الموز كاملاً. بدلًا من إبقاء الموز معلقًا معًا، افصل بينهما. من الأفضل إبعادهم عن بعضهم البعض. لماذا؟ لأن الموز ينتج غازات تنضج فواكه وبالتالي سيقوم بالنضج بشكل سريع. بفصلهم، يبقون لفترة أطول. ثم لف نهاية كل ثمرة موز بغلاف بلاستيكي شفاف هذه العملية ستقوم بالحفاظ عليه لمدة أطول.\r\n\r\n', NULL, 'نصائح حفظ', '', 1, '', 0, 1, '2024-05-27 15:56:00', '0000-00-00 00:00:00', 1, 5288, '', '2022-05-16 16:28:00', '359', '359.jpeg', '', 0),
(360, 'طريقة ساهلة باش  تبشر جبنة الموزاريلا', '', '\r\nفي العادة يكون بشران الموزاريلا صعيب على خاطر الموزاريلا تلصق في المبشرة و يكون شدانها صعيب. شنوا الحل مالا كان نحبو نبشروها فوق البيتزا مثلا؟ خوذ صرة الموزاريلا و لفها في ورق السيلوفان و حطها في الثلاجة مدة 30 دقيقة تقريبا و مبعد خرجها و هكا تنجم تستعملاها بكل سهولة . \r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:56:06', '0000-00-00 00:00:00', 1, 5879, '', '2022-05-16 16:29:00', '360', '360.jpeg', '', 0),
(362, ' !ما تغسلش المقرونة مبعد ما تطيب ', '', 'ما تغسلش المقرونة مبعد ما تطيب هاو علاش : كي تغسل المقرونة، النشا الطبيعي الي في المقرونة الي يعاون الصوص باش تلسق فيها يمشي مع الماء. خلي المقرونة تستقطر مليح و صبها طول في الصوص هكا تتحصل على نتيجة افضل.\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:56:23', '0000-00-00 00:00:00', 1, 6653, '', '2022-05-16 16:33:00', '362', '362.jpeg', '', 0),
(365, 'شنوا تعمل كان تحرقت الصوص؟  ', '', 'حرقت الصوص و ماعرفتش شنوا تعمل؟ الحل ساهل برشا : صب الصوص في قدر نظيف من غير ما تحط الاجزاء المحروقة. زيد شوية ماء بارد وخليها تكمل تطيب. توا زيد شوية سكر و اقعد حرك في الصوص. كل مرة تفقد الصوص و زيد شوية سكر و ذوق حتى لين تحس الي مطعم الحراق اختفى. كيما تنجم تحط بطاطا نية في الصوص باش تمتص مطعم الحراق.\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:56:30', '0000-00-00 00:00:00', 1, 6325, '', '2022-05-16 16:37:00', '365', '365.jpeg', '', 0),
(367, 'ما تلوحش ماء صمتان المقرونة و البطاطا! هاو علاش ', '', 'استخدم ماء صمطان المعكرونة أو البطاطا لسقي النباتات المنزلية الخاصة بيك، فقط استنا حتى لين يبرد الماء. نباتاتك باش تحبو خاطر ماء الصمطان يحتوي على عناصر غذائية مفيدة لصحة النبات.\r\nونفس الشيء بالنسبة لماء الحوض لسقي النباتات، كي تنظيف حوض السمك متاعك، ما تلوحش الماء. فضلات الأسماك غنية بالنيتروجين والفوسفور وهما عنصران أساسيان لصنع سماد ممتاز لنباتاتك.\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:56:35', '0000-00-00 00:00:00', 1, 7796, '', '2022-05-17 08:50:00', '367', '367.jpeg', '', 0),
(371, 'عندك زوز كيسان واحلين في بعضهم؟ هاو الحل', '', 'كان عندك زوز كيسان واحلين في بعضهم الحل ساهل : حط مكعبات ثلج في الكاس إلي من فوق و غطس الكاس إلي من لوطا في الماء السخون. بالطريقة هاذي يمتد الكاس إلي من لوطا بالسخانة وينكمش الكاس إلي من فوق و هكاكا تنجم تفصلهم على بعضهم بكل سهولة. \r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2024-05-27 15:56:42', '0000-00-00 00:00:00', 1, 6514, '', '2022-05-17 08:59:00', '371', '371.jpeg', '', 0),
(374, 'مقرونة طايبة برشا؟ شوف الحل  ', '', 'المقرونة كي تطيب برشا تعجن وتولي مش بنينة، الحل الشنوا ؟ حط المقرونة في الماء البارد مبعد حمرها في مقلاة كبيرة مع الزبدة وهكا تخليها تشد روحها مرة اخرى.\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:56:57', '0000-00-00 00:00:00', 1, 8447, '', '2022-05-17 09:07:00', '374', '374.jpeg', '', 0),
(375, 'مشكلة النمال في الكوجينة ', '', 'باش تبعد النمال على كوجينتك، تبع مسرب النمال باش تعرف منين يدخل ومبعد حط الفازلين باش تسد الفتحة الي يدخلو منها. النمال ما ينجمش يتعدى عبر الفازلين. و كان النمال يدخل من تحت الباب، أرسم خط طباشيري خشين. النمال يكره الطباشير و مايتعداش فوقو.\r\nطرق اخرى تنجم تتخلص بيها مالنمال:\r\nرش الخل على الاماكن الي فيها النمال.\r\nرش الصابون والماء في البلايص الي يدخل منها النمال.\r\nخلط شوية كحول مع الماء و رش بيهم.\r\nذوب شوية ملح في الماء و رش بيه.\r\nقشور الفقوص و القارص: حط قشور الفقوص ولا القارص في اماكن تواجد النمال.\r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2024-05-27 15:57:20', '0000-00-00 00:00:00', 1, 6753, '', '2022-05-17 09:09:00', '375', '375.jpeg', '', 0),
(376, ' زعما نجمو نعاودو نستعملو زيت القلي ؟', '', 'باش نعاودو نستعملو زيت القليان لازم يكون الزيت مش محروق وما تبدلش لونو وريحتو ، يعني ما نجموش نعاودو نستعملو الزيت الي قلينا فيه الحوت ولا العضم لازم نتخلصو منو، اما زيت الي قلينا فيه البطاطا نجمو نعلودو نستعملوه مرة اخرى كهو.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 15:57:39', '0000-00-00 00:00:00', 1, 7521, '', '2022-05-17 09:12:00', '376', '376.jpeg', '', 0),
(377, 'حيلة ساهلة باش البيض المسموط يتقشر بكل سهولة ', '', 'بالنسبة للبيض المسموط، الملح هو احسن حل، حط مغرفة صغيرة ملح في الماء قبل ما تحط البيض و شوف الفرق و السهولة في التقشير.\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:57:52', '0000-00-00 00:00:00', 1, 5838, '', '2022-05-17 09:14:00', '377', '377.jpeg', '', 0),
(378, 'الشابلير، في رمشة عين  ', '', 'تحب تتحصل على شابلير في وقت قصير؟\r\nعندك بقايا خبز حتى كان مش قديمة ويابسة؟ قصهم طروف طروف وحطهم في كيس بلاستيكي و سكر مليح و حط الكيس في الفريزر. مبعد، من غير ما تذوب منها الثلج، حط الخبز في الخلاط الكهربائي باش يتهروش، و هكا تتحصل على شابلير في أسرع وقت ممكن.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:58:04', '0000-00-00 00:00:00', 1, 6551, '', '2022-05-17 09:16:00', '378', '378.jpeg', '', 0),
(379, ' قواعد تفويج اللحم', '', '5 مكونات أساسية لتفويح لحم العلوش:\r\nالإكليل والزعتر:\r\nيعطي الإكليل والزعتر نكهة مميّزة للحم و ريحة بنينة.\r\nالخل الأبيض:\r\nالخل الأبيض ينحي الروائح الزفرة و القوية الي نشموها ساعات في اللحم.\r\nالياغورت الطبيعي:\r\nالياغورت الطبيعي يخلي اللحم طري بعد ما تشويه.\r\nزيت الزيتون:\r\nزيت الزيتونة مفيد و يطري اللحم و يعاون باش التوابل تلصق في اللحم.\r\nعصير الليمون:\r\nيقوم عصير الليمون بتعزيز نكهة المشوي كيما يقوم زادة بتسهيل عملية الهضم.\r\n\r\nنجمو نزيدو مكونات أخرى كيما الفلفل الأحمر، الزعفران، الكركم ولا اي توابل اخرى نجبو عليها، و ما ننساوش بالطبيعة رشة البصل والمعدونس قبل تقديم الطبق.\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 15:58:10', '0000-00-00 00:00:00', 1, 4227, '', '2022-05-17 09:18:00', '379', '379.jpeg', '', 0),
(380, 'طريقة مبتكرة باش تخبي السكاكن ', '', 'اصنع تخزينًا سريعًا للسكين بمفردك، ضعي السكاكين بكوب مملوء بالأرز. بالإضافة إلى إمساك السكاكين، يمتص الأرز الرطوبة ويمنع الصدأ عن الشفرات.\r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 0, '2024-05-27 15:58:15', '0000-00-00 00:00:00', 1, 0, '', '2022-05-17 09:21:00', '380', '380.jpeg', '', 0),
(382, 'سر الكراميل البنين', '', 'حط ماللول قطرات ليمون مع السكر باش يمنع الكراميل من التبلور ويجي بنين و قوامو معتدل، وما تنساش في الأخير كي يطيب الكراميل حط شوية زبدة باش تعطي لمعة للكراميل.\r\n', NULL, 'نصائح حلويات', '', 1, '', 0, 1, '2024-05-27 15:58:22', '0000-00-00 00:00:00', 1, 7481, '', '2022-05-17 09:24:00', '382', '382.jpeg', '', 0),
(383, 'هاو كيفاش تحمي يديك مالفلفل الحار ', '', 'قبل ما تقص الفلفل الحار، افرك يديك بقطرات زيت نباتي ولا زيت زيتونة. الزيت يكون طبقة عازلة و يمنع اليدين من امتصاص العنصر الحارق من الفلفل.\r\nو كان نسيت باش تحط الزيت و يديك حرقتك مالفلفل هاو شنوا تنجم تستعمل باش تتخلص من آثار الحراق:\r\n- الحليب البارد ولا الزبدة\r\n- سائل غسيل الماعون\r\n- الزيت\r\n- عصير الليمون \r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2024-05-27 15:59:22', '0000-00-00 00:00:00', 1, 5306, '', '2022-05-17 12:08:00', '383', '383.jpeg', '', 0),
(384, 'كول تفاحة قبل الماكلة و تفكرني !! ', '', '1.	تفاحة قبل الوجبة لتناول طعام أقل: كان اختصاصي التغذية الأمريكي تامي فلين، الذي خطرت له هذه الفكرة لمساعدة مريض غير قادر على إنقاص وزنه. اقترحت عليه أن يأكل تفاحة قبل كل وجبة دون تغيير أي شيء آخر في نظامه الغذائي. النتيجة: أقل من 2.3 كجم في أسبوع واحد لأن هذه العادة تساعد كبار الأكل على اعتدال شهيتهم وهذا دون أي إحباط.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 15:59:30', '0000-00-00 00:00:00', 1, 0, '', '2022-05-19 09:39:00', '384', '384.jpeg', '', 0),
(385, 'رد بالك تاكل و تتفرج في التلفزة! هاو علاش ', '', 'من أسوء العادات الي نجمو نعملوهم هي الماكلة قدام التلفزة ولا شاشة التلفون، على خاطر الفرجة و التركيز مع الشاشة يخلي الدماغ ماينتبهش لكمية الماكلة الي قاعد تاكل فيها و يعطل إشارات الشبع الي تنتقل إلى باقي الجسم. وهذا يؤدي الي أكل كميات طعام أكثر من العادة و بالتالي نزيدو في الميزان من غير مانشعرو. كان انت مستاتس تتفرج و تاكل حاجة تنجم تحضر صحن معبي بالغلة ولا الفواكه الجافة و لا سلاطة باش هكا تكون تاكل في حاجة صحية و ماتضركش.\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 15:59:36', '0000-00-00 00:00:00', 1, 6523, '', '2022-05-19 09:40:00', '385', '385.jpeg', '', 0),
(386, 'تعرفشي فوائد التاي؟ اجا نقلك ', '', 'بالنسبة للتاي أخضر ولا أحمر، عندو برشا منافع منها :\r\nالتاي الاخضر : يعاون على خسارة الوزن، يساهم في تخفيض ضغط الدم، يساهم في خفض الكوليسترول، يحسن الذاكرة، تعزيز صحة القلب...\r\nالتاي الأحمر : يساهم في تنظيم الوزن، يقلل الخلايا الدهنية بالجسم، التنبيه و تقليل الشعور بالنعاس، يحتوي على العديد من مضادات الأكسدة.\r\nشنوا الوقت الصحيح لشرب الشاي؟ في الصباح خاطر فيه نسبة مالكافيين تخليك ناشط و تعاونك على التركيز ولا في العشية بعيد على الفطور، كيما تنجم تشربو قبل الأكل باش يساعدك على حرق الدهون.\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 15:59:42', '0000-00-00 00:00:00', 1, 8456, '', '2022-05-19 09:43:00', '386', '386.jpeg', '', 0),
(387, 'ماكلة سخونة و لا باردة شنوا خير؟ ', '', 'الفرق بين الماكلة الباردة والسخونة من الناحية الصحية :\r\n\r\nالماكلة الباردة:\r\n\r\nتحافظ أكثر على القيمة الغذائية، كيما الفيتامينات والمعادن.\r\n\r\nبعض الأطعمة تنجم تفقد بعض القيم الغذائية متاعها كي تتعرض للحرارة العالية.\r\n\r\nالطعام الساخن:\r\n\r\nساهل في الهضم ويعاون في امتصاص بعض العناصر الغذائية.\r\n\r\nيدمر البكتيريا الضارة والميكروبات في الطعام.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 15:59:47', '0000-00-00 00:00:00', 1, 7421, '', '2022-05-19 09:46:00', '387', '387.jpeg', '', 0),
(388, 'حيلة ساهلة باش تنقص في الميزان ', '', '1.     أشرب عصير الليمون على معدة فارغة\r\nكل صباح، قبل الإفطار، اشرب عصير الليمون في كوب من الماء الدافئ لتصريفه، وإزالة السموم والمساعدة على حرق الدهون. لماذا الصيام؟ لأن فضائله محدودة للغاية في معدة ممتلئة. ولماذا في الماء المعتدل؟ لأنه من الأفضل أن يستوعبه الجسم. الليمون هو أيضًا أحد مضادات الأكسدة القوية المضادة للشيخوخة وقلوية المعدة مع تأثيرات مضادة للالتهابات.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 15:59:57', '0000-00-00 00:00:00', 1, 6522, '', '2022-05-19 09:48:00', '388', '388.jpeg', '', 0),
(389, 'الشكلاطة الكحلة كنز ما تفلتوش ', '', 'فوائد صحية للشكلاطة الكحلة باش دوخك :\r\nمفيدة للقلب والدورة الدموية\r\nتقلل خطر السكتة الدماغية\r\nغنية بالمعادن\r\nتعزيز قوة الذاكرة\r\n مصدر قوي لمضادات الأكسدة\r\n وزيد مع هذا الكل، في بالك الي الشكلاطة الكحلة تنجم تعاونك باش تنقص في الميزان؟\r\nأكدت دراسات انو أكل 30غ من الشكلاطة الكحلة (ما يعادل 4 مربعات) صحي برشا و ينجم يعاونك باش تنقص في الميزان. بفضل مادة البوليفينول الي تقلل الشهية وتحد من تخزين الدهون. لكن لازم تتأكد من الشكلاطة الي تختارها تكون صافية قدر الإمكان و ما فيهاش زيادات، لازم تكون بين 85٪ و99٪ كاكاو للحد من تناول السكر.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:00:02', '0000-00-00 00:00:00', 1, 6855, '', '2022-05-19 10:05:00', '389', '389.jpeg', '', 0),
(390, 'فوائد الفلفل الأكحل ', '', 'اكتشف شنوا ينجم يعمل الفلفل الأكحل :\r\nيساعد في علاج الجهاز التنفسي.\r\nيساهم في علاج البرد والسعال.\r\nيعالج التهاب الجيوب الأنفية.\r\nيحسن عملية الهضم بفضل القدرة متاعو في زيادة افراز حمض الهيروكلوريك في المعدة الي يسهل عملية الهضم.\r\nالمساعدة على فقدان الوزن بفضل الطبقة الخارجية الموجودة على السطح الي تعاون في عملية تفتيت الخلايا الدهنية.\r\nالفلفل الأكحل يحتوي على مواد مضادة للبكتيريا الي تعمل على محاربة الالتهابات.\r\nيساعد الفلفل الأكحل على امتصاص العناصر الغذائية، خاطر يحتوي على مواد تساعد على امتصاص العناصر كيما الكالسلينيوم وفيتامين B والبيتاكاروتين.\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:59:23', '0000-00-00 00:00:00', 1, 5979, '', '2022-05-19 10:06:00', '390', '390.jpeg', '', 0),
(391, 'دخل بذور الكتان لوصفاتك  ', '', 'دخل بذور الكتان في نظامك الغذائي\r\nللأسف قاعدين نستعملو في بذور الكتان بقلة، رغم فوائدو العديدة. تساعد بذور الكتان الغنية بالألياف والأوميجا 3 على الشعور بالشبع، وتسهل عملية الهضم وتعمل على حرق الدهون و بالتالي تعاون في إنقاص الوزن بسرعة وفعالية.\r\nكيفاش نستعملوه؟ ساهل برشا، تنجم تزيدو لأي نوع سلاطة، حط 2 مغارف كبار مع السلاطة و فوحها و تمتع بالبنة و المنفعة. كيما تنجم تستعملو مرحي في المخبوزات مع الفارينة مثلا و لا تخلطو مع المايوناز ولا الموتارد و تفوح بيه وصفاتك.\r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2024-05-27 16:59:11', '0000-00-00 00:00:00', 1, 7452, '', '2022-05-19 10:10:00', '391', '391.jpeg', '', 0),
(392, 'حيلة ساهلة باش تحافظ على ميزانك !', '', 'أي واحد فينا ينجم يعمل حيلة ساهلة برشا باش يحافظ على ميزانو إلي هي: تشرب الماء قبل ما تاكل: \r\nأظهرت دراسة من جامعة برمنغهام في إنجلترا إلي شرب كاس كبير من الماء قبل نصف ساعة من الوجبة يعزز فعالية النظام الغذائي. كيفاش؟ عن طريق إحداث الشعور بالشبع. أحسن حاجة أنو تكون المياه تحتوي على نسبة عالية من الكالسيوم، مما يعزز التخلص من الدهون (هيبار، كورمايور، كونتريكس)، وغنية بالمغنيسيوم.\r\n\r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2024-05-27 16:59:05', '0000-00-00 00:00:00', 1, 5489, '', '2022-05-19 10:19:00', '392', '392.jpeg', '', 0),
(393, 'حيلة الأصحنة للحفاظ على الميزان', '', 'كشفت دراسات انو كي تاكل في أصحنة أقل حجم من المعتاد يعاونك في التحكم في كميات الطعام وبالتالي في نقص الميزان، وهذا عن طريق استهلاك كمية أكل أقل من العادة كي تستعمل صحن صغير، حيث ان الدماغ يبعث اشارات الشبع ويتوهم انو الكمية الي في الصحن كافية وكفيلة باش تخليه يشبع. وحسب صحيفة “ديلى ميل” البريطانية، الحيلة هاذي تنجم تخليك تنقص في استهلاك الطعام بنسبة 30%.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:58:59', '0000-00-00 00:00:00', 1, 8847, '', '2022-05-19 10:21:00', '393', '393.jpeg', '', 0),
(394, 'افضل اوقات للاكل', '', 'كول مليح في الصباح و شوية في المساء\r\nأظهرت العديد من الدراسات أن أوقات الوجبات تؤثر على الوزن بحيث ان الأشخاص الذين تناولوا طعامًا غزيرًا في الصباح وخفة في المساء كان وزنهم أقل من أولئك الذين فعلوا العكس.\r\nالسبب؟ يتم تخزين الدهون بسهولة أكبر في المساء عندما يتم إنفاقها في الأنشطة اليومية أثناء النهار.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:58:53', '0000-00-00 00:00:00', 1, 7485, '', '2022-05-19 10:23:00', '394', '394.jpeg', '', 0),
(395, 'المعدوس فوائد تحصى ولا تعد ', '', 'ما فماش دار مافيهاش معدنوس، ايجا شوف معانا فوائدو العديدة و مادوخش:\r\n\r\n1. تعزيز صحة الجهاز الهضمي والكبد\r\n\r\n2. تقليل مستويات السكر في الدم\r\n3. تحسين رائحة النفس والجسم\r\n4. خفض ضغط الدم\r\n5. المساهمة في تقليل احتمالية الإصابة بالسرطان\r\nالعناصر الغذائية التي يحتويها البقدونس:\r\nفيتامين أ: يحسن من الرؤية الليلية وصحة الجلد.\r\nفيتامين ب: يساعد في ترميم خلايا الجسم، وإطلاق الطاقة من الخلايا.\r\nفيتامين ج: يساعد في الحفاظ على الجهاز المناعي.\r\nفيتامين هـ: يساهم في تأخير شيخوخة الخلايا.\r\nفيتامين ك: يساعد على موازنة لزوجة الدم وتنظيف وإنتاج بروتين الكبد.\r\nالحديد: يستخدم لإنتاج الهيموغلوبين المطلوب للطاقة.\r\nالمنغنيز: يساعد على امتصاص فيتامين ج.\r\nالبوتاسيوم: جيد للنمو، وبناء العضلات وتعزيز الجهاز العصبي.\r\nالزنك: يقوي جهاز المناعة ويمنع حب الشباب.\r\nالكالسيوم: يعمل على تقوية العظام والأسنان والشعر.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:58:49', '0000-00-00 00:00:00', 1, 7458, '', '2022-05-19 10:25:00', '395', '395.jpeg', '', 0),
(396, 'كثر مالشربة! هاو علاش ', '', 'من المهم جدا انو ندخلو الشربة في نظامنا الغذائي اليومي على خاطر عندها برشا منافع، تعرف عليهم معانا:\r\n1. تحتوي شربة الخضرة على مجموعة كبيرة من الألياف النباتية المفيدة جدا للجسم.\r\n2. تعتبر الشربة مصدر غني جدا بالسوائل خاطر تحتوي على نسبة عالية من الماء الي يغذّي خلايا الجسم ويحافظ على نضارة البشرة.\r\n3. تعطيك احساس بالشبع بالرغم الي فيها سعرات حرارية منخفضة مما يساعد على المحافظة على الوزن.\r\n4. الحفاظ على صحة الجهاز الهضمي خاطرها تساعد على مقاومة الإمساك والقولون العصبي وغيرها من مشكلات الجهاز الهضمي الشائعة.\r\n5. تساعد شربة الخضار و الدجاج على تعزيز الجهاز المناعي و المساعدة في علاج نزلات البرد.\r\n\r\n\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:58:37', '0000-00-00 00:00:00', 1, 7819, '', '2022-05-19 10:27:00', '396', '396.jpeg', '', 0),
(397, 'كورن فلاكس صحي !', '', 'بدل الكورن فلاكس بالشوفان مع الحليب وتمتع ببرشا فوائد منها :\r\n\r\n1-الشعور بالشبع \r\n\r\n2-غني بمضادات الأكسدة\r\n\r\n3-يحسن من مستويات الكوليسترول\r\n\r\n4-ينظم مستويات سكر الدم\r\n\r\n5-يمنع الإمساك\r\n\r\nوباش تزيدو بنة على بنة تنجم تزيدو أي مكون تحب عليه كيما العسل، الغلة، الفواكه الجافة، بذور الشيا.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:58:32', '0000-00-00 00:00:00', 1, 7553, '', '2022-05-19 10:30:00', '397', '397.jpeg', '', 0),
(398, 'فوائد المشماش! ما دوخش', '', 'مع دخول موسم المشماش جبنالكم أهم فوائد الكنز هذا:\r\n1- الحماية من الإصابة بسرطان الجلد.\r\n2- تعزيز صحة الجهاز الهضمي: يحتوي المشماش على الألياف الغذائية الهامة باش تسهل عملية الهضم.\r\n3- ترطيب البشرة: مضادات الأكسدة الي يحتوي عليها المشماش وخاصة فيتامين (أ، هـ، ج)، تساهم في زيادة جمال وإشراق البشرة، وزيد يقوم المشماش بمحاربة علامات الشيخوخة والتجاعيد.\r\n4- تخفيف الالتهابات وعلاج النقرس: يتمتع المشماش ببرشا خصائص مضادة للالتهابات.\r\n5- يعزز صحة العين: يحتوي المشماش على مركبات متعددة ضرورية لصحة العين، منها فيتامين (A . E).\r\n6- علاج فقر الدم: المشماش غني بالحديد، الي يعاون في العلاج والوقاية من الإصابة بفقر الدم.\r\n7- علاج الإمساك: يحتوي المشماش على مادة البكتين، وهي عبارة على ألياف غذائية ذايبة في الماء تساعد في علاج الإمساك.\r\n8- تحسين صحة القلب: يعاون المشماش على حماية القلب من برشا أمراض كيما تصلب الشرايين والنوبات القلبية والسكتات الدماغية لاحتوائه على كمية كبيرة من فيتامين C، و البوتاسيوم والألياف الغذائية.\r\n9- يعزز صحة الجلد: تناول المشمش يفيد البشرة لاحتوائه على فيتامين (C . E)، والذي تحمي البشرة من تلف الأشعة البنفسجية والملوثات البيئية من خلال تحييد الجذور الحرة.\r\n10- حماية الكبد: يساعد المشمش في حماية الكبد من الإجهاد التأكسدي.\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:58:28', '0000-00-00 00:00:00', 1, 5255, '', '2022-05-19 10:33:00', '398', '398.jpeg', '', 0),
(399, 'من اليوم معادش باش تلوح تنوة القهوة ', '', 'فوائد تنوة القهوة : \r\n1- يعالج السيلوليت: خلط تنوة القهوة مع الماء الدافي وافرك بيه المنطقة التي فيها السيلوليت، لمدة 10 دقايق مرتين في الأسبوع.\r\n2- مفيد للشعر: قبل ما تستخدم الشامبو، دلك شعرك بحفنة من البقايا و تمتع بشعر أكثر صحة و نظافة.\r\n3- تعزيز الدورة الدموية: أفرك بقايا القهوة حول مناطق مختلفة في جسمك باش تعزز الدورة الدموية.\r\n4- تنظيف البشرة: تنجم تفرك بشرتك ببقايا القهوة باش تنحي الخلايا الجلد الميتة .\r\n5- يمتص الروائح الغذائية: بقايا القهوة تنجم تمتص الروائح إلي في الثلاجة والفريزر، حط التنوة في علبة صغيرة وخليها محلولة في الجزء الخلفي من الثلاجة.\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2024-05-27 16:58:21', '0000-00-00 00:00:00', 1, 4908, '', '2022-05-19 10:35:00', '399', '399.jpeg', '', 0),
(400, 'لعشاق الكرموس! تعرف الى الفوائد متاعو ', '', '1- السيطرة على مستويات السكر في الدم.\r\n2- تعزيز صحة القلب.\r\n3- غني بالعناصر الغذائية مثل فيتامينات سي، د، أ، ك، ب، بوتاسيوم ومغنيسيوم وحديد وزنك ونحاس، وغيرها.\r\n4-غني بمضادات الأكسدة التي تعمل على مقاومة الجذور الحرة التي تضر خلايا الجسم.\r\n5-منخفض في السعرات الحرارية ولا يحتوي على دهون، أي أنه جيد للأشخاص الذين يعانون من الوزن الزائد ويرغبون بانقاص وزنهم.\r\n4- تحسين صحة الجلد.\r\n5- تعزيز صحة الشعر.\r\n7- يعالج مشاكل الجهاز الهضمي حيث يمتاز التين بأنه يتمتع بخصائص ملينة، أي أنه يساعد في علاج الإصابة بالإمساك تحديدًا.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:58:16', '0000-00-00 00:00:00', 1, 8511, '', '2022-05-19 10:39:00', '400', '400.jpeg', '', 0),
(401, 'كيفاش تختار الدلاع؟ ', '', ' برشا منا يحبو يختارو دلاعة حمراء وحلوة أما مايعرفوش كيفاش! على هاذاكا جبنالكم الأسرار متاع أصحاب الخبرة:  \r\nفي العادة تظهر على الدلاعة بقعة تتسمى \"بقعة الحقل\"، و لازم تكون البقعة هاذيكا لونها أصفر ولا برتقالي باش تتأكد إلي الدلاعة حمراء. \r\nتلقى على قشرة الدلاع الحلو نقط لونهم بني يدلو إلى النحل مس أجزاء التلقيح على زهرة الدلاع برشا مرات و هذا يأكدلك إلي هي طايبة و حلوة.\r\nدق على الدلاعة و كان سمعت رنين عندو صدى يعني راهي دلاعة حمراء و مطعمها حلو.\r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2024-05-27 16:58:11', '0000-00-00 00:00:00', 1, 5239, '', '2022-05-19 10:41:00', '401', '401.jpeg', '', 0),
(402, 'أسرار اختيار البطيح ', '', 'أول خطوة تعملها باش تعرف البطيخة حلوة ولا هي: تشم ريحتها، البطيخة الحلوة فيها ريحة فاوحة برشا و مميزة، كان حسيت الريحة قوية برشا تنجم تكون طايبة أكثر ماللازم، بدل الكعبة وقتها.\r\nالخطوة الثانية: ملمس البطيخة، أحرص على أنو تك﻿و﻿ن خالية من البقع والشوائب الي تدل على انو الحشرات اخترقوها.\r\nالخطوة الثالثة: شد عنق البطيخة(السويقة) بالشوية لازم يكون ماهوش شادد برشا خاطر هذاكا يدل الي هي تنحات قبل وقتها و ماهيش ناضجة.\r\nالخطوة الرابعة: انزل بالشوية على صرة البطيخة كان حسيتها طرية راهي ناضجة و كان يابسة يعني تنجم تكون مش ناضجة و بالتالي مش حلوة.\r\nإذا اخترت ثمرة البطيخالمثالية، تنجو تخبيها في الثلاجة حتى 5 أيام قبل التقديم. أما كان خذيتها و هي مش ناضجة، خليها على حرارة الغرفة لـ2حتى ل3 أيام حتى تنضج و تولي جاهزة للتقطيع. ألف صحة!\r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 0, '2024-05-31 18:09:52', '0000-00-00 00:00:00', 1, 5686, '', '2022-05-19 10:44:00', '402', '402.jpeg', '', 0),
(403, 'كيفاش تعرف الحوت جديد ولا!', '', 'باش تعرف الحوت جديد ولا تبع الخطوات هاذي :\r\n1 . لازم يكون لون خياشيمها وردي طبيعي وخالي من المواد المخاطية.\r\n2 . لازم تكون العينين لامعة والقرنية شفافة.\r\n3 . لازم تكون ريحتها مميزة الي هي ريحة الحشائش البحرية الطازجة و مافيهاش ريحة خايبة تدل على الفساد.\r\n4 . لازم يكون جلد الحوت براق لامع خالي من المخاط البكتيري، و تكون الطبقة اللزجة الي فوقها شفافة.\r\n5 . كي تنزل على لحم الحوتة في منطقة الظهر لازم يرجع اللحم لوضعو الطبيعي بعد ما تهز صبعك، يعني لازم الجلد يكون مطاطي و ما تقعدش بلاصة النزلة.\r\n6 . لازم مايكونش فما تغير في لون جوانب البطن على اللون الطبيعي للحوتة و ما فماش أي انفجار في منطقة البطن و يكون لون اللحم الداخلي طبيعي وشفاف.\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:58:02', '0000-00-00 00:00:00', 1, 6521, '', '2022-05-19 10:48:00', '403', '403.jpeg', '', 0),
(404, 'علاش ما لازمكش تقشر التفاحة قبل ما تاكلها؟', '', 'برشا منا يقشرو التفاح قبل ما ياكلوه، اما العادة هذي غالطة، هاو علاش:\r\nتحتوي قشور التفاح على برشا فوائد نذكرو منها:\r\n1- حماية الدماغ\r\n2- تقوية الجهاز المناعي\r\n3- تنظيم السكر\r\n4- فوائد للجهاز الهضمي\r\n5- الوقاية من الأنيميا\r\n6- الحماية من التصلب المتعدد\r\n7- التقليل من تسوس الأسنان\r\nعلى هذاكا لازمنا ناكلو التفاحة بقشورها و مانحيو كان الاجزاء المتعفنة منها.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:57:54', '0000-00-00 00:00:00', 1, 6485, '', '2022-05-19 10:53:00', '404', '404.jpeg', '', 0),
(405, 'فوائد السمك الأزرق ', '', 'السمك الأزرق (كيما السردينة و التونا و غيرهم) عندهم برشا فوائد، فما الي نعرفوهم و فما الي لا، اكتشف معانا الفوائد المذهلة للكنز هذا:\r\n1.تتميز معظم الأسماك الزرقاء بالدهون، يعني مشبع بالأوميجا 3، الي يعمل على تفادي النوبات القلبية؛ نتيجة تعزيزه لعمل القلب والشرايين، بالإضافة لدوره الفعال في تعزيز عمل الدماغ وتنشيط الذاكرة.\r\n2.يحتوي على مجموعة فيتامين ب: ب 1، ب 2، ب 12، ب 3.\r\n3.الفيتامينات التي يشتمل عليها السمك الأزرق مهمة في تعزيز الجهاز المناعي، زيادة صحة الجلد والشعر، منع فقر الدم.\r\n4.يحتوي على فيتامين أ، المهم لتعزيز المناعة، تعزيز قوة الإبصار، كما أنه من بين الفيتامينات مضادة الأكسدة.\r\n5.تحتوي على فيتامين د، والذي يعزز قوة العظام والأسنان، تقليل خطر الإصابة بأنواع معينة من السرطانات.\r\n6.تتضمن الأسماك الزرقاء نسبة مرتفعة من المعادن، مثل: الفوسفور، الكالسيوم، البوتاسيوم، الحديد، الماغنسيوم.\r\n7.كل 100 جرام من السمك الأزرق تحتوي على 18% من البروتين، الذي يعمل على بناء وتقوية العضلات.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:57:42', '0000-00-00 00:00:00', 1, 7458, '', '2022-05-19 10:56:00', '405', '405.jpeg', '', 0),
(406, 'الفريت البنين على طريقة المطاعم ', '', 'باش تتحصل على فريت بنين على طريقة المطاعم تبع الخطوات هاذي:\r\n1- قشر الباطاطا و أغسلها مليح مبعد قصها على شكل أصابع خشينة.\r\n2- حط أصابع الباطاطا في وعاء معبي بالخل والماء الدافئ، وحطها على النار مدة 5 الى 7 دقائق تقريبا.\r\n3- صفي الباطاطا مالماء و حطها في صينية تشيح شوية.\r\n4- توا سخن الزيت مليح و اقلي الباطاطا متاعك حتى تتحصل على اللون الذهبي.\r\nو صحة و بالشفاء.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 16:57:38', '0000-00-00 00:00:00', 1, 6453, '', '2022-05-19 11:03:00', '406', '406.jpeg', '', 0),
(407, 'أسرار شويان الحوت ', '', '1- لازم تختار حوت فرشك و جديد و تنظفو مليح بالماء البارد.\r\n2- حضر التوابل متاعك (ملح،فلفل أكحل،شوية كركم و شوية كمون) حسب ذوقك، وخلطهم مع شوية قارص و زيت.\r\n3- اعمل شقوق على ظهر الحوتة كان كبيرة باش تدخل فيهم التوابل و تطيب فيسع.\r\n4- حط نص الخليط الي حظرتو في كرش الحوتة و النص الاخر من برة و ادهن مليح الظهر و الشقوق.\r\n5- خلي الحوت يرتاح و يشرب التوابل لمدة ساعة على الأقل.\r\n6- ادهن المقلاة متاعك بشوية زيت و سخنها مليح على نار هادية.\r\n7- حط الحوت متاعك يتشوى في عقلو و ما تغطيهش المقلاة باش ما يسيبش زيتو.\r\n8- ما تقلبش الحوت برشا مرات باش مايتفتفتش، طيب كل جيهة مدة 15 دقيقة تقريبا على نار هادية.\r\n9- اطفي النار و خلي الحوتة ترتاح شوية قبل ما تخرجها باش القشرة ما تتنحاش.\r\n10- أعصر شوية قارص عند التقديم و صحة و بالشفاء.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 16:57:33', '0000-00-00 00:00:00', 1, 6524, '', '2022-05-19 11:08:00', '407', '407.jpeg', '', 0),
(408, 'من اليوم معادش باش تلوح قشور البرقدان', '', 'شيح قشور البرقدان و ارحيهم واستعمل المسحوق في برشا وصفات\r\nيساهم مسحوق قشور البرقدان في :\r\n1- الحماية من خطر السرطان\r\n2- تقوية عضلة القلب\r\n3- تعزيز صحة الرئة \r\n4- الحد من الإصابة بنزلات البرد\r\n5- تنظيم مستوى السكر في الدم\r\n6- إنقاص الوزن\r\n7- تحسين عملية الهضم \r\n8- تعزيز صحة العين\r\n9- مكافحة الالتهابات\r\n10- تحسين البشرة\r\nاستخدامات منزليةاخرى لقشور البرقدان :\r\nتطهير الثلاجة\r\nتنظيف الأسطح الخشبية\r\nتلميع الفولاذ المقاوم للصدأ\r\nإزالة الروائح الكريهة \r\nصد الحشرات\r\nيعد سمادًا جيدًا للحديقة\r\n\r\n', NULL, 'نصائح عامة', '', 1, '', 0, 1, '2024-05-27 16:56:42', '0000-00-00 00:00:00', 1, 8692, '', '2022-05-19 14:27:00', '408', '408.jpeg', '', 0),
(409, 'أضرار الماء البارد', '', 'برغم الي الماء البارد تحسو يروي العطش اما للاسف عندو برشا أضرار اهمها:\r\nيفاقم في بعض المشكلات الصحية كيما:\r\n1. حالة ضخامة المريء (Achalasia) و هي حالة نادرة تصيب المريء و تسببلو صعوبة في بلع الطعام والشراب، و ينصح المصابون بيها تجنب شرب الماء البارد.\r\n2. صداع الشقيقة الي تزيد فرصة اصابتك بنوبات الصداع كي تشرب ماء بارد ولا مثلج خاصة.\r\n3. اضطرابات هضمية تصير خاصة كي تشرب الماء البارد أثناء تناول الوجبة ولا بعد الانتهاء من الوجبة بفترة قصيرة، كيما: عسر الهضم، والإمساك. \r\n4. زيادة الألم للناس الي يعانيو من حساسية الأسنان.\r\n5. حدوث اضطرابات في درجة حرارة أجهزة الجسم الداخلية.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:56:33', '0000-00-00 00:00:00', 1, 5482, '', '2022-05-23 09:31:00', '409', '409.jpeg', '', 0),
(410, 'كيفاش نخبيو الماكلة في الصيف؟', '', 'كيفاش نحافظو على الماكة متاعنا في فصل الصيف :\r\nاول حاجة لازم مانعبيوش الفريجيدار برشا.\r\nنقصو في حرارة الفريجيدار المعتادة.\r\n نتجنبو تخبية الماكلة الي مسخنة اكثر من مرة. \r\nنذوبو الماكلة المجمدة ببطء في الفريجيدار مش البرا. \r\nنخبيو الماكلة في حكك تتسكر مليح و من الافضل تكون بلار. \r\nنحطو الماكلة الي طايبة بالطماطم و لا فيها حليب في الفريجيدار بسرعة خاطر معرضة اكثر باش تفسد. \r\n\r\n\r\n', NULL, 'نصائح حفظ', '', 1, '', 0, 1, '2024-05-27 16:56:25', '0000-00-00 00:00:00', 1, 5412, '', '2022-05-23 10:08:00', '410', '410.jpeg', '', 0),
(411, 'نصائح صحية وغذائية في عيد الأضحى', '', 'نصائح صحية وغذائية في عيد الأضحى:\r\n-نحي أكبر قدر ممكن من الدهون الموجودة في اللحم قبل ما طيبو.\r\n-لازم إدخل للماكلة متاعك في العيد برشا خضرة و ما تقتصرش على اللحم برك باش تشبع، و ما تنساش صحن السلاطة مهم برشا مع اللحوم.\r\n-احسن بلاصة في العلوش تنجم تاكل منها هي منطقة الفخذ والعنق، خاطر تحتوي على كمية اقل مالدهون مالبلايص الاخرين.\r\n-ما تكثرش برشا من اللحم خاطر يعمل برشا مشاكل صحية كيما الكوليسترول والقلب والأوعية الدموية، وأمراض النقرس، وإضطرابات الجهاز الهضمي إلى جانب السمنة.\r\n-ما تنساش باش تشرب برشا ماء نهار العيد.\r\n-حاول باش ما تاكلش اللحم في وجبة العشاء و كول حاجات خفيفة.\r\n-ما تشربش المشروبات الغازية والقهوة بعد ما تاكل اللحم بالظبط، بدلهم بالتاي الاخضل ولا الزنجبيل ولا النعناع خاطر يعاونو برشا في امتصاص الدهون.\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 16:56:18', '0000-00-00 00:00:00', 1, 5369, '', '2022-06-09 11:03:00', '411', '411.jpeg', '', 0),
(412, 'الجرجير فوائد صحية متنوعة', '', 'فوائد الجرجير المذهلة : \r\n1. يحتوي الجرجير على كمية أكبر من فيتامين \"سي\" مقارنة بالبرتقال.\r\n2. يحتوي على كمية من حمض الفوليك أكثر من الموز. \r\n3. يمتلك الجرجير كمية من الحديد أكثر من السبانخ. \r\n4. يحتوي الجرجير على كمية من الكالسيوم أكثر من الحليب. \r\n5. يعد مصدرا للعديد من الفيتامينات منها (إيه - بي 6 - بي 12).\r\n6. يحتوي على العديد من المعادن مثل الحديد والمغنيسيوم والكالسيوم والفوسفور.\r\n7. له القدرة على خفض ضغط الدم المرتفع، ومنع تجلط الدم والحماية من الجلطات.\r\n8. يعمل الجرجير على خفض نسبة الكوليسترول الضار في الدم بصورة تساهم في حماية القلب.\r\n9. يحتوي الجرجير على نسبة عالية من الفيتامينات والمعادن التي تساهم في حماية الجسم من التعرض لهشاشة العظام.\r\n10. يساهم في تحسن حالة العين ويعمل على خفض نسبة السكر في الدم كما أنه يساعد على حركة الأمعاء ويخلصها من المواد الضارة.\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:56:10', '0000-00-00 00:00:00', 1, 6325, '', '2022-07-26 12:29:00', '412', '412.jpeg', '', 0),
(413, 'فوائد القهوة', '', 'تحتوي القهوة على بعض الفيتامينات والمعادن كما أنها تحتوي على نسبة عالية من مضادات الأكسدة، القهوة غنية بالعديد من العناصر الغذائية الموجودة بشكل طبيعي في حبوب الب، ويحتوي فنجان قهوة 240 مل على:\r\n-فيتامين ب 2\r\n- فيتامين ب 1\r\n- فيتامين ب 3\r\n- الفولات\r\n- المنجنبز\r\n- البوتاسيوم\r\n- المغنيسيوم\r\n- الفوسفور\r\nفوائد القهوة\r\nأثبتت الأبحاث التي تم تدشينها على القهوة منذ القدم وحتى الآن الكثير من الفوائد الصحية التي يمكن تحديدها فيما يلي:\r\n1-تحسين الأداء البدني\r\n\r\nأثبتت بعض الدراسات قدرة القهوة على زيادة القدرة على القيام بالأنشطة البدنية، كما تساعد في تحسين الأداء الرياضي، قد يرجع ذلك لزياد مستويات الكافيين الذي يعمل على رفع مستوى الأدرنالين في الدم الذي قد يساهم في زيادة القدرة البدنية\r\n2- فقدان الوزن\r\n\r\nتحتوي القهوة على المغنيسيوم والبوتاسيوم مما يساعد جسم الإنسان على استخدام الأنسولين وتنظيم مستويات السكر في الدم وتقليل الرغبة في تناول الأطعمة السكرية والوجبات الخفيفة، كما قد يساعد الكافيين الخلايا الدهنية على تكسير دهون الجسم واستخدامها كمصدر للطاقة مما يخلص الجسم من بعض الدهون الغير مرغوب بها.\r\n3- قد تقلل من خطر الإصابة بببعض الأمراض\r\n\r\nيرتبط الاستهلاك المتوسط للقهوة بانخفاض خطر الإصابة بالسكتة الدماغية، كما أظهرت الدراسات أن شرب القهوة بانتظام يقلل من خطر الإصابة بمرض باركنسون بنسبة 25٪، وقد يقلل ارتفاع مستويات الكافيين في الدم من خطر الإصابة بمرض الزهايمر كذلك.\r\n4- الوقاية من العدوى\r\n\r\nتحتوي القهوة على الكثير من مضادات الأكسدة التي قد تهاجم الجذور الحرة المسبب الرئيسي في تعرض الجسم للالتهاب والمرض، مما قد يقى من التعرض للكثير من أنواع العدوى الفيروسية والبكتيرية التي تهاجم الجسم\r\n5- الوقاية من السكري\r\n\r\nيقلل الكافيين من حساسية الأنسولين ويضعف من تحمل الجلوكوز، وبالتالي يقلل من خطر الإصابة بمرض السكري من النوع الثاني.\r\n6- مضاد للاكتئاب\r\n\r\nيحفز الكافيين الجهاز العصبي المركزي ويعزز إنتاج الناقلات العصبية مثل السيروتونين والدوبامين والنورادرينالين، مما يساعد على تحسن الحالة المزاجية، وأثبتت أحد الدراسات أن تناول القهوة بشكل يومي يقلل من فرص التعرض للاكتئاب.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 16:56:05', '0000-00-00 00:00:00', 1, 0, '', '2022-07-26 12:48:00', '413', '413.jpeg', '', 0),
(414, 'فوائد زيت الزيتون', '', 'تعرف على أهم فوائد زيت الزيتون على الريق:\r\n1- تعزيز صحة القولون\r\nإن القيم الغذائية الموجودة في زيت الزيتون تلعب دورًا كبيرًا في الحفاظ على صحة القولون وعمله، حيث يساهم تناول زيت الزيتون على الريق في حماية خلايا القولون من التضرر وبالتالي خفض خطر الإصابة بسرطان القولون تحديدًا.\r\nإلى جانب ذلك فإن زيت الزيتون يعمل على تحسين عمل القولون من خلال تعزيز عمل الأمعاء وحركتها والمساهمة في الحماية من الإصابة بالإمساك أو حتى علاجها.\r\n2- تحسين صحة البشرة والشعر والأظافر\r\nمن أهم فوائد زيت الزيتون على الريق أنه يعمل على تحسين صحة البشرة والشعر والأظافر وحتى العظام، حيث يعمل زيت الزيتون على ترطيب الشعر والبشرة ويغذي كل من الأظافر والعظام.\r\nهناك العديد من الوصفات التي يتم استخدام زيت الزيتون فيها من أجل ترطيب الشعر وتقويته وزيادة سماكته، ووصفات أخرى لكل من البشرة والأظافر أيضًا.\r\n3- تنظيف الكبد من السموم\r\nللكبد وظائف أساسية ومهمة في الجسم، فهو يعمل على تنظيف الجسم من أجل توفير بيئة مناسبة لأعضاء الجسم المختلفة، وهذا يعني أن الحفاظ على صحته وتنظيفه من السموم له أثر إيجابي على صحة الكبد وعمله.\r\nمن الممكن الحصول على هذه الفائدة من خلال مزج ملعقتان من زيت الزيتون من عصير الليمون الطازج وشربه على الريق.\r\n4- المساهمة في فقدان الوزن\r\nيمتاز زيت الزيتون بفوائده الصحية العديدة ومن بينها المساهمة في فقدان الوزن، وتزداد هذه الفائدة مع تناول زيت الزيتون على الريق، إذ يساعد تناول زيت الزيتون على زيادة شعورك بالشبع لفترة أطول، بالتالي يقلل من كمية الطعام المتناولة لاحقًا وهذا يعني سعرات حرارية أقل.\r\nكما يشكل زيت الزيتون البديل الأنسب للزبدة عند الطهي.\r\n5- تقوية عمل الجهاز المناعي\r\nمنذ القدم كان الناس يتناولون زيت الزيتون على الريق من أجل تحسين عمل وكفاءة الجهاز المناعي في محاربة الأمراض المختلفة، حيث أن الأحماض الدهنية الموجودة في زيت الزيتون تلعب دورًا في تعزيز صحة الجهاز المناعي ودعمه في محاربة مسببات المرض التي تدخل الجسم.\r\nفوائد زيت الزيتون على الريق الأخرى\r\nإلى جانب كل ما سبق، فإن تناول زيت الزيتون على الريق يعني التمتع بالفوائد الصحية التالية أيضًا:\r\nحماية القلب من الإصابة بالأمراض المختلفة.\r\nخفض مستوى الكوليسترول المرتفع.\r\nالتقليل من خطر الإصابة بالالتهابات.\r\nضبط مستويات السكر في الدم.\r\nتعزيز صحة الدماغ.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 16:56:01', '0000-00-00 00:00:00', 1, 0, '', '2022-07-26 12:59:00', '414', '414.jpeg', '', 0),
(415, ' فوائد التمر لجسمك ', '', 'فوائد التمر العظيمة :\r\n\r\n1. تعزيز صحة الجهاز الهضمي.\r\n\r\n2. تحسين حركة الأمعاء ويحمي من الإمساك.\r\n\r\n3. الوقاية من التهابات الأمعاء والقولون نتيجة خصائصه المضادة للبكتيريا، ودوره في تحفيز نمو البكتيريا النافعة في الأمعاء.\r\n\r\n4. الوقاية من البواسير وسرطانات الجهاز الهضمي.\r\n\r\n5. الحفاظ على صحة مينا الأسنان والوقاية من تسوس الأسنان.\r\n\r\n6. علاج فقر الدم.\r\n\r\n7. تعزيز صحة الجهاز العصبي.\r\n\r\n8. الحفاظ على صحة القلب.\r\n\r\n9. مضاد للالتهابات.\r\n\r\n10. السيطرة على ضغط الدم.\r\n\r\n11. مفيد لصحة العظام.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:55:55', '0000-00-00 00:00:00', 1, 6527, '', '2022-07-26 13:06:00', '415', '415.jpeg', '', 0),
(416, 'فوائد الرمان العجيبة', '', 'فوائد الرمان العديدة :\r\n1. تعزيز صحة الفم والأسنان : الرمان جيد لصحة الفم والأسنان، حيث أن استخدامه يُساهم في التقليل من خطر الإصابة بالتهاب اللثة.\r\n2. جيد لصحة القلب : يساهم الرمان في خفض الكوليسترول المرتفع ومنع أكسدة الكوليسترول الضار، وبالتالي الحماية من الإصابة بانسداد الأوعية، وأمراض القلب، وتصلب الشرايين.\r\n3. المساهمة في الوقاية من السرطان : يعد الرمان غني بمضادات الأكسدة التي تساهم في تقوية الجهاز المناعي والحد من السرطانات.\r\n4. المساهمة في الوقاية من فقر الدم : إن وجود نسب عالية من عنصر الحديد في الرمان جعلته مُساهم في الوقاية من فقر الدم (Anemia) الناتج من نقص الحديد، إضافة لقدرة الحديد على ما يأتي:\r\nالمساعدة في التخفيف من حالات التهاب المفاصل.\r\nالمساعدة في علاج التهابات العيون.\r\nالمساهمة في الوقاية من هشاشة العظام.\r\n5. المساهمة في تعزيز نضارة البشرة : بالرغم من فوائد الرمان العديدة في المجال الطبي إلا أنه أثبت دوره الفعال أيضًا في المجال التجميلي، حيث يُساهم في إبطاء الشيخوخة، وذلك بفضل المكونات الفريدة لمضادات الأكسدة التي تعمل على حماية الجلد والخلايا من الشيخوخة.\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:55:48', '0000-00-00 00:00:00', 1, 5698, '', '2022-07-26 13:09:00', '416', '416.jpeg', '', 0),
(417, 'كيفاش تشري الرمان و تخبيه', '', 'اليوم جبنالكم الطريقة الصحيحة باش تختار رمان بنين\r\n\r\nاول حاجة لازم تكون القشرة الخارجية يابسة و مافيهاش كدمات ولا علامات تدل على انها بدات تفسد.\r\n\r\nلازم يكون لون الرمانة من أحمر لأحمر غامق، و مايكونش اللون يميل للفاتح برشا ولا البني برشا.\r\n\r\nلازم يكون وزنها ثقيل نسبيا، هذا يدل انو الرمانة راوية و مش شايحة.\r\n\r\nمبعد ما تشري الرمان، ينصح بتخبيتو في درجة حرارة الغرفة و بعيد على الشمس المباشرة. تنجم تخبي الرمان (بالقشرة) في الفريجيار داخل كيس بلاستيكي باش يقعد اكثر وقت. كيما تنجم تستخرج حبات الرمان و تخبيهم في الفريزر مدة طويلة.\r\n\r\n\r\n', NULL, 'نصائح طبخ', '', 1, '', 0, 1, '2024-05-27 16:55:43', '0000-00-00 00:00:00', 1, 7458, '', '2022-07-26 13:10:00', '417', '417.jpeg', '', 0),
(418, 'فوائد العنب الصحية', '', 'فوائد العنب الصحية\r\nسوف نذكر في هذا القسم بعضًا من أهم فوائد العنب للصحة:\r\n1. تقليل مستويات الكولسترول في الجسم\r\nباحتواء العنب على الألياف الغذائية ومواد فعالة ومضادات أكسدة فسيكون له دور كبير في تخفيض نسبة كولسترول الدم، ويحتوي العنب الأحمر بشكل خاص على تراكيز كبيرة من مادة ريسفيراترول (Resveratrol) التي تساهم في التحكم بمستويات الكولسترول في الجسم.\r\n2. تعزيز صحة القلب والشرايين\r\nبما أن تناول العنب يساهم في ضبط مستويات الكولسترول في الجسم فإنه بالتأكيد سوف يساهم في تعزيز صحة الشرايين والقلب، والسبب في ذلك غالبًا يعود إلى احتوائه على نسبة عالية من البوتاسيوم والمغنيسيوم، واللذان يعتبران مهمين لتنظيم عمل القلب والأوعية الدموية المختلفة ولضبط مستويات ضغط الدم.\r\n3. الوقاية من فقر الدم\r\nبسبب محتوى العنب العالي من الحديد فهو يعد مهمًا في عملية صناعة الهيموغلوبين الضروري لعلاج حالات فقر الدم الناتج عن نقص الحديد.\r\n4. تعزيز صحة الجهاز الهضمي وعمليات الأيض\r\nإن المحتوى العالي من الألياف في العنب بكافة أنواعه يساهم بشكل كبير في تعزيز صحة الجهاز الهضمي وتحسين عمليات الأيض في الجسم، كما يساعد في تسهيل عملية الهضم.\r\nومحتوى العنب من بعض المعادن، مثل: البوتاسيوم يعد عاملًا مساعدًا في عمل الأمعاء والعضلات والأعصاب، ومنظم لتوازن السوائل في الجسم.\r\n5. الوقاية من الإمساك\r\nبفضل محتوى العنب العالي من الألياف فهو يساهم في تسهيل عملية الهضم والإخراج في الجسم، والتخلص من الفضلات، وبالتأكيد يساعد في الوقاية من الإمساك.\r\n6. مضاد للالتهابات\r\nتساهم مضادات الأكسدة التي يحويها العنب في تعزيز مناعة الجسم.\r\nبالإضافة إلى وجود مادة ريسفيراترول التي تعمل على إضفاء خواص مضادة للالتهابات في العنب، لتجعل منه فاكهة تساعد في الوقاية من الأمراض المعدية ومن الالتهابات المختلفة وخاصة التهاب المفاصل، بالإضافة إلى أن العنب يساعد على التخفيف من التشنجات بفضل محتواه العالي من المغنيسيوم.\r\n7. تعزيز صحة الفم\r\nتساعد الأحماض التي يحويها العنب في تعزيز صحة الفم والأسنان عن طريق المساهمة في خلق بيئة متوازنة في الفم.\r\n8. فوائد العنب الأخرى \r\nكما وجد أن تناول العنب باعتدال قد يحمل فوائد أخرى، تشمل الآتي:\r\nتعزيز صحة البصر.\r\nتحسين الذاكرة.\r\nإطالة عمر بعض الجينات.\r\nالوقاية من السكري.\r\nالتخلص من السموم في الجسم وحمض اليوريك المتراكم في العضلات والخلايا.\r\nالمساعدة في الوقاية من السرطانات بفضل محتواه من البوليفنولات.\r\nتقليل أعراض الحساسية الموسمية.\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 16:55:36', '0000-00-00 00:00:00', 1, 0, '', '2022-07-26 13:10:00', '418', '418.jpeg', '', 0),
(419, 'فوائد الانزاص', '', '1. تحسين الهضم : كعبة وحدة من الانزاص توفر 22% من حاجتك اليومية من الألياف الغذائية،الي يخليه مفيد برشا في تحسين الهضم.\r\n2. خسارة الوزن الزائد : تحتوي حبة الانزاص المتوسطة الحجم على ما يقارب 100 سعرة حرارية فقط، مقابل محتوى غذائي عالي برشا وبالتالي تحس بالشبع لوقت طويل.\r\n3. تحسين صحة القلب : يعد الانزاص أحد المصادر الممتازة للبوتاسيوم، ما يجعل تأثيرها الإيجابي على القلب كبير.\r\n4. تعزيز صحة جهاز المناعة :يحتوي الانزاص  على كمية كبيرة من مضادات الأكسدة، وبشكل خاص فيتامين ج، وهذا يخليه يعزز صحة جهاز المناعة.\r\n5. تسريع الشفاء والتقليل من فرص حصول التهاب : يساعد فيتامين ج الموجود في الانزاص على تحفيز بناء أنسجة جديدة في الجسم وتجدد الخلايا فيه.\r\n6. تعزيز جمال الشعر والبشرة :يساعد الانزاص كي ناكلوه بانتظام على التقليل من علامات تقدم السن الظاهرة على البشرة، كيما: التجاعيد.\r\n7. تقوية العظام : يحتوي الإجاص على نسب كبيرة من المغنيسيوم والفسفور والكالسيوم والنحاس، وهي معادن هامة بشكل كبير للعظام.\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 1, '2024-05-27 16:55:29', '0000-00-00 00:00:00', 1, 8577, '', '2022-07-26 13:14:00', '419', '419.jpeg', '', 0),
(420, 'فوائد التوت', '', 'دراسات حول فوائد التوت أشارت دراسةٌ نُشِرت في مجلة British journal of nutrition عام 2012، إلى أنّ تناول التوت يساهم في تحسين ضبط مستويات السكر في الدم، إذ أُجريت الدراسة على مجموعةٍ من الأشخاص الذين تمّ إعطاؤهم وجبةً تحتوي على توت وسكر المائدة، فتبيّن أنّ ارتفاع مستوى السكر والإنسولين في الدم بعد تناول التوت مع السكر كان أقلّ من الارتفاع بعد تناول السكر فقط.[٥] أشارت دراسةٌ مخبرية نُشرت في مجلة The Journal of Nutritional Biochemistry عام 2014، إلى أنّ التوت يحتوي على تراكيز عالية من مركبات الأنثوسيان التي تُعدُّ من مضادات الأكسدة، والتي تمتلك خصائص مضادة للالتهابات، ممّا يقلّل من خطر الإصابة ببعض الأمراض.[١][٦] أشارت مراجعة ٌنُشرت في مجلة Nutrition Reviews عام 2018، إلى أنّ مستخلص حبات التوت الكاملة يعزّز من عمل جهاز المناعة والجهاز الهضمي، إذ إنَّه يزيد البكتيريا النافعة، مثل: بيفيدوباكتيريا (بالإنجليزية: Bifidobacteria)، والعصيّة اللبنية (بالإنجليزية: Lactobaccilli)، وتقليل عدد الأنسجة الخبيثة (بالإنجليزية: Malignant tissue)، والأنسجة محتملة الخباثة (بالإنجليزية: Premalignant) وحجمها، ممّا قد يُشير إلى إمكانيّة استهلاك التوت إلى جانب العلاج المستخدم للتقليل من خطر الإصابة بالأمراض المتعلّقة بالجهاز الهضمي والمناعة.[٧] أشارت دراسةٌ نُشرت في مجلة The American Journal of Clinical Nutrition عام 2014، إلى أنّ التوت قد يساهم في تقليل خطر الإصابة بالسكري من النوع الثاني، إذ شملت الدراسة 2332 رجلًا لا يعاني أحدهم من السكري أو اضطراباتٍ في مستوى سكر الدم الصياميّ، وتمّت متابعتهم لمعرفة فرص الإصابة بالسكري وارتباطها بتناول أنواع مختلفة من الفواكه والخضروات، وبالتالي بيّنت الدراسة أنَّ الرجال الذين يتناولون التوت أقلّ عرضة للإصابة بالسكري من النوع الثاني.[٨] أشار تحليلٌ شموليّ نُشر في مجلة Scientific Reports عام 2016، إلى أنّ استهلاك التوت يقلّل من خطر الإصابة بأمراض القلب والأوعية الدموية، إذ بيّنت 22 دراسة أجريت على 1251 شخص، أنَّ تناول التوت مرتبطٌ بتقليل مستويات البروتين الدهني منخفض الكثافة (بالإنجليزية: LDL)، وضغط الدم الانقباضي، وغيرها من المؤشّرات المرتبطة بالإصابة بأمراض القلب.[٩] أشارت دراسةٌ مخبريةٌ نُشرت في مجلة Molecular nutrition & food research عام 2007، إلى وجود دليل بسيط حول ارتباط استهلاك التوت وتقليل خطر الإصابة بالسرطان، وذلك نظراً لاحتواء التوت على مركبات متعدد الفينول، والأنثوسيان، التي قد تساهم في التقليل من تلف الحمض النووي الصبغي (بالإنجليزية: DNA)، ممّا قد يقلّل من تكوين الخلايا السرطانية، إلّا أنّ الدراسات على الإنسان كانت ضعيفة وغير كافية لتأكيد هذا التأثير،[١٠] وقد أشارت دراسةٌ أخرى نُشرت في مجلة Journal of agricultural and food chemistry عام 2006، إلى أنّ مستخلصات بعض أنواع التوت، وبالأخص توت العُلّيق الأسود، والفراولة، كان لها تأثير في تقليل نمو الخلايا السرطانية في القولون، إلّا أنّ هذا التأثير لا يزال بحاجة إلى مزيدٍ من البحث والدراسة.[١١][١\r\n\r\n\r\n', NULL, 'نصائح غذائية', '', 1, '', 0, 0, '2024-05-27 16:55:23', '0000-00-00 00:00:00', 1, 0, '', '2022-07-26 13:19:00', '420', '420.jpeg', '', 0);
